svg.script:

    jQuery(Core.Common.Mnemoscheme.Selector).on(Core.Common.EventTypes.MnemoschemeWidgetManager.MnemoschemeLoadComplete, 
		function()
      	{      		    
      		jQuery(this).data(Core.Common.Mnemoscheme.CustomAppearanceContainerKey, {
                ellipse:
                {
                    states:
                    [
                        {
                            dictionaryValueCode: "PowerOff",
                            attributes:
                            [
                                { name: "fill", value: "gray" }
                            ]
                        },
                        {
                            dictionaryValueCode: "PowerOn",
                            attributes:
                            [
                                { name: "fill", value: "#3ec83e" }
                            ]
                        }
                    ]
                }
            });  

      		Core.Common.Mnemoscheme.MissingParameterRenderer = function(tagName, jQElement, parameterName)
            {
              	// ? - \ue195
              	// ! - \ue505
              	// ! - \uE197

              	if (tagName === "text" || tagName === "tspan")
            	{
                  jQElement.text("\uE197");
                  jQElement.css
                  	({
                    	"font-family": "Glyphicons",  "font-size": "9px",
                      	"fill": "#0072C6"
					});
                } else if(tagName === "ellipse")
                {                      	
                  	var jt = jQuery("svg text[data-core-parameter-proxy='{0}']".format(jQElement.attr("data-core-parameter")));
                  	if(jt.length == 0)
                    {
                      var jText = jQuery(document.createElementNS("http://www.w3.org/2000/svg", "text"));                  
                      var b = (jQElement[Number.zero]).getBBox();

                      jText.css({                      			
                                  "fill": "#0072C6",
                                  "font-size": "9px",
                                  "font-family": "Glyphicons"
                                 });                                    
                      jText.attr("transform", "translate(" + (b.x + b.width/2 + 7) + " " + (b.y + b.height/2 - 8) + ")");                    
                      jText.attr("data-core-parameter-proxy", jQElement.attr("data-core-parameter"));
                      jText.text("\uE197");           

                      jText.insertAfter(jQElement);     
                    }
                  else
                  {
                    jt.text("\uE197");
                  }
                }
            };
		});
