BEGIN
   --     
   DECLARE @activeArchiveDownLoader TABLE 
   (
    [Id] INT
   );

   --        
   UPDATE [Admin].[User] SET [IsApproved] = 0
	 OUTPUT [Inserted].[Id] INTO @activeArchiveDownLoader
   WHERE [RoleId] = 3 AND [IsApproved] = 1;

   DECLARE @dataBaseName sysname = N'EnergyTechAudit.PowerAccounting.Database';

   DECLARE @tableName sysname;

   DECLARE @tableSchema sysname;

   DECLARE @tableFullName NVARCHAR(1024);

   DECLARE @averageFragmentation FLOAT = 0;

   DECLARE @infoSchemaTableCursor CURSOR;

   BEGIN TRY

     SET @infoSchemaTableCursor = CURSOR FAST_FORWARD READ_ONLY LOCAL FOR
	     SELECT [T].[TABLE_NAME], [T].[TABLE_SCHEMA]
		     FROM INFORMATION_SCHEMA.TABLES T 
		     WHERE [T].[TABLE_CATALOG] = @dataBaseName;	

     OPEN @infoSchemaTableCursor;

     FETCH NEXT FROM @infoSchemaTableCursor INTO @tableName, @tableSchema;

     WHILE @@FETCH_STATUS = 0 
     BEGIN
	     SET @tableFullName = '[' + @tableSchema + '].[' +  @tableName + ']';

	     SELECT @averageFragmentation = AVG([avg_fragmentation_in_percent])
                 FROM [sys].[dm_db_index_physical_stats]
                    ( 
                       DB_ID(@dataBaseName), 
                       OBJECT_ID(@tableFullName), NULL, NULL, NULL
                    )  [A] 
                    JOIN [sys].[indexes] [B]
                       ON [A].[object_id] = [B].[object_id] AND [A].[index_id] = [B].[index_id]


	        IF(@averageFragmentation > 30)
	        BEGIN
	          EXEC ('ALTER INDEX ALL ON ' + @tableFullName + ' REORGANIZE;');	

          /*EXEC (
				     'ALTER INDEX ALL ON ' + @tableFullName + ' REBUILD WITH 
				     (
				      ONLINE = ON,
				      FILLFACTOR = 80, 
				      SORT_IN_TEMPDB = ON,
				      STATISTICS_NORECOMPUTE = ON
				     );'
			     );*/
	        END
	        ELSE 
	        BEGIN
		       IF(@averageFragmentation >= 5 AND @averageFragmentation <= 30)
		       BEGIN
			      EXEC ('ALTER INDEX ALL ON ' + @tableFullName + ' REORGANIZE;');			
		       END;
	        END;

	     FETCH NEXT FROM @infoSchemaTableCursor INTO @tableName, @tableSchema;
     END;

     CLOSE @infoSchemaTableCursor;
     DEALLOCATE @infoSchemaTableCursor;

    END TRY

    BEGIN CATCH   

      DECLARE @errorNumber INT = ERROR_NUMBER();      
      DECLARE @error NVARCHAR(128) = CONCAT( ERROR_MESSAGE(), ' ', ' : ', @errorNumber);      
      SET @error = CONCAT( @error, CHAR(13), '    : ', GETDATE());

      DECLARE @serverName  NVARCHAR(128) = @@SERVERNAME;
      DECLARE @subject NVARCHAR(128) = CONCAT
            (
               '     [Programmability].[DoIndexesReorganize]    ',               
               @serverName
            );

      EXEC msdb.dbo.sp_send_dbmail
         @profile_name = 'EnergyTechAudit.PowerAccounting.Database.MailProfile',
         @recipients = 'eta.development.dba@yandex.ru',
         @subject = @subject,
         @body = @error; 

    END CATCH;

   --   
   UPDATE [Admin].[User] SET [IsApproved] = 1
   WHERE [Admin].[User].[Id] IN 
   (
	    SELECT [Id] FROM @activeArchiveDownLoader
   );

   RETURN 0;
END;

BEGIN SET NOCOUNT ON;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'MainData'), -- (2, 'ColumnCaption'); --
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */ DECLARE @rowCountAffected INT = 0; DECLARE @statusMessage NVARCHAR(128) = N' '; DECLARE @errorMessage NVARCHAR(1024) = N''; DECLARE @deliter VARCHAR(4) = ',';
DECLARE @taskOperation TABLE ( [Id] INT NOT NULL PRIMARY KEY, [StatusMessage] NVARCHAR(128), [RowCountAffected] INT, [ErrorMessage] NVARCHAR(1024) );
IF(OBJECT_ID('[tempdb].[guest].[DoMeasurementDevicePollingInterval]') IS NULL) BEGIN CREATE TABLE [tempdb].[guest].[DoMeasurementDevicePollingInterval] ( [Id] INT IDENTITY (1, 1) NOT NULL,
[OperationUniqueId] UNIQUEIDENTIFIER NOT NULL, [Reguestor] NVARCHAR(32) NOT NULL,
[MeasurementDeviceId] INT NOT NULL,
[OriginalValue] INT NOT NULL, [CurrentValue] INT NOT NULL,
CONSTRAINT PK_Guest_DoMeasurementDevicePollingIntervalOutput_Id PRIMARY KEY ([Id]) ) ; END;
-- DECLARE @lastOperationUniqueId UNIQUEIDENTIFIER = ( SELECT TOP 1 [OperationUniqueId] FROM [tempdb].[guest].[DoMeasurementDevicePollingInterval] WITH(NOLOCK) WHERE [Reguestor] = @userName );
-- IF (@lastOperationUniqueId IS NOT NULL) BEGIN -- - ( ) IF (@lastOperationUniqueId <> @operationUniqueId) BEGIN -- DELETE FROM [tempdb].[guest].[DoMeasurementDevicePollingInterval] WHERE [Reguestor] = @userName; END ELSE BEGIN -- , , -- GOTO ToOut; END; END;
BEGIN TRAN
BEGIN TRY SET NOCOUNT OFF;
UPDATE [MeasurementDevice] SET [PollingInterval] = @pollingInterval
OUTPUT @operationUniqueId, @userName, inserted.[Id], deleted.[PollingInterval], inserted.[PollingInterval] INTO [tempdb].[guest].[DoMeasurementDevicePollingInterval]
FROM [Business].[MeasurementDevice] [MeasurementDevice]
WHERE [MeasurementDevice].[DeviceId] IN ( SELECT CONVERT(INT, [Item]) [DeviceId] FROM [Business].[StringSplitToArray] (@deviceIdArray, @deliter) ) AND ISNULL([MeasurementDevice].[AccessPointId], 0) IN ( SELECT DISTINCT([AccessPointLinkBuilding].[AccessPointId]) FROM [Business].[Building] [Building] WITH(NOLOCK)
INNER JOIN [Business].[AccessPointLinkBuilding] [AccessPointLinkBuilding] WITH(NOLOCK) ON [Building].[Id] = [AccessPointLinkBuilding].[BuildingId]
WHERE [Building].[DistrictId] IN ( SELECT CONVERT(INT, [Item]) [DistrictId] FROM [Business].[StringSplitToArray] (@districtIdArray, @deliter) ) )
SET @rowCountAffected = @@ROWCOUNT; COMMIT TRAN; SET @statusMessage = ' '; SET NOCOUNT ON; END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN; SET @errorMessage = ERROR_MESSAGE(); END CATCH; /**/
ToOut:
/* */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
/* 3. */
IF (@showOutputLinksInfo = 0) BEGIN
/* ( ) */ INSERT INTO @taskOperation VALUES (1, @statusMessage, @rowCountAffected, @errorMessage);
SELECT [TaskOperation].[Id], [TaskOperation].[StatusMessage], [TaskOperation].[RowCountAffected], [TaskOperation].[ErrorMessage]
FROM @taskOperation [TaskOperation];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 1, 1) ,('MainData', 'StatusMessage', '', 1, 2) ,('MainData', 'RowCountAffected', ' ', 1, 3) ,('MainData', 'ErrorMessage', '', 1, 4) END ELSE BEGIN
/* */ SELECT ROW_NUMBER() OVER (ORDER BY [ProcessingResult].[MeasurementDeviceId]) [Id], [ProcessingResult].[MeasurementDeviceId] [MeasurementDeviceId],
[City].[Description] [CityDescription], [District].[Description] [DistrictDescription], [Building].[FullAddress] [BuildingDescription], [MeasurementDevice].[Description] [MeasurementDeviceDescription],
[ProcessingResult].[OriginalValue] [OriginalValue], [ProcessingResult].[CurrentValue] [CurrentValue]
FROM [tempdb].[guest].[DoMeasurementDevicePollingInterval] [ProcessingResult] WITH(NOLOCK)
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [ProcessingResult].[MeasurementDeviceId]
INNER JOIN [Business].[AccessPoint] [AccessPoint] WITH(NOLOCK) ON [AccessPoint].[Id] = [MeasurementDevice].[AccessPointId]
INNER JOIN [Business].[AccessPointLinkBuilding] [AccessPointLinkBuilding] WITH(NOLOCK) ON [AccessPointLinkBuilding].[AccessPointId] = [AccessPoint].[Id]
INNER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [AccessPointLinkBuilding].[BuildingId]
INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [District].[Id] = [Building].[DistrictId]
INNER JOIN [Dictionaries].[City] [City] WITH(NOLOCK) ON [City].[Id] = [District].[CityId]
WHERE [ProcessingResult].[OperationUniqueId] = @operationUniqueId AND [ProcessingResult].[Reguestor] = @userName;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 0, 1), ('MainData', 'MeasurementDeviceId', '. ', 0, 2),
('MainData', 'CityDescription', ' ', 1, 3), ('MainData', 'DistrictDescription', '', 1, 4), ('MainData', 'BuildingDescription', '', 1, 5), ('MainData', 'MeasurementDeviceDescription', '', 1, 6),
('MainData', 'OriginalValue', ' ', 1, 7), ('MainData', 'CurrentValue', ' ', 1, 8); END;
END;

CREATE PROCEDURE [Programmability].[DoMeasurementDevicePollingInterval]
   @userName NVARCHAR(32),
   @operationUniqueId UNIQUEIDENTIFIER,   

   @deviceIdArray NVARCHAR(1024), 
   @districtIdArray NVARCHAR(1024),
   @pollingInterval INT,    
   @showOutputLinksInfo BIT = 1
AS

BEGIN
  BEGIN TRY    

      DECLARE @maxMemory INT = 
      (
	      SELECT [physical_memory_kb] / 1024 FROM [sys].[dm_os_sys_info]
      );

      -- CHECKPOINT;

      DECLARE @minMemory  INT;

      SET @minMemory = CAST( (@maxMemory * @minBound) AS INT);

      EXEC sp_configure  'max server memory', @minMemory

      RECONFIGURE WITH OVERRIDE;

      WAITFOR DELAY @delay;

      SET @minMemory = CAST( ( @maxMemory * @maxBound ) AS INT);

      EXEC sp_configure  'max server memory', @minMemory

      RECONFIGURE WITH OVERRIDE;

      -- DBCC DROPCLEANBUFFERS;
      -- CHECKPOINT;
  END TRY
  BEGIN CATCH

    DECLARE @errorNumber INT = ERROR_NUMBER();      
    DECLARE @error NVARCHAR(256) = CONCAT( ERROR_MESSAGE(), ' ', ' : ', @errorNumber);      
    SET @error = CONCAT( @error, CHAR(13), '    : ', GETDATE());

    DECLARE @serverName  NVARCHAR(128) = @@SERVERNAME;

    DECLARE @subject NVARCHAR(256) = CONCAT
    (
        '     SQL SERVER   ',               
        @serverName
    );

    EXEC msdb.dbo.sp_send_dbmail
        @profile_name = 'EnergyTechAudit.PowerAccounting.Database.MailProfile',
        @recipients = 'eta.development.dba@yandex.ru',
        @subject = @subject,
        @body = @error

    RETURN @@ERROR; 

  END CATCH;    

  RETURN 0;

END;

BEGIN SET NOCOUNT ON;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'MainData'), -- (2, 'ColumnCaption'); --
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */ DECLARE @rowCountAffected INT = 0; DECLARE @statusMessage NVARCHAR(128) = N' '; DECLARE @errorMessage NVARCHAR(1024) = N''; DECLARE @deliter VARCHAR(4) = ',';
DECLARE @taskOperation TABLE ( [Id] INT NOT NULL PRIMARY KEY, [StatusMessage] NVARCHAR(128), [RowCountAffected] INT, [ErrorMessage] NVARCHAR(1024) );
IF(OBJECT_ID('[tempdb].[guest].[DoUserLinkChannelOutput]') IS NULL) BEGIN CREATE TABLE [tempdb].[guest].[DoUserLinkChannelOutput] ( [Id] INT IDENTITY (1, 1) NOT NULL,
[OperationUniqueId] UNIQUEIDENTIFIER NOT NULL, [Reguestor] NVARCHAR(32) NOT NULL,
[UserId] INT NOT NULL, [ChannelId] INT NOT NULL,
CONSTRAINT PK_Guest_DoUserLinkChannelOutput_Id PRIMARY KEY ([Id]) ) ; END;
