-- IF (@lastOperationUniqueId IS NOT NULL) BEGIN -- - ( ) IF (@lastOperationUniqueId <> @operationUniqueId) BEGIN -- DELETE FROM [tempdb].[guest].[DoUserLinkChannelOutput] WHERE [Reguestor] = @userName; END ELSE BEGIN -- , , -- GOTO ToOut; END; END;
BEGIN TRAN
BEGIN TRY SET NOCOUNT OFF;
DECLARE @startWatch DATETIME = GETDATE();
-- IF(@operationType = 1) BEGIN -- using Briefcase IF(@useBriefcase = 1) BEGIN INSERT INTO [Business].[UserLinkChannel] ([UserId], [ChannelId]) OUTPUT @operationUniqueId, @userName, inserted.[UserId], inserted.[ChannelId] INTO [tempdb].[guest].[DoUserLinkChannelOutput] SELECT CONVERT(INT, [Item]) [UserId], [BriefcaseChannels].[ChannelId] [ChannelId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) CROSS JOIN ( SELECT [BriefcaseItem].[EntityId] [ChannelId] FROM [Core].[BriefcaseItem] [BriefcaseItem] INNER JOIN [Core].[Briefcase] [Briefcase] ON [BriefcaseItem].[BriefcaseId] = [Briefcase].[Id] WHERE [Briefcase].[Id] = @briefcaseId AND [BriefcaseItem].[EntityTypeName] = 'Channel' AND EXISTS ( SELECT [Channel].[Id] FROM [Business].[Channel] [Channel] WHERE [Channel].[Id] = [BriefcaseItem].[EntityId] AND [Channel].[Activated] = 1 ) ) [BriefcaseChannels] EXCEPT SELECT [Link].[UserId], [Link].[ChannelId] FROM [Business].[UserLinkChannel] [Link] WHERE [Link].[UserId] IN ( SELECT CONVERT(INT, [Item]) [UserId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) END ELSE BEGIN INSERT INTO [Business].[UserLinkChannel] ([UserId], [ChannelId]) OUTPUT @operationUniqueId, @userName, inserted.[UserId], inserted.[ChannelId] INTO [tempdb].[guest].[DoUserLinkChannelOutput] SELECT CONVERT(INT, [Item]) [UserId], [UserLink].[ChannelId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) CROSS JOIN ( SELECT [Ch].[Id] [ChannelId]
FROM [Business].[Channel] [Ch] WITH(NOLOCK) INNER JOIN [Business].[MeasurementDevice] [Md] WITH(NOLOCK) ON [Md].[Id] = [Ch].[MeasurementDeviceId]
WHERE
/* */ [Ch].[Activated] = 1 AND
/* */ ISNULL([Md].[AccessPointId], 0) IN ( SELECT DISTINCT([BuildingLink].[AccessPointId]) FROM [Business].[Building] [Building] WITH(NOLOCK)
INNER JOIN Business.AccessPointLinkBuilding [BuildingLink] WITH(NOLOCK) ON [Building].[Id] = [BuildingLink].[BuildingId]
WHERE [Building].[DistrictId] IN ( SELECT CONVERT(INT, [Item]) [DistrictId] FROM [Business].[StringSplitToArray] (@districtIdArray, @deliter) ) AND
ISNULL([Building].[OrganizationId], 0) IN ( SELECT CONVERT(INT, [Item]) [OrganizarionId] FROM [Business].[StringSplitToArray] (@organizationIdArray, @deliter) ) )/**/
/* */ AND [Md].[DeviceId] IN ( SELECT CONVERT(INT, [Item]) [DeviceId] FROM [Business].[StringSplitToArray] (@deviceIdArray, @deliter) )/**/
/* */ AND [Ch].[ResourceSystemTypeId] IN ( SELECT CONVERT(INT, [Item]) [ResourceSystemTypeId] FROM [Business].[StringSplitToArray] (@resourceSystemTypeIdArray, @deliter) )/**/ )[UserLink]
EXCEPT
-- SELECT [Link].[UserId], [Link].[ChannelId] FROM [Business].[UserLinkChannel] [Link] WHERE [Link].[UserId] IN ( SELECT CONVERT(INT, [Item]) [UserId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) END; END -- ELSE IF(@operationType = 2) BEGIN -- using Briefcase IF(@useBriefcase = 1) BEGIN DELETE [UserLinkChannel] OUTPUT @operationUniqueId, @userName, deleted.[UserId], deleted.[ChannelId] INTO [tempdb].[guest].[DoUserLinkChannelOutput] FROM [Business].[UserLinkChannel] [UserLinkChannel] WHERE [UserLinkChannel].[UserId] IN ( SELECT CONVERT(INT, [Item]) [UserId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) AND [UserLinkChannel].[ChannelId] IN ( SELECT [BriefcaseItem].[EntityId] [ChannelId]
FROM [Core].[BriefcaseItem] [BriefcaseItem] INNER JOIN [Core].[Briefcase] [Briefcase] ON [BriefcaseItem].[BriefcaseId] = [Briefcase].[Id] WHERE [Briefcase].[Id] = @briefcaseId AND [BriefcaseItem].[EntityTypeName] = 'Channel' ); END ELSE BEGIN DELETE [Link]
OUTPUT @operationUniqueId, @userName, deleted.[UserId], deleted.[ChannelId] INTO [tempdb].[guest].[DoUserLinkChannelOutput]
FROM [Business].[UserLinkChannel] [Link]
WHERE [Link].[UserId] IN ( SELECT CONVERT(INT, [Item]) [UserId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) AND [Link].[ChannelId] IN ( SELECT [Ch].[Id]
FROM [Business].[Channel] [Ch] WITH(NOLOCK)
INNER JOIN [Dictionaries].[ResourceSystemType] [ResourceSystemType] ON [ResourceSystemType].[Id] = [Ch].[ResourceSystemTypeId]
INNER JOIN [Business].[MeasurementDevice] [Md] WITH(NOLOCK) ON [Md].[Id] = [Ch].[MeasurementDeviceId]
INNER JOIN [Business].[AccessPoint] [AP] WITH(NOLOCK) ON [Md].[AccessPointId] = [AP].[Id]
INNER JOIN [Business].[AccessPointLinkBuilding] [ApBL] WITH(NOLOCK) ON [AP].[Id] = [ApBL].[AccessPointId]
INNER JOIN [Business].[Building] [B] WITH(NOLOCK) ON [B].[Id] = [ApBL].[BuildingId]
WHERE /* */ [Ch].[Activated] = 1 AND
[Md].[DeviceId] IN ( SELECT CONVERT(INT, [Item]) [DeviceId] FROM [Business].[StringSplitToArray] (@deviceIdArray, @deliter) ) AND [B].[DistrictId] IN ( SELECT CONVERT(INT, [Item]) [DistrictId] FROM [Business].[StringSplitToArray] (@districtIdArray, @deliter) ) AND ISNULL([B].[OrganizationId], 0) IN ( SELECT CONVERT(INT, [Item]) [OrganizationId] FROM [Business].[StringSplitToArray] (@organizationIdArray, @deliter) ) AND [Ch].[ResourceSystemTypeId] IN ( SELECT CONVERT(INT, [Item]) [ResourceSystemTypeId] FROM [Business].[StringSplitToArray] (@resourceSystemTypeIdArray, @deliter) ) ); END; END -- detatch all inactive channels or all channels ELSE IF(@operationType = 3 OR @operationType = 4) BEGIN DELETE [UserLinkChannel] OUTPUT @operationUniqueId, @userName, deleted.[UserId], deleted.[ChannelId] INTO [tempdb].[guest].[DoUserLinkChannelOutput] FROM [Business].[UserLinkChannel] [UserLinkChannel] WHERE [UserLinkChannel].[UserId] IN ( SELECT CONVERT(INT, [Item]) [UserId] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) AND [UserLinkChannel].[ChannelId] IN ( SELECT [Channel].[Id] FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].Activated = 0 OR @operationType = 4 ); END;
SET @rowCountAffected = @@ROWCOUNT;
/**/ DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
DECLARE @actionParameter XML = ( SELECT @rowCountAffected [RowCountAffected], ( SELECT [User].[Id], [User].[UserName] FROM [Admin].[User] [User] WHERE [User].[Id] IN ( SELECT CONVERT(INT, [Item]) [Id] FROM [Business].[StringSplitToArray] (@userIdArray, @deliter) ) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Users') ), ( SELECT [Device].[Id], [Device].[Code] FROM [Dictionaries].[Device] [Device] WHERE [Device].[Id] IN ( SELECT CONVERT(INT, [Item]) [Id] FROM [Business].[StringSplitToArray] (@deviceIdArray, @deliter) ) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Devices') ), ( SELECT [District].[Id], [District].[CityId], [District].[Description], [City].[Description] FROM [Dictionaries].[District] [District] INNER JOIN [Dictionaries].[City] [City] ON [District].[CityId] = [City].[Id] WHERE [District].[Id] IN ( SELECT CONVERT(INT, [Item]) [Id] FROM [Business].[StringSplitToArray] (@districtIdArray, @deliter) ) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Districts') ), ( SELECT [Organization].[Id], [Organization].[Description] FROM [Business].[Organization] [Organization] WHERE [Organization].[Id] IN ( SELECT CONVERT(INT, [Item]) [Id] FROM [Business].[StringSplitToArray] (@organizationIdArray, @deliter) ) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Organizations') ), ( SELECT [ResourceSystemType].[Id], [ResourceSystemType].[Code] FROM [Dictionaries].[ResourceSystemType] [ResourceSystemType] WHERE [ResourceSystemType].[Id] IN ( SELECT CONVERT(INT, [Item]) [Id] FROM [Business].[StringSplitToArray] (@resourceSystemTypeIdArray, @deliter) ) FOR XML AUTO, ELEMENTS, TYPE, ROOT('ResourceSystemTypes') ) FOR XML PATH('RequestParams') );
INSERT INTO [Admin].[Audit] ( [Area], [Controller], [Action], [UserId], [Time], [Elapsed], [ActionParams] ) VALUES('InternalSqlServer', 'DoUserLinkChannel', IIF(@operationType = 1, 'AttachUserLinkChannel', 'DetachUserLinkChannel'), @userId, GETDATE(), DATEDIFF(ms, @startWatch, GETDATE()), CAST( @actionParameter AS NVARCHAR(MAX) ) );
/**/
COMMIT TRAN; SET @statusMessage = ' '; SET NOCOUNT ON; END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN; SET @errorMessage = ERROR_MESSAGE(); END CATCH; /**/
ToOut:
/* */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
/* 3. */
IF (@showOutputLinksInfo = 0) BEGIN
/* ( ) */ INSERT INTO @taskOperation VALUES (1, @statusMessage, @rowCountAffected, @errorMessage);
SELECT [TaskOperation].[Id], [TaskOperation].[StatusMessage], [TaskOperation].[RowCountAffected], [TaskOperation].[ErrorMessage]
FROM @taskOperation [TaskOperation];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 1, 1) ,('MainData', 'StatusMessage', '', 1, 2) ,('MainData', 'RowCountAffected', ' ', 1, 3) ,('MainData', 'ErrorMessage', '', 1, 4) END ELSE BEGIN
/* */ SELECT ROW_NUMBER() OVER (ORDER BY [ProcessingResult].[ChannelId], [ProcessingResult].[UserId]) [Id],
[ProcessingResult].[ChannelId] [ChannelId], [ProcessingResult].[UserId] [UserId],
[City].[Description] [CityDescription], [District].[Description] [DistrictDescription], [Building].[FullAddress] [BuildingDescription],
[User].[Description] [UserDescription], [Channel].[Description] [ChannelDescription]
FROM [tempdb].[guest].[DoUserLinkChannelOutput] [ProcessingResult] WITH(NOLOCK)
INNER JOIN [Admin].[User] [User] WITH(NOLOCK) ON [User].[Id] = [ProcessingResult].[UserId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = [ProcessingResult].[ChannelId]
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [District].[Id] = [Building].[DistrictId]
INNER JOIN [Dictionaries].[City] [City] WITH(NOLOCK) ON [City].[Id] = [District].[CityId] WHERE [ProcessingResult].[OperationUniqueId] = @operationUniqueId AND [ProcessingResult].[Reguestor] = @userName;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 0, 1), ('MainData', 'ChannelId', '. ', 0, 2), ('MainData', 'UserId', '. ', 0, 3),
('MainData', 'CityDescription', ' ', 1, 4), ('MainData', 'DistrictDescription', '', 1, 5), ('MainData', 'BuildingDescription', '', 1, 6), ('MainData', 'ChannelDescription', '', 1, 7), ('MainData', 'UserDescription', '', 1, 8) END;
END;

CREATE PROCEDURE [Programmability].[DoUserLinkChannel]	
   @userName NVARCHAR(32),

   @operationUniqueId UNIQUEIDENTIFIER,
   @operationType INT,
   @userIdArray NVARCHAR(1024),
   @deviceIdArray NVARCHAR(1024), 
   @districtIdArray NVARCHAR(1024),
   @organizationIdArray NVARCHAR(1024),
   @resourceSystemTypeIdArray NVARCHAR(1024),
   @showOutputLinksInfo BIT = 1,
   @useBriefcase BIT = 0,
   @briefcaseId INT = 0
AS


BEGIN
  BEGIN TRY
    OPEN SYMMETRIC KEY  [EnergyTechAudit.PowerAccounting.Database.SymmetricKey] 
      DECRYPTION BY CERTIFICATE [EnergyTechAudit.PowerAccounting.Database.Certificate];

    SET @encryptedText = ENCRYPTBYKEY(KEY_GUID(N'[EnergyTechAudit.PowerAccounting.Database.SymmetricKey]'), @clearText);

    CLOSE SYMMETRIC KEY [EnergyTechAudit.PowerAccounting.Database.SymmetricKey];
  END TRY
  BEGIN CATCH
    SET @encryptedText = NULL;
    CLOSE SYMMETRIC KEY [EnergyTechAudit.PowerAccounting.Database.SymmetricKey];
    RETURN -1;
  END CATCH

  RETURN 0;
END

CREATE PROCEDURE [Programmability].[EncryptData]
  @clearText VARBINARY(MAX), 
  @encryptedText VARBINARY(MAX) OUT
WITH EXECUTE AS OWNER
AS

BEGIN SET NOCOUNT ON;
SET XACT_ABORT ON;
BEGIN TRY
/* "" */ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
BEGIN TRANSACTION;
DECLARE @measurementDeviceId INT = ( SELECT TOP(1) [Channel].[MeasurementDeviceId] FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].[Id] = @channelId );
DECLARE @deviceId INT; DECLARE @deviceCode NVARCHAR(64);
SELECT TOP(1) @deviceId = [MeasurementDevice].[DeviceId], @deviceCode = [Device].[Code]
FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK)
INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK) ON [MeasurementDevice].[DeviceId] = [Device].[Id]
WHERE [MeasurementDevice].[Id] = @measurementDeviceId
/* */ IF NOT(@periodTypeId = 2 AND (@deviceCode = 'EK270' OR @deviceCode = 'Irvis' OR @deviceCode = 'VIST')) BEGIN SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME); SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME); END;
/* (, 01.03.2015 23:59:59)*/ EXEC [Programmability].[CorrectArchiveTime] @periodTypeId, @deviceId, @measurementDeviceId, @periodBegin OUTPUT, @periodEnd OUTPUT
IF(@includePeriodEnd = 1 AND @deviceCode <> 'TV7' AND @deviceCode <> 'Elf') BEGIN SET @periodEnd = DATEADD (day , 1, @periodEnd); END;
IF(@includePeriodEnd = 0 AND (@deviceCode = 'VZLJOT_024' OR @deviceCode = 'VZLJOT_024M' OR @deviceCode = 'VZLJOT_026M')) BEGIN SET @periodEnd = DATEADD (day , 1, @periodEnd); END;
IF (@periodTypeId = 3 AND (@deviceCode = 'Spt943' OR @deviceCode = 'Spt941_10_11' OR @deviceCode = 'Spt941_20')) BEGIN SET @periodEnd = DATEADD (day , 1, @periodEnd); END;
IF (@periodTypeId = 2 AND (@deviceCode = 'Spt943' OR @deviceCode = 'Spt941_10_11' OR @deviceCode = 'Spt941_20' OR @deviceCode = 'EK270' OR @deviceCode = 'Irvis' OR @deviceCode = 'Pramer710')) BEGIN SET @periodEnd = DATEADD (hour , 1, @periodEnd); END;
/* @periodBegin (.. ) */ DECLARE @realPeriodBegin DATETIME = (SELECT TOP 1 [Archive].[Time] FROM [Business].[Archive] [Archive] WITH(NOLOCK) INNER JOIN [Business].[MeasurementDevice] [Device] WITH(NOLOCK) ON [Device].[Id] = @measurementDeviceId AND [Device].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = @channelId AND [Channel].[MeasurementDeviceId] = [Device].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId]
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH(NOLOCK) ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [Map] WITH(NOLOCK) ON [Map].[DeviceParameterId] = [DeviceParameter].[Id] AND [Map].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [Map].[PeriodTypeId] = @periodTypeId
WHERE [Archive].[PeriodTypeId] = @periodTypeId AND [Archive].[Time] >= @periodBegin ORDER BY [Archive].[Time] ASC)
/* @periodBegin , , */ IF (@periodBegin <> @realPeriodBegin) BEGIN IF (@periodTypeId = 3) BEGIN IF(@deviceCode = 'Mercury230' OR @deviceCode = 'Mercury234') BEGIN SET @periodBegin = @realPeriodBegin END ELSE BEGIN SET @periodBegin = DATEADD (day, 1, @realPeriodBegin) END END
IF (@periodTypeId = 2) BEGIN IF(@deviceCode = 'KM5' OR @deviceCode = 'RM5') BEGIN SET @periodBegin = @realPeriodBegin END ELSE BEGIN SET @periodBegin = DATEADD (hour, 1, @realPeriodBegin) END END END
/* */ IF (@deviceCode <> 'VZLJOT_033-034' AND @deviceCode <> 'VZLJOT_024' AND @deviceCode <> 'VZLJOT_024M' AND @deviceCode <> 'VZLJOT_026M' AND @deviceCode <> 'VKT9') BEGIN IF (@periodTypeId = 3) BEGIN SET @periodBegin = DATEADD (day , -1, @periodBegin); END
IF (@periodTypeId = 2 AND @deviceCode <> 'EK270' AND @deviceCode <> 'Irvis' AND @deviceCode <> 'VKT7' AND @deviceCode <> 'Pulsar2M' AND @deviceCode <> 'VZLJOT_043' AND @deviceCode <> 'VZLJOT_025' AND @deviceCode <> 'KM5' AND @deviceCode <> 'RM5' AND @deviceCode <> 'EskoTm3E' AND @deviceCode <> 'VIST') BEGIN SET @periodBegin = DATEADD (hour, -1, @periodBegin); END END
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'ReportParameter'), (2, 'ReportFieldInfo'), (3, 'MainData'), (4, 'SummaryData');
IF (@deviceCode = 'KM5' OR @deviceCode = 'RM5') BEGIN INSERT INTO @resultSetName VALUES (5, 'ErrorsData') INSERT INTO @resultSetName VALUES (6, 'DynamicParameters') END
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */ DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
-- IF(NOT EXISTS(SELECT 1 FROM [Business].[UserLinkChannel] [Link] WITH(NOLOCK) WHERE [Link].[UserId] = @userId AND [Link].[ChannelId] = @channelId)) BEGIN DECLARE @errorPermissionMsg NVARCHAR(2048) = [Programmability].[GetSysMessage](297, 1033); -- 297 THROW 500001, @errorPermissionMsg, 1; END;
/**/
/* 3. */ --
-- IF(NOT EXISTS (SELECT 1 FROM [Business].[Channel] [Channel] WITH (NOLOCK) INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id] WHERE [Channel].[Id] = @channelId) ) BEGIN ;THROW 500001, N' ', 1; END;
-- DECLARE @deviceParameterCodePull [Programmability].[CodePullTableType];
INSERT INTO @deviceParameterCodePull SELECT [Parameter].[Id] [Id], [Parameter].[Code] [Code] FROM [Dictionaries].[Parameter] [Parameter] WITH (NOLOCK) INNER JOIN [Business].[ParameterMapDeviceParameter] [Map] WITH (NOLOCK) ON [Parameter].[Id] = [Map].[ParameterId] AND [Map].[PeriodTypeId] = @periodTypeId INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) ON [ChannelTemplate].[Id] = [Map].[ChannelTemplateId] INNER JOIN [Business].[Channel] WITH (NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [Channel].[Id] = @channelId WHERE [Channel].[MeasurementDeviceId] = @measurementDeviceId
-- IF(NOT EXISTS(SELECT 1 FROM @deviceParameterCodePull)) BEGIN ;THROW 500001, N' .', 1; END
-- DECLARE @deviceParameterCodeFields NVARCHAR(MAX) = ''; DECLARE @deviceParameterCodeFieldsWithNext NVARCHAR(MAX) = '';
SELECT @deviceParameterCodeFields = @deviceParameterCodeFields + CONCAT('[', [D].[Code], ']') + ', ',
@deviceParameterCodeFieldsWithNext = @deviceParameterCodeFieldsWithNext + CONCAT('[', [D].[Code], ']') + ', ' + CONCAT('LEAD([', [D].[Code], ']) OVER(ORDER BY [Time]) [', [D].[Code], '.Next', ']') + ','
FROM @deviceParameterCodePull [D];
SET @deviceParameterCodeFields = LEFT(@deviceParameterCodeFields, LEN(@deviceParameterCodeFields) - 1); SET @deviceParameterCodeFieldsWithNext = LEFT(@deviceParameterCodeFieldsWithNext, LEN(@deviceParameterCodeFieldsWithNext) - 1);
/**/
/* 4. */ DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM (SELECT
CAST([D].[Code] AS NVARCHAR(128)) [DeviceCode], CAST([Md].[SubModel] AS NVARCHAR(128)) [DeviceSubModel], CAST([Md].[Id] AS NVARCHAR(128)) [MeasurementDeviceId], CAST([D].[IsIntegralArchiveValue] AS NVARCHAR(128)) [IsIntegralArchiveValue], CAST([District].[Description] AS NVARCHAR(128)) [District], CAST([Building].[FullAddress] AS NVARCHAR(128)) [FullAddress], CAST([Building].[Description] AS NVARCHAR(128)) [BuildingDescription], CAST([Building].[BuildingPurposeId] AS NVARCHAR(128)) [BuildingPurposeId], CAST ([BuildingPurpose].[Code] AS NVARCHAR(128) ) [BuildingPurposeCode],
CONVERT(NVARCHAR(128), [Md].[FactoryNumber], 0) [FactoryNumber], CAST([ResourceSystemType].[Code] AS NVARCHAR(128)) [ResourceSystemTypeCode], CAST(ISNULL([ResourceSubsystemType].[Code], 'NULL') AS NVARCHAR(128)) [ResourceSubsystemTypeCode], CAST(ISNULL([ResourceSubsystemType].[Description], 'NULL') AS NVARCHAR(128)) [ResourceSubsystemTypeDescription], CAST([D].[ShortName] AS NVARCHAR(128)) [DeviceShortName], CAST([Channel].[ChannelTemplateId] AS NVARCHAR(128)) [ChannelTemplateId], CAST(0 AS NVARCHAR(128)) [InfoBandVisible], CAST('' AS NVARCHAR(128)) [Info]
FROM [Business].[MeasurementDevice] [Md] WITH (NOLOCK) INNER JOIN [Dictionaries].[Device] [D] ON [D].[Id] = [Md].[DeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Channel].[PlacementId] = [Placement].[Id]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Dictionaries].[BuildingPurpose] [BuildingPurpose] WITH (NOLOCK) ON [Building].[BuildingPurposeId] = [BuildingPurpose].[Id]
INNER JOIN [Dictionaries].[District] [District] WITH (NOLOCK) ON [District].[Id] = [Building].[DistrictId]
INNER JOIN [Dictionaries].[ResourceSystemType] [ResourceSystemType] WITH (NOLOCK) ON [Channel].[ResourceSystemTypeId] = [ResourceSystemType].[Id]
LEFT JOIN [Dictionaries].[ResourceSubsystemType] [ResourceSubsystemType] WITH (NOLOCK) ON [Channel].[ResourceSubsystemTypeId] = [ResourceSubsystemType].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId]
WHERE [Md].[Id] = @measurementDeviceId) [MeasurementDeviceInfo] UNPIVOT ( [Value] FOR [Name] IN ( [DeviceCode], [DeviceSubModel], [MeasurementDeviceId], [IsIntegralArchiveValue], [District], [FullAddress], [BuildingDescription], [BuildingPurposeId], [BuildingPurposeCode], [FactoryNumber], [ResourceSystemTypeCode], [ResourceSubsystemTypeCode], [ResourceSubsystemTypeDescription], [DeviceShortName], [ChannelTemplateId], [InfoBandVisible], [Info] ) ) [ReportParameter];
INSERT INTO @reportParameter SELECT TOP(1) 'PeriodTypeCode' [Name], [PeriodType].[Code] [Value] FROM [Dictionaries].[PeriodType] [PeriodType] WITH (NOLOCK) WHERE [PeriodType].[Id] = @periodTypeId;
IF(@deviceCode = 'Mercury230' OR @deviceCode = 'CET_4TM_03M' OR @deviceCode = 'Mercury234' OR @deviceCode = 'PSCH_4TM_05MK' OR @deviceCode = 'CE301' OR @deviceCode = 'CE303') BEGIN DECLARE @designFactor NVARCHAR(8) = (SELECT [DPV].[Value] [Value] FROM [Core].[DynamicParameterValue] [DPV] WHERE [DPV].[DynamicParameterId] = 166 AND [DPV].[EntityId] = @measurementDeviceId) IF @designFactor IS NULL BEGIN SET @designFactor = 1 END
INSERT INTO @reportParameter ([Name],[Value]) VALUES ('DesignFactor', @designFactor) END
DECLARE @buildingId INT = (SELECT [Placement].[BuildingId] FROM [Business].[Placement] [Placement] WITH(NOLOCK) INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId] AND [Channel].[Id] = @channelId)
-- "UseDescriptionInReports" DECLARE @useDescriptionInReports NVARCHAR(8) = (SELECT [DPV].[Value] [Value] FROM [Core].[DynamicParameterValue] [DPV] WHERE [DPV].[DynamicParameterId] = 181 AND [DPV].[EntityId] = @buildingId)
IF @useDescriptionInReports IS NULL BEGIN SET @useDescriptionInReports = 'false' END
INSERT INTO @reportParameter ([Name],[Value]) VALUES ('UseBuildingDescriptionInReports', @useDescriptionInReports)
-- " " EK270 IF(@deviceCode = 'EK270' OR @deviceCode = 'Irvis') BEGIN DECLARE @gasHour NVARCHAR(16) = (SELECT [DPV].[Value] [Value] FROM [Core].[DynamicParameterValue] [DPV] WHERE [DPV].[DynamicParameterId] = 171 AND [DPV].[EntityId] = @measurementDeviceId) IF @gasHour IS NULL BEGIN SET @gasHour = CAST(10 as nvarchar(16)); END
INSERT INTO @reportParameter ([Name],[Value]) VALUES ('GasHour', @gasHour) END
SELECT [ReportParameter].[Name], [ReportParameter].[Value] FROM @reportParameter [ReportParameter]; /**/
/* 4. - */ SELECT [Parameter].[Code] [Name] ,[Parameter].[IntegrableValue] [IntegrableValue] FROM @deviceParameterCodePull [ReportFieldInfo]
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ReportFieldInfo].[Id] /**/
/* 5. */ DECLARE @minDate DATETIME = ( SELECT MIN(Time) FROM [Business].[Archive] WITH (NOLOCK) WHERE MeasurementDeviceId = @measurementDeviceId AND PeriodTypeId = @periodTypeId );
DECLARE @shift INT = IIF(@deviceCode = 'TV7' OR @deviceCode = 'VZLJOT_033-034' OR @deviceCode = 'VZLJOT_024' OR @deviceCode = 'VZLJOT_024M' OR @deviceCode = 'VZLJOT_026M' OR @deviceCode = 'EK270' OR @deviceCode = 'VKT9' OR @deviceCode = 'Irvis' OR @deviceCode = 'VIST', 0, IIF(DATEADD(day, 1, @periodBegin) <= @minDate, 0, 1));
DECLARE @periodTypeBeforeShift INT = IIF(@periodTypeId = 3, @shift, IIF(@deviceCode = 'TV7' OR @deviceCode = 'KM5' OR @deviceCode = 'RM5' OR @deviceCode = 'VZLJOT_033-034' OR @deviceCode = 'VZLJOT_024' OR @deviceCode = 'VZLJOT_024M' OR @deviceCode = 'VZLJOT_026M' OR @deviceCode = 'CET_4TM_03M' OR @deviceCode = 'PSCH_4TM_05MK' OR @deviceCode = 'Mercury230' OR @deviceCode = 'Mercury234' OR @deviceCode = 'CE301' OR @deviceCode = 'CE303' OR @deviceCode = 'VKT9' OR @deviceCode= 'VKT7' OR @deviceCode = 'EskoTm3E' OR @deviceCode = 'Pramer710' OR @deviceCode = 'VIST', 0, IIF(@periodTypeId = 2, 24, 1)));
SET @periodTypeBeforeShift = IIF (@periodTypeId = 2 AND (@deviceCode = 'EK270' OR @deviceCode = 'Irvis' OR @deviceCode = 'Pulsar2M' OR @deviceCode = 'VZLJOT_043' OR @deviceCode = 'VZLJOT_025'), 0, @periodTypeBeforeShift);
DECLARE @mainDataQuery NVARCHAR(MAX) = 'WITH [Archive] AS ( SELECT [Archive].[Time] [Time] ,[Parameter].[Code] [ParameterCode] ,[dbo].[ConvertMeasurementUnit] ( [Dimension].[Value], [Muc].[DimensionValue2], [Archive].[Value], [Muc].[Expression] ) [Value] FROM [Business].[Archive] [Archive] WITH(NOLOCK)
INNER JOIN [Business].[MeasurementDevice] [Device] WITH(NOLOCK) ON [Device].[Id] = @measurementDeviceId AND [Device].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = @channelId AND [Channel].[MeasurementDeviceId] = [Device].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId]
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH(NOLOCK) ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [Map] WITH(NOLOCK) ON [Map].[DeviceParameterId] = [DeviceParameter].[Id] AND [Map].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [Map].[PeriodTypeId] = @periodTypeId
INNER JOIN [Dictionaries].[Dimension] [Dimension] WITH(NOLOCK) ON [Map].[DimensionId] = [Dimension].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] WITH(NOLOCK) ON [Map].[MeasurementUnitId] = [MeasurementUnit].[Id]
LEFT OUTER JOIN [Programmability].[MeasurementUnitConverterWithDefaultMeasurementUnitView] [Muc]
/* ()*/ ON [MeasurementUnit].[Id] = [Muc].[MeasurementUnit1Id]
INNER JOIN [Dictionaries].[Parameter] [Parameter] WITH(NOLOCK) ON [Parameter].[Id] = [Map].[ParameterId]
WHERE ([Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEnd) AND [Archive].[PeriodTypeId] = @periodTypeId ) SELECT * INTO #ResultPivotArchive FROM ( SELECT [Time], CAST(1 AS BIT) [IsValidRowState], {deviceParameterCodeFieldsWithNext} FROM ( SELECT [Time], {deviceParameterCodeFields} FROM [Archive] PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( {deviceParameterCodeFields} ) ) [MainData] ) [PrePivotArchive] ORDER BY [PrePivotArchive].[Time] OFFSET @periodTypeBeforeShift ROW ) [PivotArchive];
/* */
DECLARE @resultPivotArchiveCount INT = ( SELECT COUNT(*) - 1 FROM #ResultPivotArchive );
IF(@resultPivotArchiveCount < 0) BEGIN SET @resultPivotArchiveCount = 0; END;
DECLARE @isReturnAllMainDataForVkt7 BIT = 0; IF (@deviceCode = ''VKT7'') BEGIN DECLARE @lastTime DATETIME; SELECT @lastTime = [Time] FROM #ResultPivotArchive [R] ORDER BY [Time] ASC IF (@periodEnd > @lastTime) BEGIN SET @isReturnAllMainDataForVkt7 = 1; END END
IF(@resultPivotArchiveCount = 0 OR (@includePeriodEnd = 1 AND (@deviceCode = ''VZLJOT_026M'' OR @deviceCode = ''VZLJOT_024'' OR @deviceCode = ''VZLJOT_024M'' OR @deviceCode = ''Irvis'' OR @deviceCode = ''Elf'')) OR @isReturnAllMainDataForVkt7 = 1) BEGIN SELECT * FROM #ResultPivotArchive [R] ORDER BY [Time] END ELSE BEGIN -- (LEAD) SELECT * FROM #ResultPivotArchive [R] ORDER BY [Time] OFFSET IIF(@deviceCode = ''TV7'' OR @deviceCode = ''Spt943'' OR @deviceCode = ''Spt941_10_11'' OR @deviceCode = ''Spt941_20'', 1, 0) ROWS FETCH NEXT @resultPivotArchiveCount ROWS ONLY; END
/* " " */ SET @periodBegin = ( SELECT [R].[Time] FROM #ResultPivotArchive [R] ORDER BY [R].[Time] ASC OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY );
-- -- -26, , IF((@deviceCode = ''VZLJOT_026M'' OR @deviceCode = ''VZLJOT_024'' OR @deviceCode = ''VZLJOT_024M'') AND @includePeriodEnd = 0) BEGIN SET @periodEnd = ( SELECT [R].[Time] FROM #ResultPivotArchive [R] ORDER BY [R].[Time] DESC OFFSET 1 ROWS FETCH NEXT 1 ROWS ONLY ); END ELSE BEGIN SET @periodEnd = ( SELECT [R].[Time] FROM #ResultPivotArchive [R] ORDER BY [R].[Time] DESC OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY ); END
SELECT * FROM #ResultPivotArchive [R] WHERE [R].[Time] = @periodBegin OR [R].[Time] = @periodEnd ORDER BY [R].[Time];
IF(@deviceCode = ''KM5'' OR @deviceCode = ''RM5'') BEGIN SELECT [Id],[InternalDeviceEventId],[Time],[OriginalValue] FROM [Business].[MeasurementDeviceJournal] WHERE [MeasurementDeviceId] = @measurementDeviceId AND [Time] >= @periodBegin AND [Time] <= @periodEnd SELECT [DynamicParameterId],[Value] FROM [Core].[DynamicParameterValue] WHERE [DynamicParameterId] = 116 AND [EntityId] = @measurementDeviceId END ';
SET @mainDataQuery = REPLACE(@mainDataQuery, '{deviceParameterCodeFields}', @deviceParameterCodeFields); SET @mainDataQuery = REPLACE(@mainDataQuery, '{deviceParameterCodeFieldsWithNext}', @deviceParameterCodeFieldsWithNext);
EXEC [sys].[sp_executesql] @mainDataQuery,
N'@measurementDeviceId INT, @channelId INT, @periodTypeId INT, @periodBegin DATETIME, @periodEnd DATETIME, @periodTypeBeforeShift INT, @deviceParameterCodePull [Programmability].[CodePullTableType] READONLY, @deviceCode NVARCHAR(32), @includePeriodEnd BIT',
@measurementDeviceId = @measurementDeviceId, @channelId = @channelId, @periodTypeId = @periodTypeId, @periodBegin = @periodBegin, @periodEnd = @periodEnd, @periodTypeBeforeShift = @periodTypeBeforeShift, @deviceParameterCodePull = @deviceParameterCodePull, @deviceCode = @deviceCode, @includePeriodEnd = @includePeriodEnd
COMMIT TRANSACTION; END TRY BEGIN CATCH
/* */ IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END;
-- !IMPORTANT /* , , */ IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ' , @userName, @lineFeed, '@measurementDeviceId: ', @measurementDeviceId, @lineFeed, '@channelId: ', @channelId, @lineFeed, '@periodBegin: ', @periodBegin, @lineFeed, '@periodEnd: ', @periodEnd, @lineFeed, '@periodTypeId: ', @periodTypeId, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ' , ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ( [Time], [Exception], [Message], [UserName], [StackTrace] ) VALUES ( GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace );
THROW; END CATCH
/**/
END;

-- ====================================================================================================
-- Author:  	Leo
-- Create date: 2015-01-23
-- Description:      '  '
-- ====================================================================================================

CREATE PROCEDURE [Programmability].[GetAccountingParamSheet]
  @userName NVARCHAR(32),  
  @channelId INT,
  @periodBegin DATETIME,
  @periodEnd DATETIME,
  @includePeriodEnd BIT,
  @periodTypeId INT 
AS

BEGIN
    SET NOCOUNT ON;
	SET XACT_ABORT ON;

	BEGIN TRY

	/*         "" */
      SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

      BEGIN TRANSACTION;

	  DECLARE @measurementDeviceId INT = 
      (
        SELECT TOP(1) [Channel].[MeasurementDeviceId] 
        FROM [Business].[Channel] [Channel] WITH (NOLOCK)
        WHERE [Channel].[Id] = @channelId 
      );
	  DECLARE @channelTemplateId INT = 
      (
        SELECT TOP(1) [Channel].[ChannelTemplateId]
        FROM [Business].[Channel] [Channel] WITH (NOLOCK)
        WHERE [Channel].[Id] = @channelId 
      );

		DECLARE @deviceCode NVARCHAR(64);

	  SELECT TOP(1) 
      @deviceCode = [Device].[Code]

      FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK)

      INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK)
        ON [MeasurementDevice].[DeviceId] = [Device].[Id]

      WHERE [MeasurementDevice].[Id] = @measurementDeviceId

  /*    */
  SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME);
  SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME);

  IF (@deviceCode = 'Elf' AND @periodTypeId = 2)
    BEGIN
       SET @periodEnd = DATEADD(HOUR, -1, @periodEnd);
    END

  /* 1.          */
      DECLARE @resultSetName [Programmability].[ResultSetNameTableType];

      INSERT INTO @resultSetName VALUES
         (1, 'ReportParameter'),
         (2, 'MainData')


	  SELECT [ResultSetName].[Order], [ResultSetName].[Name]  FROM @resultSetName [ResultSetName]

       DECLARE @userId  INT;    
      DECLARE @roleCode NVARCHAR(64);

      EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;

     --      
     IF(NOT EXISTS(SELECT 1 FROM [Business].[UserLinkChannel] [Link] WITH(NOLOCK) WHERE [Link].[UserId] = @userId AND [Link].[ChannelId] = @channelId))
     BEGIN    
        DECLARE @errorPermissionMsg  NVARCHAR(2048) 
         = [Programmability].[GetSysMessage](297, 1033);
        --      297                 
        THROW 500001, @errorPermissionMsg, 1;
     END;

    DECLARE @reportParameter [Programmability].[CustomParameterTableType]; 

      INSERT INTO @reportParameter
         SELECT [Name], [Value]
         FROM
		      (SELECT  
			   CAST([D].[Code] AS NVARCHAR(128)) [DeviceCode],           
               CAST([Z].[Description] AS NVARCHAR(128)) [District],
               CAST([B].[FullAddress] AS NVARCHAR(128)) [FullAddress],
               CONVERT(NVARCHAR(128), [Md].[FactoryNumber], 0) [FactoryNumber],
               CONVERT(NVARCHAR(128), [Md].[Id], 0) [MeasurementDeviceId]

		      FROM [Business].[MeasurementDevice] [Md] WITH (NOLOCK)
			      INNER JOIN [Dictionaries].[Device] [D] 
              ON [D].[Id] = [Md].[DeviceId]

            INNER JOIN [Business].[AccessPoint] [Ap] WITH (NOLOCK)
              ON [Ap].[Id] = [Md].[AccessPointId]

            INNER JOIN [Business].[AccessPointLinkBuilding] [ApBL] WITH (NOLOCK)  
              ON [ApBL].[AccessPointId] = [Ap].[Id]

            INNER JOIN [Business].[Building] [B] WITH (NOLOCK)
              ON [B].[Id] = [ApBL].[BuildingId]

            INNER JOIN [Dictionaries].[District] [Z] WITH (NOLOCK)
              ON [Z].[Id] = [B].[DistrictId] 

            INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK)
              ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT

		      WHERE [Md].[Id] = @measurementDeviceId) [MeasurementDeviceInfo]
         UNPIVOT 
         (  
            [Value] FOR [Name] IN 
            (
			  [DeviceCode],
              [District], 
              [FullAddress], 
              [FactoryNumber],
              [MeasurementDeviceId]
            ) 
         ) [ReportParameter];

      SELECT 
         [ReportParameter].[Name], 
         [ReportParameter].[Value] 
      FROM @reportParameter [ReportParameter];


      IF @deviceCode = 'Elf'
      BEGIN
      --         
      DECLARE @deviceParameterId INT = (SELECT pmdp.DeviceParameterId FROM [Business].[ParameterMapDeviceParameter] pmdp WITH (NOLOCK)
        WHERE pmdp.ParameterId = 5000 AND pmdp.ChannelTemplateId = @channelTemplateId AND pmdp.PeriodTypeId = @periodTypeId);

      SELECT a.Time, a.[Value] FROM [Business].[Archive] a WITH (NOLOCK)
        WHERE a.PeriodTypeId = @periodTypeId AND a.MeasurementDeviceId = @measurementDeviceId AND 
              a.Time >= @periodBegin AND a.Time <= @periodEnd AND a.DeviceParameterId = @deviceParameterId
      END
      ELSE
        BEGIN
          SELECT 1;
          END

	  COMMIT TRANSACTION;

	  END TRY

  /*     */
  BEGIN CATCH
    DECLARE @lineFeed CHAR(1) = CHAR(13);

       DECLARE @parameterListTrace NVARCHAR(256) = 
             CONCAT
             (  
                '@userName: ' ,            @userName, @lineFeed, 
                '@measurementDeviceId: ',  @measurementDeviceId, @lineFeed, 
                '@channelId: ',            @channelId, @lineFeed
             );

      DECLARE @errorMessage NVARCHAR(MAX) = 
         CONCAT
         (
            'ErrorNumber: ',     ERROR_NUMBER(), @lineFeed,
            'ErrorSeverity: ',   ERROR_SEVERITY(), @lineFeed,
            'ErrorState: ' ,     ERROR_STATE(), @lineFeed,
            'ErrorProcedure: ',  ERROR_PROCEDURE(), @lineFeed,
            'ErrorLine: ',       ERROR_LINE(), @lineFeed,
            'ErrorMessage: ',    ERROR_MESSAGE()
         );

      INSERT INTO [Admin].[ErrorLogEntry] 
         (
            [Time], 
            [Exception], 
            [Message], 
            [UserName],
            [StackTrace]
         )
      VALUES
         (
            GETDATE(), 
            'InternalSqlServerException', 
            @errorMessage, 
            SUSER_NAME(),
           @parameterListTrace
         );
      --    
      THROW;    
  END CATCH;
END;

-- ====================================================================================================
-- Author: Maxim Okulin
-- Create date: 26 September 2018
-- Description:      "    "
-- ====================================================================================================

CREATE PROCEDURE [Programmability].[GetAccountingParamSheetEmergencySituations]
	@userName NVARCHAR(32),
	@channelId INT,
	@periodBegin DATETIME,
	@periodEnd DATETIME,
	@periodTypeId INT
AS

BEGIN    
    DECLARE @parameterCodePull [Programmability].[CodePullTableType];

    INSERT INTO @parameterCodePull 		    
        SELECT   
            [Parameter].[Id] [Id],
            [Parameter].[Code] [Code]
        FROM [Dictionaries].[Parameter] [Parameter] WITH (NOLOCK)

        INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK)    
            ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId] AND [ParameterMapDeviceParameter].[PeriodTypeId] = @periodTypeId 

        INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK)
            ON [ChannelTemplate].[Id] = [ParameterMapDeviceParameter].[ChannelTemplateId] AND [ChannelTemplate].[Id] = @channelTemplateId

    DECLARE @parameterCodeFields NVARCHAR(MAX) = '';

    SELECT @parameterCodeFields =  CONCAT(@parameterCodeFields, 'CAST(NULL AS DECIMAL(19, 7)) [', [ParameterCodePull].[Code], '], ')  
        FROM @parameterCodePull [ParameterCodePull];

    SET @parameterCodeFields = LEFT(@parameterCodeFields, LEN(@parameterCodeFields) - 1);

    SET @parameterCodeFields = CONCAT( N'SELECT CAST(NULL AS BIT) [IsValidRowState], CAST(NULL AS DATETIME) [Time], ',  @parameterCodeFields, ' WHERE NULL IS NOT NULL');

    EXEC [sys].[sp_executesql] @parameterCodeFields;

END;

BEGIN SET NOCOUNT ON;
SET XACT_ABORT ON;
BEGIN TRY
/* "" */ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
BEGIN TRANSACTION;
-- DECLARE @deviceParameterCodePull [Programmability].[CodePullTableType];
INSERT INTO @deviceParameterCodePull SELECT [Parameter].[Id] [Id] ,[Parameter].[Code] [Code] FROM [Dictionaries].[Parameter] [Parameter] WITH (NOLOCK) INNER JOIN [Business].[ParameterMapDeviceParameter] [Map] WITH (NOLOCK) ON [Parameter].[Id] = [Map].[ParameterId] AND [Map].[PeriodTypeId] = @periodTypeId INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) ON [ChannelTemplate].[Id] = [Map].[ChannelTemplateId] INNER JOIN [Business].[Channel] WITH (NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [Channel].[Id] = @channelId WHERE [Channel].[MeasurementDeviceId] = @measurementDeviceId
-- DECLARE @deviceParameterCodeFields NVARCHAR(MAX) = ''; DECLARE @deviceParameterCodeFieldsWithNext NVARCHAR(MAX) = '';
SELECT @deviceParameterCodeFields = @deviceParameterCodeFields + CONCAT('[', [D].[Code], ']') + ', ' ,@deviceParameterCodeFieldsWithNext = @deviceParameterCodeFieldsWithNext + CONCAT('[', [D].[Code], ']') + ', ' + CONCAT('LEAD([', [D].[Code], ']) OVER(ORDER BY [Time]) [', [D].[Code], '.Next', ']') + ','
FROM @deviceParameterCodePull [D];
SET @deviceParameterCodeFields = LEFT(@deviceParameterCodeFields, LEN(@deviceParameterCodeFields) - 1); SET @deviceParameterCodeFieldsWithNext = LEFT(@deviceParameterCodeFieldsWithNext, LEN(@deviceParameterCodeFieldsWithNext) - 1);
DECLARE @mainDataQuery NVARCHAR(MAX) = 'WITH [Archive] AS ( SELECT [Archive].[Time] [Time] ,[Parameter].[Code] [ParameterCode] ,[dbo].[ConvertMeasurementUnit] ( [Dimension].[Value], [Muc].[DimensionValue2], [Archive].[Value], [Muc].[Expression] ) [Value] FROM [Business].[Archive] [Archive] WITH(NOLOCK)
INNER JOIN [Business].[MeasurementDevice] [Device] WITH(NOLOCK) ON [Device].[Id] = @measurementDeviceId AND [Device].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = @channelId AND [Channel].[MeasurementDeviceId] = [Device].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId]
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH(NOLOCK) ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [Map] WITH(NOLOCK) ON [Map].[DeviceParameterId] = [DeviceParameter].[Id] AND [Map].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [Map].[PeriodTypeId] = @periodTypeId
INNER JOIN [Dictionaries].[Dimension] [Dimension] WITH(NOLOCK) ON [Map].[DimensionId] = [Dimension].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] WITH(NOLOCK) ON [Map].[MeasurementUnitId] = [MeasurementUnit].[Id]
LEFT OUTER JOIN [Programmability].[MeasurementUnitConverterWithDefaultMeasurementUnitView] [Muc]
/* ()*/ ON [MeasurementUnit].[Id] = [Muc].[MeasurementUnit1Id]
INNER JOIN [Dictionaries].[Parameter] [Parameter] WITH(NOLOCK) ON [Parameter].[Id] = [Map].[ParameterId]
WHERE [Archive].[Time] = @date AND [Archive].[PeriodTypeId] = @periodTypeId ) SELECT * INTO #ResultPivotArchive FROM ( SELECT [Time], CAST(1 AS BIT) [IsValidRowState], {deviceParameterCodeFieldsWithNext} FROM ( SELECT [Time], {deviceParameterCodeFields} FROM [Archive] PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( {deviceParameterCodeFields} ) ) [MainData] ) [PrePivotArchive] ORDER BY [PrePivotArchive].[Time] OFFSET 0 ROW ) [PivotArchive];
/* */ SELECT * FROM #ResultPivotArchive [R] ';
SET @mainDataQuery = REPLACE(@mainDataQuery, '{deviceParameterCodeFields}', @deviceParameterCodeFields); SET @mainDataQuery = REPLACE(@mainDataQuery, '{deviceParameterCodeFieldsWithNext}', @deviceParameterCodeFieldsWithNext);
EXEC [sys].[sp_executesql] @mainDataQuery ,N'@measurementDeviceId INT, @channelId INT, @periodTypeId INT, @date DATETIME, @deviceParameterCodePull [Programmability].[CodePullTableType] READONLY' ,@measurementDeviceId = @measurementDeviceId ,@channelId = @channelId ,@periodTypeId = @periodTypeId ,@date = @date ,@deviceParameterCodePull = @deviceParameterCodePull
COMMIT TRANSACTION; END TRY BEGIN CATCH
/* */ IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END;
-- !IMPORTANT /* , , */ IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
THROW; END CATCH
END;

CREATE PROCEDURE [Programmability].[GetArchiveRowByDate]
@channelTemplateId INT,
@date DATETIME,
@channelId INT,
@measurementDeviceId INT,
@periodTypeId INT
AS

BEGIN SET DATEFORMAT dmy; SET NOCOUNT ON;
SET @periodTypeId = IIF(@periodTypeId = 0, NULL, @periodTypeId);
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') ,(2, 'ColumnCaption');
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName]
SET @measurementDeviceId = IIF(@measurementDeviceId = 0, NULL, @measurementDeviceId)
-- , IF (@measurementDeviceId IS NULL AND @factoryNumber > 0 AND @deviceId > 0) BEGIN SET @measurementDeviceId = ( SELECT [MeasurementDevice].[Id] FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) WHERE [MeasurementDevice].[FactoryNumber] = @factoryNumber AND [MeasurementDevice].[DeviceId] = @deviceId ); END;
DECLARE @count INT = (SELECT COUNT(*) [Count] FROM [Business].[Archive] [Archive] WITH(NOLOCK) WHERE [Archive].[MeasurementDeviceId] = @measurementDeviceId AND ([Archive].[PeriodTypeId] = @periodTypeId OR @periodTypeId IS NULL) AND [Archive].[Time] BETWEEN @periodBegin AND @periodEnd)
/* 2. */ IF (@count <= 5000) BEGIN SELECT [PeriodType].[Description] [PeriodDescription], [Archive].[Time], [Archive].[Value] [Value], [Dp].[Description] [ParameterDescription] FROM [Business].[Archive] [Archive] WITH(NOLOCK) INNER JOIN [Dictionaries].[PeriodType] [PeriodType] WITH (NOLOCK) ON [Archive].[PeriodTypeId] = [PeriodType].[Id] INNER JOIN [Business].[MeasurementDevice] [Device] WITH (NOLOCK) ON [Archive].[MeasurementDeviceId] = [Device].[Id] INNER JOIN [Dictionaries].[DeviceParameter] [Dp] WITH (NOLOCK) ON [Dp].[Id] = [Archive].[DeviceParameterId]
WHERE [Archive].[MeasurementDeviceId] = @measurementDeviceId AND ([Archive].[PeriodTypeId] = @periodTypeId OR @periodTypeId IS NULL) AND [Archive].[Time] BETWEEN @periodBegin AND @periodEnd ORDER BY [Archive].[Time] DESC END ELSE BEGIN ;THROW 500001, ' . ', 1; END
/* 3. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'PeriodDescription', '', 1, 1) ,('MainData', 'Time', '', 1, 2) ,('MainData', 'ParameterDescription', '', 1, 3) ,('MainData', 'Value', '', 1, 4)
END;

