INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT
WHERE [Md].[Id] = @measurementDeviceId) [MeasurementDeviceInfo] UNPIVOT ( [Value] FOR [Name] IN ( [DeviceCode], [District], [FullAddress], [FactoryNumber], [MeasurementDeviceId], [SubModel] ) ) [ReportParameter];
SELECT [ReportParameter].[Name] ,[ReportParameter].[Value] FROM @reportParameter [ReportParameter];
SELECT [dp].[Description] ,[dtp].[Time] ,[dtp].[StringValue] ,[dp].[Code] FROM [Business].[DeviceTechnicalParameter] [dtp] WITH (NOLOCK) INNER JOIN [Dictionaries].[DeviceParameter] [dp] WITH (NOLOCK) ON [dtp].[DeviceParameterId] = [dp].[Id] WHERE [dtp].[MeasurementDeviceId] = @measurementDeviceId
COMMIT TRANSACTION;
END TRY
/* */ BEGIN CATCH DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ', @userName, @lineFeed, '@measurementDeviceId: ', @measurementDeviceId, @lineFeed, '@channelId: ', @channelId, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ', ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ([Time], [Exception], [Message], [UserName], [StackTrace]) VALUES (GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace); -- THROW; END CATCH; END;

BEGIN
SET DATEFORMAT dmy; SET LANGUAGE N''; SET NOCOUNT ON;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
DECLARE @periodBegin DATETIME = GETDATE(); DECLARE @periodEnd DATETIME = GETDATE();
IF (@filterType = 'ByLastDay' OR @filterType = 'ByLastDayTop1') BEGIN SET @periodBegin = DATEADD(DAY,DATEDIFF(DAY, 0, GETDATE()), 0); END ELSE IF (@filterType = 'ByLastWeek') BEGIN SET @periodBegin = DATEADD(DAY, DATEDIFF(DAY, 0, GETDATE()) / 7 * 7, 0); END ELSE IF (@filterType = 'By7Days') BEGIN SET @periodBegin = DATEADD(DAY, -7, GETDATE()); END; ELSE IF (@filterType = 'ByLast10MinutesTop1') BEGIN SET @periodBegin = DATEADD(MINUTE, -10, GETDATE()) END;
IF (@filterType != 'ByCount') BEGIN SET @top = 10000; END; IF (@filterType = 'ByLastDayTop1' OR @filterType = 'ByLast10MinutesTop1') BEGIN SET @top = 1; END;
INSERT INTO @resultSetName VALUES (1, 'MainData'), -- (2, 'ColumnCaption'); --
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */
SELECT [EmergencySituationParameter].[Description] [EmergencySituationParameterDescription]
,[Channel].[Id] [ChannelId] ,[Channel].[MeasurementDeviceId] [MeasurementDeviceId] ,[Channel].[Description] [ChannelDescription] ,[Channel].[ResourceSystemTypeId] [ResourceSystemTypeId]
,[MeasurementDevice].[AccessPointId] [AccessPointId] ,[MeasurementDevice].[SuccessConnectionPercent] [MeasurementDeviceSuccessConnectionPercent] ,[MeasurementDevice].[LastSuccessConnectionTime] [MeasurementDeviceLastSuccessConnectionTime] ,[MeasurementDevice].[LastStatusConnectionId] [LastStatusConnectionId]
,[ChannelTemplate].[Description] [ChannelTemplateDescription]
,[Building].[Description] [BuildingDescription] ,[Building].[Id] [BuildingId]
,[EmergencyLog].[EmergencyLogId] [Id] ,[EmergencyLog].[EmergencyLogTime] [EmergencyLogTime] ,[EmergencyLog].[EmergencyLogDescription] [EmergencyLogDescription] ,[EmergencyLog].[IsAcknowledgement] [IsAcknowledgement] ,[EmergencyLog].[AcknowledgementDate] [AcknowledgementDate] ,[EmergencyLog].[AcknowledgementUserName] [AcknowledgementUserName]
FROM [Business].[EmergencySituationParameter] [EmergencySituationParameter] WITH (NOLOCK)
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = [EmergencySituationParameter].[ChannelId]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id]
INNER JOIN [Business].[Placement] [Placement] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH(NOLOCK) ON [Channel].[Id] = [UserLinkChannel].[ChannelId]
INNER JOIN [Admin].[User] [User] WITH(NOLOCK) ON [User].[Id] = [UserLinkChannel].[UserId]
CROSS APPLY ( SELECT [EmergencyLog].[Id] [EmergencyLogId], [EmergencyLog].[Value] [EmergencyLogDescription], [EmergencyTimeSignature].[Time] [EmergencyLogTime], [EmergencyLog].[IsAcknowledgement] [IsAcknowledgement], [EmergencyLog].[AcknowledgementDate] [AcknowledgementDate], [EmergencyLog].[AcknowledgementUserName] [AcknowledgementUserName]
FROM [Business].[EmergencyLog] [EmergencyLog] WITH(NOLOCK)
/* INNER JOIN MERGE JOIN, TOP 1 */ /* HASH MATCH , , LOOP */
INNER JOIN [Business].[EmergencyTimeSignature] [EmergencyTimeSignature] WITH(NOLOCK) ON [EmergencyTimeSignature].[Id] = [EmergencyLog].[EmergencyTimeSignatureId]
WHERE [EmergencyLog].[RecoveryTime] IS NULL AND [EmergencyLog].[EmergencySituationParameterId] = [EmergencySituationParameter].[Id] AND ( @filterType = 'ByCount' OR ([EmergencyTimeSignature].[Time] BETWEEN @periodBegin AND @periodEnd ) )
ORDER BY [EmergencyLogTime] DESC OFFSET 0 ROWS FETCH NEXT @top ROWS ONLY --ORDER BY [EmergencyLogTime] DESC OFFSET 0 ROWS FETCH FIRST @top ROWS ONLY
) [EmergencyLog]
WHERE [User].[UserName] = @userName
--OPTION ( OPTIMIZE FOR ( @filterType = 'ByCount', @top = 1) );
/* 3. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 1, 1) ,('MainData', 'ChannelId', '. ', 1, 2) ,('MainData', 'ChannelDescription', ' ', 1, 3) ,('MainData', 'EmergencyLogDescription', '', 1, 4) ,('MainData', 'IsAcknowledgement', '', 1, 5) ,('MainData', 'EmergencyLogTime', ' ', 1, 6) ,('MainData', 'AcknowledgementDate', ' ', 1, 7) ,('MainData', 'AcknowledgementUserName', '', 1, 8)
/**/ END;

CREATE   PROCEDURE Programmability.GetEmergencyLogList
  @userName NVARCHAR(32),
  @filterType NVARCHAR(32),
  @top INTEGER  
AS

SET NOCOUNT ON;
DECLARE @empty NCHAR(1) = '';
/* */ SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME ); SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME );
/* */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'ReportParameter'), (2, 'MainData'), (3, 'SummaryData');
/* */ DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter OUTPUT inserted.[Name], inserted.[Value] SELECT [Name], [Value] FROM ( SELECT [Name], [Value] FROM ( SELECT CAST([User].[UserName] AS NVARCHAR(128)) [UserName] ,CAST(ISNULL([UserInfo].[FirstName], @empty) AS NVARCHAR(128)) [FirstName] ,CAST(ISNULL([UserInfo].[LastName], @empty) AS NVARCHAR(128)) [LastName] ,CAST(ISNULL([UserInfo].[Description], @empty) AS NVARCHAR(128)) [UserInfoDescription]
FROM [Admin].[User] [User] WITH (NOLOCK) LEFT OUTER JOIN [Business].[UserAdditionalInfo] [UserInfo] WITH (NOLOCK) ON [User].[Id] = [UserInfo].[Id] WHERE [User].[UserName] = @userName) [A] UNPIVOT ( [Value] FOR [Name] IN ( [UserName], [FirstName], [LastName], [UserInfoDescription] ) ) [B] ) [C]
UNION ALL
SELECT 'ResourceSystemTypeDescription' [Name], [ResourceSystemType].[Description] [Value] FROM [Dictionaries].[ResourceSystemType] [ResourceSystemType] WHERE [ResourceSystemType].[Id] = @resourceSystemTypeId ;
/* */ DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName = @userName, @userId = @userId OUT, @roleCode = @roleCode OUT;
/*---------------------------*/ /* */ SELECT [MainData].[MeasurementDeviceId] ,[MainData].[ChannelId] ,[MainData].[EmergencySituationParameterId] ,[MainData].[BuildingId]
,[MainData].[BuildingDescription] ,[MainData].[UserAdditionalInfoDescription] ,[MainData].[MeasurementDeviceDescription] ,[MainData].[EmergencySituationParameterDescription]
,[MainData].[EmergencyTimeSignatureFirstTime] ,[MainData].[EmergencyTimeSignatureLastTime]
,[MainData].[EmergencyLogLastRecoveryTime]
,[MainData].[EmergencyLogCount] ,[MainData].[EmergencyLogIsAcknowledgementCount]
,[MainData].[EmergencyLogAcknowledgementUserName] ,[MainData].[EmergencyLogAcknowledgementLastTime] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [Channel].[Id], [EmergencySituationParameter].[Id] ORDER BY [Channel].[Id]) Num,
/* */ [MeasurementDevice].[Id] [MeasurementDeviceId]
, [Channel].[Id] [ChannelId]
, [EmergencySituationParameter].[Id] [EmergencySituationParameterId]
, [Building].[Id] [BuildingId]
, [Building].[FullAddress] [BuildingDescription]
/* */ , [MeasurementDevice].[Description] [MeasurementDeviceDescription] , [UserAdditionalInfo].[Description] [UserAdditionalInfoDescription]
/* */ , [EmergencySituationParameter].[Description] [EmergencySituationParameterDescription]
/* [EmergencyTimeSignature].[Time]*/ , FIRST_VALUE ( [EmergencyTimeSignature].[Time] ) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyTimeSignature].[Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING ) [EmergencyTimeSignatureFirstTime]
/* [EmergencyTimeSignature].[Time]*/ , LAST_VALUE ( [EmergencyTimeSignature].[Time] ) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyTimeSignature].[Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING ) [EmergencyTimeSignatureLastTime]
/* [EmergencyLog].[RecoveryTime] */ , LAST_VALUE ( [EmergencyLog].[RecoveryTime] ) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyTimeSignature].[Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING ) [EmergencyLogLastRecoveryTime]
/* - */ , COUNT(*) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyTimeSignature].[Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [EmergencyLogCount]
/* !!!( "" )*/ ,( SELECT COUNT(*) FROM [Business].[EmergencyLog] [InnerEmergencyLog] WITH (NOLOCK)
INNER JOIN [Business].[EmergencySituationParameter] [InnerEmergencySituationParameter] WITH (NOLOCK) ON [InnerEmergencySituationParameter].[Id] = [InnerEmergencyLog].[EmergencySituationParameterId]
INNER JOIN [Business].[Channel] [InnerChannel] WITH (NOLOCK) ON [InnerChannel].[Id] = [InnerEmergencySituationParameter].[ChannelId] AND [Channel].[ResourceSystemTypeId] = @resourceSystemTypeId
WHERE [InnerEmergencyLog].[EmergencySituationParameterId] = [EmergencySituationParameter].[Id] AND [InnerChannel].[Id] = [Channel].[Id] AND [InnerEmergencyLog].[IsAcknowledgement] = 1 -- ,
AND (ISNULL([InnerEmergencyLog].[AcknowledgementDate], '19000101') >= @periodBegin AND ISNULL([InnerEmergencyLog].[AcknowledgementDate], '19000101') <= @periodEnd)
) [EmergencyLogIsAcknowledgementCount]
/* */ , LAST_VALUE ( [EmergencyLog].[AcknowledgementUserName] ) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyLog].[AcknowledgementDate] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [EmergencyLogAcknowledgementUserName] /* */ , LAST_VALUE ( [EmergencyLog].[AcknowledgementDate] ) OVER ( PARTITION BY [MeasurementDevice].[Id], [EmergencySituationParameter].[Id] ORDER BY [EmergencyLog].[AcknowledgementDate] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [EmergencyLogAcknowledgementLastTime]
FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK)
/* */ INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK) ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id] AND [Channel].[ResourceSystemTypeId] = @resourceSystemTypeId
/* */ INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
/* */ INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
/* */ LEFT JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] WITH (NOLOCK) ON [UserAdditionalInfo].[Id] = [Building].[UserAdditionalInfoId]
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH (NOLOCK) ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
/* */ INNER JOIN [Business].[EmergencySituationParameter] [EmergencySituationParameter] WITH (NOLOCK) ON [Channel].[Id] = [EmergencySituationParameter].[ChannelId]
/* */ INNER JOIN [Business].[EmergencyLog] [EmergencyLog] WITH (NOLOCK)
ON [EmergencySituationParameter].[Id] = [EmergencyLog].[EmergencySituationParameterId]
/* */ INNER JOIN [Business].[EmergencyTimeSignature] [EmergencyTimeSignature] WITH (NOLOCK) ON [EmergencyTimeSignature].[Id] = [EmergencyLog].[EmergencyTimeSignatureId] AND ([EmergencyTimeSignature].[Time] >= @periodBegin AND [EmergencyTimeSignature].[Time] <= @periodEnd) ) [MainData]
WHERE [MainData].[Num] = 1
ORDER BY [MainData].[MeasurementDeviceId], [MainData].[EmergencyTimeSignatureFirstTime]
/* ( )*/ OPTION (MAXDOP 1);
/*---------------------------*/ /* */
SELECT
[SummaryData].[EmergencySituationParameterId] ,[SummaryData].[EmergencySituationParameterTemplateDescription] ,[SummaryData].[BuildingCount] ,[SummaryData].[BuildingUserAdditionalInfoId] ,[SummaryData].[UserAdditionalInfoDescription] ,[SummaryData].[EmergencyLogCount] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id] ORDER BY [EmergencySituationParameterTemplate].[Id]) [Num],
[Building].[UserAdditionalInfoId] [BuildingUserAdditionalInfoId]
,[EmergencySituationParameterTemplate].[Id] [EmergencySituationParameterTemplateId]
,[EmergencySituationParameter].[Id] [EmergencySituationParameterId]
/* */ ,[EmergencySituationParameterTemplate].[Description] [EmergencySituationParameterTemplateDescription]
/* , */ /* COUNT */ ,( DENSE_RANK() OVER (PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id] ORDER BY [Building].[Id]) + DENSE_RANK() OVER (PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id] ORDER BY [Building].[Id] DESC) - 1 ) [BuildingCount]
/*- */
/* - */ , COUNT(*) OVER ( PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id] ORDER BY [Building].[Id] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [EmergencyLogCount]
/* */ ,[UserAdditionalInfo].[Description] [UserAdditionalInfoDescription]
FROM [Business].[EmergencySituationParameterTemplate] [EmergencySituationParameterTemplate] WITH (NOLOCK)
INNER JOIN [Business].[EmergencySituationParameter] [EmergencySituationParameter] WITH (NOLOCK) ON [EmergencySituationParameterTemplate].[Id] = [EmergencySituationParameter].[EmergencySituationParameterTemplateId]
INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK) ON [Channel].[Id] = [EmergencySituationParameter].[ChannelId] AND [Channel].[ResourceSystemTypeId] = @resourceSystemTypeId
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH (NOLOCK) ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
LEFT JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] WITH (NOLOCK) ON [UserAdditionalInfo].[Id] = [Building].[UserAdditionalInfoId]
INNER JOIN [Business].[EmergencyLog] [EmergencyLog] WITH (NOLOCK) ON [EmergencySituationParameter].[Id] = [EmergencyLog].[EmergencySituationParameterId]
/* */ INNER JOIN [Business].[EmergencyTimeSignature] [EmergencyTimeSignature] WITH (NOLOCK) ON [EmergencyTimeSignature].[Id] = [EmergencyLog].[EmergencyTimeSignatureId] AND ([EmergencyTimeSignature].[Time] >= @periodBegin AND [EmergencyTimeSignature].[Time] <= @periodEnd) ) [SummaryData]
WHERE [SummaryData].[Num] = 1 OPTION (MAXDOP 1);
/*
"- " */
SELECT [BuildingUserAdditionalInfoId]
,[EmergencySituationParameterTemplateId]
,[BuildingId]
,[EmergencySituationParameterTemplateDescription]
,[EmergencyTimeSignatureLastTime]
,[EmergencyLogLastRecoveryTime]
, COUNT (*) OVER (PARTITION BY [BuildingUserAdditionalInfoId], [EmergencySituationParameterTemplateId] ) [Count]
,ROW_NUMBER() OVER (PARTITION BY [BuildingUserAdditionalInfoId], [EmergencySituationParameterTemplateId] ORDER BY [BuildingId] ) [Num] FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id], [Building].[Id] ORDER BY [EmergencySituationParameterTemplate].[Id] ) [Num],
[Building].[UserAdditionalInfoId] [BuildingUserAdditionalInfoId]
,[EmergencySituationParameterTemplate].[Id] [EmergencySituationParameterTemplateId]
,[Building].[Id] [BuildingId]
,[EmergencySituationParameterTemplate].[Description] [EmergencySituationParameterTemplateDescription]
/* */ , LAST_VALUE ( [EmergencyLog].[RecoveryTime]) OVER ( PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id], [Building].[Id] ORDER BY [EmergencyLog].[RecoveryTime] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING ) [EmergencyLogLastRecoveryTime]
/* [EmergencyTimeSignature].[Time] */ ,LAST_VALUE ([EmergencyTimeSignature].[Time] ) OVER ( PARTITION BY [Building].[UserAdditionalInfoId], [EmergencySituationParameterTemplate].[Id], [Building].[Id] ORDER BY [EmergencyTimeSignature].[Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING ) [EmergencyTimeSignatureLastTime]
FROM [Business].[EmergencySituationParameterTemplate] [EmergencySituationParameterTemplate] WITH (NOLOCK)
INNER JOIN [Business].[EmergencySituationParameter] [EmergencySituationParameter] WITH (NOLOCK) ON [EmergencySituationParameterTemplate].[Id] = [EmergencySituationParameter].[EmergencySituationParameterTemplateId]
INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK) ON [Channel].[Id] = [EmergencySituationParameter].[ChannelId] AND [Channel].[ResourceSystemTypeId] = 4
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH (NOLOCK) ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = 7
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
LEFT JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] WITH (NOLOCK) ON [UserAdditionalInfo].[Id] = [Building].[UserAdditionalInfoId]
INNER JOIN [Business].[EmergencyLog] [EmergencyLog] WITH (NOLOCK) ON [EmergencySituationParameter].[Id] = [EmergencyLog].[EmergencySituationParameterId]
/* */ INNER JOIN [Business].[EmergencyTimeSignature] [EmergencyTimeSignature] WITH (NOLOCK) ON [EmergencyTimeSignature].[Id] = [EmergencyLog].[EmergencyTimeSignatureId] AND ([EmergencyTimeSignature].[Time] >= '20160801' AND [EmergencyTimeSignature].[Time] <= '20161220')
) [A] WHERE [A].[Num] = 1
RETURN 0;

CREATE PROCEDURE Programmability.GetEmergencySituationSummary
  @userName NVARCHAR(32),
  @resourceSystemTypeId INT,
  @periodBegin DATETIME, 
  @periodEnd DATETIME  
AS

BEGIN SET NOCOUNT ON;
BEGIN TRY SET @periodEnd = DATEADD(DAY, 1, @periodEnd);
SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME); SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME);
DECLARE @measurementDeviceId INT = 0; DECLARE @channelTemplateId INT = 0; DECLARE @userId INT = NULL; DECLARE @periodTypeId INT = 5;
/* 1. "-" */
SELECT @userId = [User].[Id] FROM [Admin].[User] [User] INNER JOIN [Admin].[Role] [Role] ON [User].[RoleId] = [Role].[Id] AND [User].[UserName] = @userName AND [Role].[Code] = 'HOLDER'
IF(@userId IS NULL) BEGIN -- ;THROW 500001, N' " "', 1; END;
IF NOT EXISTS ( SELECT 1 [Ch] FROM [Business].[Channel] [Channel] WITH (NOLOCK) INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Channel].[PlacementId] = [Placement].[Id] WHERE (ISNULL([Placement].[UserAdditionalInfoId], 0) = @userId) AND [Channel].[Id] = @channelId ) BEGIN -- ;THROW 500001, N' .', 1; END;
/* 2. */ SELECT @measurementDeviceId = [Channel].[MeasurementDeviceId], @channelTemplateId = [Channel].[ChannelTemplateId]
FROM [Business].[Channel] [Channel] WITH (NOLOCK)
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id] WHERE [Channel].[Id] = @channelId
IF(NOT EXISTS ( SELECT 1 FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WHERE [ParameterMapDeviceParameter].[ChannelTemplateId] = @channelTemplateId )) BEGIN -- ;THROW 500001, N' .', 1; END;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'ReportParameter') ,(2, 'MainData') ,(3, 'ColumnCaption')
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */ DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM (SELECT CAST([Device].[Code] AS NVARCHAR(128)) [DeviceCode], CAST(CONCAT([UserAdditionalInfo].[LastName], ' ', [UserAdditionalInfo].[FirstName], ' ', [UserAdditionalInfo].[Patronimic]) AS NVARCHAR(128)) [HolderDescription], CAST(CONCAT([PlacementPurpose].[Description], ' ', [Placement].[Number]) AS NVARCHAR(128)) [PlacementDescription], CAST([Building].[FullAddress] AS NVARCHAR(128)) [FullAddress], CONVERT(NVARCHAR(128), [Md].[FactoryNumber], 0) [FactoryNumber], CAST([ResourceSystemType].[ShortName] AS NVARCHAR(128)) [ResourceSystemTypeShortName], CAST([ResourceSystemType].[Description] AS NVARCHAR(128)) [ResourceSystemTypeDescription]
FROM [Business].[MeasurementDevice] [Md] WITH(NOLOCK)
INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK) ON [Device].[Id] = [Md].[DeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT
INNER JOIN [Business].[Placement] [Placement] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] WITH(NOLOCK) ON [UserAdditionalInfo].[Id] = [Placement].[UserAdditionalInfoId]
INNER JOIN [Dictionaries].[PlacementPurpose] [PlacementPurpose] WITH(NOLOCK) ON [PlacementPurpose].[Id] = [Placement].[PlacementPurposeId]
INNER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [District].[Id] = [Building].[DistrictId]
INNER JOIN [Dictionaries].[ResourceSystemType] [ResourceSystemType] WITH(NOLOCK) ON [Channel].[ResourceSystemTypeId] = [ResourceSystemType].[Id]
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId]
WHERE [Md].[Id] = @measurementDeviceId) [ReportParameterInfo] UNPIVOT ( [Value] FOR [Name] IN ( [DeviceCode], [HolderDescription], [FullAddress], [FactoryNumber], [ResourceSystemTypeShortName], [ResourceSystemTypeDescription], [PlacementDescription] ) ) [ReportParameter];
SELECT [ReportParameter].[Name], [ReportParameter].[Value] FROM @reportParameter [ReportParameter]; /**/
/* 3. */ SELECT [Archive].[Num], [Archive].[ParameterId], [Archive].[DeviceParameterId], [Archive].[Time], [Archive].[Value], [Archive].[NextValue], [Archive].[NextValue] - [Archive].[Value] [Delta], [Archive].[ParameterDescription], [Archive].[MeasurementUnitDescription], [Archive].[DimentionPrefix] INTO #Result FROM ( SELECT TOP 100 PERCENT [Archive].[Num], [Archive].[ParameterId], [Archive].[DeviceParameterId], [Archive].[Time], [Archive].[Value], LEAD([Archive].[Value]) OVER (PARTITION BY [Archive].[DeviceParameterId] ORDER BY [Archive].[Time]) [NextValue], [Archive].[ParameterDescription], [Archive].[MeasurementUnitDescription], [Archive].[DimentionPrefix] FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY [A].[DeviceParameterId], CONVERT(DATE, [A].[Time]) ORDER BY [A].[Time] DESC, [A].[DeviceParameterId] ASC ) [Num], [A].[Time], [A].[Value], [A].[DeviceParameterId], [Parameter].[Id] [ParameterId], [Parameter].[Description] [ParameterDescription], [MeasurementUnit].[Description] [MeasurementUnitDescription], [Dimention].[Prefix] [DimentionPrefix] FROM [Business].[Archive] [A] WITH(NOLOCK)
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH(NOLOCK) ON [A].[DeviceParameterId] = [DeviceParameter].[Id]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH(NOLOCK) ON [ParameterMapDeviceParameter].[ChannelTemplateId] = @channelTemplateId AND [ParameterMapDeviceParameter].[DeviceParameterId] = [A].[DeviceParameterId] AND [ParameterMapDeviceParameter].[PeriodTypeId] = @periodTypeId
INNER JOIN [Dictionaries].[Parameter] [Parameter] WITH(NOLOCK) ON [ParameterMapDeviceParameter].[ParameterId] = [Parameter].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [ParameterMapDeviceParameter].[MeasurementUnitId] = [MeasurementUnit].[Id]
INNER JOIN [Dictionaries].[Dimension] [Dimention] WITH(NOLOCK) ON [Dimention].[Id] = [ParameterMapDeviceParameter].[DimensionId]
WHERE [A].[MeasurementDeviceId] = @measurementDeviceId AND [A].[DeviceParameterId] IN ( SELECT [ParameterMapDeviceParameter].[DeviceParameterId] FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH(NOLOCK) WHERE [ParameterMapDeviceParameter].[ChannelTemplateId] = @channelTemplateId AND [ParameterMapDeviceParameter].[PeriodTypeId] = @periodTypeId ) AND [A].[PeriodTypeId] = @periodTypeId AND ([A].[Time] >= @periodBegin AND [A].[Time] <= @periodEnd) ) [Archive]
WHERE [Archive].[Num] = 1 ORDER BY [Archive].[Time], [Archive].[DeviceParameterId] ASC ) [Archive] ORDER BY [Archive].[DeviceParameterId];
DECLARE @count INT = ( SELECT COUNT(*) [Count] FROM #Result );
SELECT [Archive].[Num] ,[Archive].[ParameterId] ,[Archive].[DeviceParameterId] ,[Archive].[Time] ,[Archive].[Value] ,[Archive].[NextValue] ,[Archive].[NextValue] - [Archive].[Value] [Delta] ,[Archive].[ParameterDescription] ,[Archive].[MeasurementUnitDescription] ,[Archive].[DimentionPrefix]
FROM #Result [Archive] WHERE [Archive].[NextValue] IS NOT NULL AND [Archive].[Delta] IS NOT NULL;
/**/
/*4. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'ParameterDescription', '', 1, 1) ,('MainData', 'Value', ' ', 1, 2) ,('MainData', 'Delta', ' ', 1, 3) ,('MainData', 'Time', ' ', 1, 4) ,('MainData', 'DeviceParameterId', ' ', 0, 5) ,('MainData', 'ParameterId', ' ', 0, 6) ,('MainData', 'DimentionPrefix', '', 1, 7) ,('MainData', 'MeasurementUnitDescription', ' ', 1, 8)
/**/
END TRY
/* */ BEGIN CATCH DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ' , @userName, @lineFeed, '@measurementDeviceId: ', @measurementDeviceId, @lineFeed, '@channelId: ', @channelId, @lineFeed, '@periodBegin: ', @periodBegin, @lineFeed, '@periodEnd: ', @periodEnd, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ' , ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ( [Time], [Exception], [Message], [UserName], [StackTrace] ) VALUES ( GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace ); -- THROW; END CATCH; END;

CREATE PROCEDURE [Programmability].[GetFlatHolderAccountingParamSheet]
  @userName NVARCHAR(32),  
  @channelId INT,
  @periodBegin DATETIME,
  @periodEnd DATETIME
AS

BEGIN
SET NOCOUNT ON;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'ReportParameter'), (2, 'MainData'), (3, 'ColumnCaption');
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName]
/**/
/* 2. */ DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM (SELECT CAST([Organization].[Description] AS NVARCHAR(128)) [OrganizationDescription], CAST([OrganizationType].[Description] AS NVARCHAR(128)) [OrganizationTypeDescription], CAST(@isBeginBuildingWithNewPage AS NVARCHAR(128)) [IsBeginBuildingWithNewPage]
FROM [Business].[Organization] [Organization] WITH(NOLOCK)
INNER JOIN [Dictionaries].[OrganizationType] [OrganizationType] WITH(NOLOCK) ON [Organization].[OrganizationTypeId] = [OrganizationType].[Id]
WHERE [Organization].[Id] = @organizationId
) [ReportParameterInfo] UNPIVOT ( [Value] FOR [Name] IN ( [OrganizationDescription], [OrganizationTypeDescription], [IsBeginBuildingWithNewPage] ) ) [ReportParameter];
SELECT [ReportParameter].[Name], [ReportParameter].[Value] FROM @reportParameter [ReportParameter];
/**/
/*2. */ OPEN SYMMETRIC KEY [EnergyTechAudit.PowerAccounting.Database.SymmetricKey] DECRYPTION BY CERTIFICATE [EnergyTechAudit.PowerAccounting.Database.Certificate];
SELECT [Building].[FullAddress] [Address], [Placement].[Number] [FlatNumber],
[UserAdditionalInfo].[LastName] [LastName], [UserAdditionalInfo].[FirstName] [FirstName], [UserAdditionalInfo].[Patronimic] [Patronimic],
[User].[UserName] [Login], CONVERT(NVARCHAR(32), DECRYPTBYKEY ([User].[EncryptedPassword])) [Password], [Building].[Id] [BuildingId]
FROM [Admin].[User] [User] WITH (NOLOCK)
INNER JOIN [Admin].[Role] [Role] WITH (NOLOCK) ON [User].[RoleId] = [Role].[Id] AND [Role].[Code] = 'HOLDER'
INNER JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] WITH (NOLOCK) ON [User].[Id] = [UserAdditionalInfo].[Id]
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[UserAdditionalInfoId] = [UserAdditionalInfo].[Id]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[OrganizationId] = @organizationId AND [Building].[Id] = [Placement].[BuildingId]
CLOSE SYMMETRIC KEY [EnergyTechAudit.PowerAccounting.Database.SymmetricKey];
/* 3. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Address', '', 1, 1) ,('MainData', 'FlatNumber', '', 1, 2)
,('MainData', 'LastName', '', 1, 3) ,('MainData', 'FirstName', '', 1, 4) ,('MainData', 'Patronimic', 'O', 1, 5)
,('MainData', 'Login', '', 1, 6) ,('MainData', 'Password', '', 1, 7) ,('MainData', 'BuildingId', ' ', 1, 8)
/**/
RETURN 0; END;

BEGIN   
	SET NOCOUNT ON;  
	SET XACT_ABORT ON;

	BEGIN TRY      
		SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

		BEGIN TRANSACTION;

		DECLARE  @errorMsg1 NVARCHAR(2048) = CONCAT
			(
				N'         (Organization)   ',
				N'Organization.HeatCurveSupplyPipe / Organization.HeatCurveReturnPipe, ',
				N'  .'
			),
			@resultSetName [Programmability].[ResultSetNameTableType],
			@cityCode NVARCHAR(32), 
			@minimalTemperature INT;

		IF(@isPreload = 1)
		BEGIN
			INSERT INTO @resultSetName 
				OUTPUT inserted.[Order], inserted.[Name]
				VALUES 
                    (1, 'HeatSysChannels'),
                    (2, 'DiagnosticOrganizationHeatCurves')

			SELECT 
				[Channel].[Id] [Id], 
				[Channel].[Description] [Code],				
				[Channel].[Description] [Description]				
			FROM [Business].[Placement] [Placement] WITH(NOLOCK)    
				INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]				
			WHERE [Placement].[BuildingId] = @buildingId 
				AND [Placement].[PlacementPurposeId] = 2	-- AccountingNode	
				AND [Channel].[ResourceSystemTypeId] = 4	-- HeatSys
				AND [Channel].[ResourceSubsystemTypeId] IS NULL

            DECLARE @isValidOrganizationHeatCurves BIT;

            EXEC [Programmability].[GetOrganizationHeatCurves]
                @buildingId = @buildingId, 
                @onlyDiagnostic = @isPreload,
                @isValid = @isValidOrganizationHeatCurves OUTPUT

            SELECT @isValidOrganizationHeatCurves [IsValid];

			COMMIT TRANSACTION;   
			RETURN ;
		END;

	    SELECT @cityCode = [City].[LatinCode], @minimalTemperature = [City].[MinimalTemperature]
			FROM [Business].[Building] [Building] WITH(NOLOCK)
		        INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [Building].[DistrictId] = [District].[Id]
			    INNER JOIN [Dictionaries].[City] [City] ON [District].[CityId] = [City].[Id]    
				WHERE [Building].[Id] = @buildingId;
        IF(@minimalTemperature IS NULL)
		BEGIN
			SET @minimalTemperature	= @defaultMinimalTemperature;
		END;

		DECLARE @hourlyConsumptionHeat DECIMAL(9,4) = 
		TRY_CAST
		(
			(
			   SELECT TOP (1) [Value] FROM [Core].[DynamicParameterValue] [DynamicParameterValue]
			   WHERE [EntityId] = @buildingId AND [DynamicParameterId] = 180	-- 'Building.HourlyConsumptionHeat'
			) 
			AS DECIMAL(9,4)
		);  		
		IF(@hourlyConsumptionHeat IS NULL)
		BEGIN
			SET @hourlyConsumptionHeat = @defaultHourlyConsumptionHeat;
		END;

		DECLARE @cwsForHwsHeatingFactor DECIMAL(9,4) = 
		TRY_CAST
		(
			(
			   SELECT TOP (1) [Value] FROM [Core].[DynamicParameterValue] [DynamicParameterValue]
			   WHERE [EntityId] = @buildingId AND [DynamicParameterId] = 182	-- 'Building.CwsForHwsHeatingFactor'
			) 
			AS DECIMAL(9,4)
		);   
        IF(@cwsForHwsHeatingFactor IS NULL)
		BEGIN
			SET @cwsForHwsHeatingFactor = @defaultCwsForHwsHeatingFactor;
		END;

		DECLARE @useDeductionHwsHeat BIT = 
		TRY_CAST
		(
			(
			   SELECT TOP (1) [Value] FROM [Core].[DynamicParameterValue] [DynamicParameterValue]
			   WHERE [EntityId] = @buildingId AND [DynamicParameterId] = 183	-- 'Building.UseDeductionHwsHeat'
			) 
			AS BIT
		);   
        IF(@useDeductionHwsHeat IS NULL)
		BEGIN
			SET @useDeductionHwsHeat = @defaultUseDeductionHwsHeat;
		END;

		INSERT INTO @resultSetName 
		OUTPUT inserted.[Order], inserted.[Name]
		VALUES
			(1, 'ReportParameter'),         			
			(2, 'HeatCurvesData'),
			(3, 'MeteoData'),
			(4, 'HwsChannel')

		DECLARE @reportParameter [Programmability].[CustomParameterTableType]; 	

		INSERT INTO @reportParameter 
		OUTPUT inserted.[Name], inserted.[Value]
		VALUES	
			('UserName', @userName),
			('ChannelId', CAST( @heatSysChannelId AS NVARCHAR )),
			('FullAddress', (SELECT  [FullAddress] FROM [Business].[Building] [Building] WITH(NOLOCK) WHERE [Building].[Id] = @buildingId)),

			('MinimalTemperature', CAST( @minimalTemperature AS NVARCHAR )),
			('HourlyConsumptionHeat', CAST( @hourlyConsumptionHeat AS NVARCHAR )),
			('CwsForHwsHeatingFactor', CAST( @hourlyConsumptionHeat AS NVARCHAR )),			
			('UseDeductionHwsHeat', CAST(IIF(@useDeductionHwsHeat = 1, 'true', 'false') AS NVARCHAR )),

			('DefaultHourlyConsumptionHeat', CAST( @defaultHourlyConsumptionHeat AS NVARCHAR )),			
			('DefaultUseDeductionHwsHeat', CAST(IIF(@defaultUseDeductionHwsHeat = 1, 'true', 'false') AS NVARCHAR ));

        EXEC [Programmability].[GetOrganizationHeatCurves]
                @buildingId = @buildingId, 
                @onlyDiagnostic = @isPreload,
                @isValid = @isValidOrganizationHeatCurves OUTPUT

		/*    */
		 DECLARE @today DATETIME  = CAST( CAST( GETDATE() AS DATE ) AS DATETIME);		
		 SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME);
		 SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME);		 
		 SET @periodEnd = DATEADD(DAY, 1, @periodEnd);

		 IF(@periodEnd >= @today)
		 BEGIN
			SET @periodEnd = @today;
		 END;

		DECLARE @meteoStationMeasurementDeviceId INT = 
		(
			SELECT TOP(1) [Id]
			FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK)
			WHERE [MeasurementDevice].[DeviceId] = 52
		);

		DECLARE @meteoStationOutdoorAirDeviceParameterId INT = 
		(
			SELECT TOP(1) [DeviceParameter].[Id] FROM [Dictionaries].[DeviceParameter] [DeviceParameter]  WITH(NOLOCK)
			WHERE 
				[DeviceParameter].[DeviceId] = 52 AND
				[DeviceParameter].[Code] = 'Monitoring_Temper_MeteoOutdoorAir_' + @cityCode 
		);

		SELECT CONVERT(DATE, [Archive].[Time]) [Time], 
			   AVG([Archive].[Value]) [AverageTemperature]
			FROM [Business].[Archive] [Archive] WITH(NOLOCK)
			WHERE [PeriodTypeId] = 1 
			AND [MeasurementDeviceId] = @meteoStationMeasurementDeviceId
			AND [DeviceParameterId] = @meteoStationOutdoorAirDeviceParameterId
			AND [Time] >= @periodBegin
			AND [Time] < @periodEnd
		GROUP BY CONVERT(DATE, [Archive].[Time])
		ORDER BY [Time]

		SELECT 
			[Channel].[Id] [Id], 					
			[Channel].[Description] [Description],

			[Channel].[ResourceSystemTypeId] [ResourceSystemTypeId],
			[Channel].[ResourceSubsystemTypeId] [ResourceSubsystemTypeId]

		FROM [Business].[Placement] [Placement] WITH(NOLOCK)    
			INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId]				
		WHERE [Placement].[BuildingId] = @buildingId 
			AND [Placement].[PlacementPurposeId] = 2	-- AccountingNode	
			AND 
			(
				[Channel].[ResourceSystemTypeId] = 3 OR -- Hws
				(
					[Channel].[ResourceSystemTypeId] = 2 AND [Channel].[ResourceSubsystemTypeId] = 6 -- Cws + CwsForHws
				)
			)			
		COMMIT TRANSACTION;   
	END TRY
	BEGIN CATCH

	  IF (XACT_STATE()) = -1
      BEGIN       
        ROLLBACK TRANSACTION;
      END;

      IF (XACT_STATE()) = 1
      BEGIN      
        COMMIT TRANSACTION;   
      END;

      DECLARE @lineFeed CHAR(1) = CHAR(13);

      DECLARE @parameterListTrace NVARCHAR(256) = 
             CONCAT
             (  
                '@userName: ' ,            @userName, @lineFeed,                 
                '@buildingId: ',           @buildingId, @lineFeed, 
                '@periodBegin: ',          @periodBegin, @lineFeed, 
                '@periodEnd: ',            @periodEnd, @lineFeed                 
             );

      DECLARE @errorMessage NVARCHAR(MAX) = 
         CONCAT
         (
            'ErrorNumber: ',     ERROR_NUMBER(), @lineFeed,
            'ErrorSeverity: ',   ERROR_SEVERITY(), @lineFeed,
            'ErrorState: ' ,     ERROR_STATE(), @lineFeed,
            'ErrorProcedure: ',  ERROR_PROCEDURE(), @lineFeed,
            'ErrorLine: ',       ERROR_LINE(), @lineFeed,
            'ErrorMessage: ',    ERROR_MESSAGE()
         );

      INSERT INTO [Admin].[ErrorLogEntry] 
         (
            [Time], 
            [Exception], 
            [Message], 
            [UserName],
            [StackTrace]
         )
      VALUES
         (
            GETDATE(), 
            'InternalSqlServerException', 
            @errorMessage, 
            SUSER_NAME(),
           @parameterListTrace
         );

      THROW;	
	END CATCH;
END;

CREATE PROCEDURE Programmability.GetHeatEnergyConsumptionAnalysis
  @userName NVARCHAR(32),  
  @buildingId INT,
  @heatSysChannelId INT,

  @periodBegin DATETIME,
  @periodEnd DATETIME,
  @calcIndoorAirTemperature INT,
  @thermalEnergyRate INT,

  @defaultHourlyConsumptionHeat DECIMAL(9,4),
  @defaultMinimalTemperature INT,
  @defaultUseDeductionHwsHeat BIT, 
  @defaultCwsForHwsHeatingFactor DECIMAL(9,4),

  @isPreload BIT = 0
AS

BEGIN
DECLARE @finalInstancePeriodType INT = 5; -- const SET NOCOUNT ON; SET XACT_ABORT ON;
BEGIN TRY SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; BEGIN TRANSACTION;
DECLARE @isDebug BIT = 1;
SET @periodBegin = CAST(CAST(@periodBegin AS DATE) AS DATETIME); SET @periodEnd = CAST(CAST(@periodEnd AS DATE) AS DATETIME);
DECLARE @periodEndWithInclude DATETIME = DATEADD(DAY, 1, @periodEnd);
DECLARE @resourceSystemTypeMapParametrer TABLE ( ResourceSystemType INT NOT NULL, ParameterId INT NOT NULL );
INSERT INTO @resourceSystemTypeMapParametrer VALUES (2, 40), (5, 469);
/* Let's build table with result sets names that will be returned from the stored procedure */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'ReportParameter'), (2, 'MainData'), (3, 'SummaryData');
/* Report parameters resultset */
DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM ( SELECT TOP 1 CAST([Dimension].[Prefix] AS NVARCHAR(128)) [DimensionPrefix], CAST([MeasurementUnit].[Description] AS NVARCHAR(128)) [MeasurementUnitDescription], CAST([ResourceSystemType].[ShortName] AS NVARCHAR(128)) [ResourceSystemTypeShortName], CAST([ResourceSystemType].[Description] AS NVARCHAR(128)) [ResourceSystemTypeDescription]
FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] ON [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] INNER JOIN [Dictionaries].[ResourceSystemType] [ResourceSystemType] ON [ChannelTemplate].[ResourceSystemTypeId] = [ResourceSystemType].[Id]
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [ParameterMapDeviceParameter].[ParameterId] = [Parameter].[Id] INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [ParameterMapDeviceParameter].[MeasurementUnitId] = [MeasurementUnit].[Id] INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [ParameterMapDeviceParameter].[DimensionId] = [Dimension].[Id]
WHERE [ParameterMapDeviceParameter].[ParameterId] IN ( SELECT [ResourceSystemTypeMapParametrer].[ParameterId] FROM @resourceSystemTypeMapParametrer [ResourceSystemTypeMapParametrer] WHERE [ResourceSystemTypeMapParametrer].[ResourceSystemType] = @resourceSystemTypeId ) ) [MeasurementUnitInfo] UNPIVOT ( [Value] FOR [Name] IN ( [DimensionPrefix], [MeasurementUnitDescription], [ResourceSystemTypeShortName], [ResourceSystemTypeDescription] ) ) [ReportParameter];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM ( SELECT CAST([Building].[Description] AS NVARCHAR(128)) [BuildingDescription] , CAST([District].[Description] AS NVARCHAR(128)) [District] FROM [Business].[Building] [Building] WITH(NOLOCK) INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [Building].[DistrictId] = [District].[Id]
WHERE [Building].[Id] = @buildingId ) [BuildingInfo] UNPIVOT ( [Value] FOR [Name] IN ([BuildingDescription], [District]) ) [ReportParameter];
SELECT [ReportParameter].[Name], [ReportParameter].[Value] FROM @reportParameter [ReportParameter];
;WITH [IndividualAccountingArchive] AS ( SELECT [Archive].[Id] ,[Archive].[Time] ,[Archive].[Value] ,[Archive].[MeasurementDeviceId] ,[Archive].[DeviceParameterId] ,[Archive].[TimeSignatureId] FROM [Business].[Archive] [Archive] WITH (NOLOCK) WHERE [Archive].[PeriodTypeId] = @finalInstancePeriodType AND -- the most preferred and the fastest way (excluding TimeSignature) [Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEndWithInclude ) SELECT [IndividualAccountingResult].[PlacementNumber] ,[IndividualAccountingResult].[ChannelDescription] ,[IndividualAccountingResult].[Diff] ,[IndividualAccountingResult].[Value]
FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [IndividualAccountingArchive].[Time] DESC) [Num]
,LAST_VALUE([IndividualAccountingArchive].[Value]) OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [IndividualAccountingArchive].[Time] ASC) - FIRST_VALUE([IndividualAccountingArchive].[Value]) OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [IndividualAccountingArchive].[Time] ASC) [Diff] ,LAST_VALUE([IndividualAccountingArchive].[Value]) OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [IndividualAccountingArchive].[Time] ASC) [Value] ,[IndividualAccountingArchive].[Time] ,[Channel].[Description] [ChannelDescription] ,[Placement].[Number] [PlacementNumber]
FROM [Business].[Channel] [Channel] WITH (NOLOCK)
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[BuildingId] = @buildingId AND [Placement].[HasIndividualAccounting] = 1 AND [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) ON [MeasurementDevice].[Id] = [Channel].[MeasurementDeviceId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK) ON [ParameterMapDeviceParameter].[ChannelTemplateId] = [Channel].[ChannelTemplateId] AND [ParameterMapDeviceParameter].[PeriodTypeId] = @finalInstancePeriodType
-- Let them be selected achive values for the given ParamerterId's from ResourceSystemTypeMapParametrer AND [ParameterMapDeviceParameter].[ParameterId] IN ( SELECT [ResourceSystemTypeMapParametrer].[ParameterId] FROM @resourceSystemTypeMapParametrer [ResourceSystemTypeMapParametrer] WHERE [ResourceSystemTypeMapParametrer].[ResourceSystemType] = @resourceSystemTypeId )
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH (NOLOCK) ON [DeviceParameter].[Id] = [ParameterMapDeviceParameter].[DeviceParameterId]
LEFT JOIN [IndividualAccountingArchive] WITH (NOLOCK) ON [IndividualAccountingArchive].[MeasurementDeviceId] = [Channel].[MeasurementDeviceId] AND [IndividualAccountingArchive].[DeviceParameterId] = [DeviceParameter].[Id]
WHERE [Channel].[ResourceSystemTypeId] = @resourceSystemTypeId )[IndividualAccountingResult] WHERE [IndividualAccountingResult].[Num] = 1
/* Channels list of measuring devices of common house accounting */
;WITH [CommonHouseAccountingChannel] AS ( SELECT
[Channel].[Id], [Channel].[ChannelTemplateId], [Channel].[MeasurementDeviceId], [Channel].[Description] [ChannelDescription],
[ParameterMapDeviceParameter].[ParameterId] [ParameterId]
FROM [Business].[Channel] [Channel] WITH(NOLOCK)
INNER JOIN [Business].[Placement] [Placement] WITH(NOLOCK) ON [Channel].[PlacementId] = [Placement].[Id] AND [Placement].[PlacementPurposeId] = 2
INNER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Placement].[BuildingId] = [Building].[Id] AND [Building].[Id] = @buildingId
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [ParameterMapDeviceParameter].[ChannelTemplateId]
AND [ParameterMapDeviceParameter].[PeriodTypeId] = @finalInstancePeriodType AND [ParameterMapDeviceParameter].[ParameterId] IN ( SELECT [ResourceSystemTypeMapParametrer].[ParameterId] FROM @resourceSystemTypeMapParametrer [ResourceSystemTypeMapParametrer] WHERE [ResourceSystemTypeMapParametrer].[ResourceSystemType] = @resourceSystemTypeId )
WHERE [Channel].[ResourceSystemTypeId] = @resourceSystemTypeId ),
/* Measuring results of common house accounting devices */ [CommonHouseAccountingArchive] AS ( SELECT [Archive].[Time] ,[Archive].[Value] ,[Archive].[MeasurementDeviceId] ,[Archive].[DeviceParameterId]
FROM [Business].[Archive] [Archive] WITH (NOLOCK) WHERE [Archive].[PeriodTypeId] = @finalInstancePeriodType AND -- the most preferred and the fastest way (excluding TimeSignature) [Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEndWithInclude )
SELECT [CommonHouseAccountingResult].[Num] [MeasurementDeviceNumber] ,[CommonHouseAccountingResult].[ChannelDescription] ,[CommonHouseAccountingResult].[Diff] ,[CommonHouseAccountingResult].[Value]
FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY [CommonHouseAccountingChannel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [CommonHouseAccountingArchive].[Time] DESC) [Num]
,LAST_VALUE([CommonHouseAccountingArchive].[Value]) OVER (PARTITION BY [CommonHouseAccountingChannel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [CommonHouseAccountingArchive].[Time] ASC) - FIRST_VALUE([CommonHouseAccountingArchive].[Value]) OVER (PARTITION BY [CommonHouseAccountingChannel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [CommonHouseAccountingArchive].[Time] ASC) [Diff]
,LAST_VALUE([CommonHouseAccountingArchive].[Value]) OVER (PARTITION BY [CommonHouseAccountingChannel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [CommonHouseAccountingArchive].[Time] ASC) [Value] ,[CommonHouseAccountingArchive].[Time] ,[CommonHouseAccountingChannel].[ChannelDescription]
FROM [CommonHouseAccountingArchive]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) ON [MeasurementDevice].[Id] = [CommonHouseAccountingArchive].[MeasurementDeviceId]
INNER JOIN [CommonHouseAccountingChannel] ON [MeasurementDevice].[Id] = [CommonHouseAccountingChannel].[MeasurementDeviceId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK) ON [ParameterMapDeviceParameter].[ChannelTemplateId] = [CommonHouseAccountingChannel].[ChannelTemplateId]
AND [ParameterMapDeviceParameter].[PeriodTypeId] = @finalInstancePeriodType AND [CommonHouseAccountingArchive].[DeviceParameterId] = [ParameterMapDeviceParameter].[DeviceParameterId]
-- Let them be selected achive values for the given ParamerterId's from ResourceSystemTypeMapParametrer AND [ParameterMapDeviceParameter].[ParameterId] IN ( SELECT [ResourceSystemTypeMapParametrer].[ParameterId] FROM @resourceSystemTypeMapParametrer [ResourceSystemTypeMapParametrer] WHERE [ResourceSystemTypeMapParametrer].[ResourceSystemType] = @resourceSystemTypeId ) ) [CommonHouseAccountingResult] WHERE [CommonHouseAccountingResult].[Num] = 1 ;
COMMIT TRANSACTION; END TRY
BEGIN CATCH PRINT ERROR_MESSAGE();
IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END;
IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
IF (@isDebug = 0) BEGIN
DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ' , @userName, @lineFeed, '@resourceSystemTypeId: ', @resourceSystemTypeId, @lineFeed, '@buildingId: ', @buildingId, @lineFeed, '@periodBegin: ', @periodBegin, @lineFeed, '@periodEnd: ', @periodEnd, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ' , ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ( [Time], [Exception], [Message], [UserName], [StackTrace] ) VALUES ( GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace );
THROW; END;
END CATCH; END;

-- ====================================================================================================
-- Author:  	Leo
-- Create date: 2019-07-22
-- Update date: 2019-07-23
-- Description: 
-- ====================================================================================================

CREATE PROCEDURE [Programmability].[GetIndividualAccountingBalanceSheet] 
    @userName NVARCHAR(64),
    @buildingId INT,
    @resourceSystemTypeId INT,
    @periodBegin DATETIME,
    @periodEnd DATETIME
AS

BEGIN
SET NOCOUNT ON; SET XACT_ABORT ON;
DECLARE @buildingId INT = (SELECT [Placement].[BuildingId] FROM [Business].[Channel] [Channel] WITH (NOLOCK) INNER JOIN [Business].[Placement] [Placement] ON [Channel].[PlacementId] = [Placement].[Id] WHERE @channelId = [Channel].[Id]);
SET @periodBegin = CAST(CAST(@periodBegin AS DATE) AS DATETIME); SET @periodEnd = CAST(CAST(@periodEnd AS DATE) AS DATETIME);
DECLARE @periodEndWithInclude DATETIME = DATEADD(DAY, 1, @periodEnd);
/* Perform checking the channel on individual accounting */ IF NOT EXISTS (SELECT 1 FROM [Business].[Channel] [Channel] WHERE [Channel].[Id] = @channelId AND EXISTS (SELECT 1 FROM [Business].[Placement] [Placement] WHERE [Placement].[Id] = [Channel].[PlacementId] AND [Placement].[HasIndividualAccounting] = 1)) BEGIN DECLARE @errorPermissionMsg NVARCHAR(2048) = [Programmability].[GetSysMessage](297, 1033); THROW 500001, @errorPermissionMsg, 1; END
/* Make table with resultset names */
DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'MainData'), (2, 'ColumnCaption');
/* Extract last TimeSignature Id */
DECLARE @lastTimeSignatureId INT = (SELECT [MeasurementDevice].[LastTimeSignatureId] FROM [Business].[MeasurementDevice] [MeasurementDevice] INNER JOIN [Business].[Channel] WITH (NOLOCK) ON [MeasurementDevice].[Id] = [Channel].[MeasurementDeviceId] WHERE [Channel].[Id] = @channelId);
IF @lastTimeSignatureId IS NULL BEGIN SET @lastTimeSignatureId = (SELECT [TimeSignature].[Id] FROM (SELECT ROW_NUMBER() OVER (ORDER BY [TimeSignature].[Time] DESC) [Num] ,[TimeSignature].[Id] FROM [Business].[TimeSignature] [TimeSignature] WITH (NOLOCK) WHERE [TimeSignature].[MeasurementDeviceId] IN (SELECT [Channel].[MeasurementDeviceId] FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].[Id] = @channelId)) [TimeSignature] WHERE [Num] = 1); END;
BEGIN TRY /* Will read dirty */ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
BEGIN TRANSACTION; /* Extract main data */ WITH [Archive] AS (SELECT [Archive].[Time] ,[Archive].[Value] ,[Archive].[DeviceParameterId] ,[Archive].[MeasurementDeviceId]
FROM [Business].[Archive] [Archive] WHERE [Archive].[PeriodTypeId] = 5 AND [Archive].[TimeSignatureId] = @lastTimeSignatureId),
[ParameterMapDeviceParameter] AS (SELECT [ParameterMapDeviceParameter].[Id] ,[ParameterMapDeviceParameter].[ParameterId] ,[ParameterMapDeviceParameter].[MeasurementUnitId] ,[ParameterMapDeviceParameter].[DimensionId] ,[ParameterMapDeviceParameter].[DeviceParameterId]
FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter]
WHERE [ParameterMapDeviceParameter].[PeriodTypeId] = 5 AND [ParameterMapDeviceParameter].[ChannelTemplateId] IN (SELECT [Channel].[ChannelTemplateId] FROM [Business].[Channel] [Channel] WHERE [Channel].[Id] = @channelId)),
[Archive1] AS (SELECT [Archive].[Id] ,[Archive].[Time] ,[Archive].[Value] ,[Archive].[MeasurementDeviceId] ,[Archive].[DeviceParameterId]
FROM [Business].[Archive] [Archive] WITH (NOLOCK) WHERE [Archive].[PeriodTypeId] = 5 /* FinalInstant */ AND [Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEndWithInclude)
/* Final selection from CTEs */
SELECT [Parameter].[Id] [ParameterId1] ,[Parameter].[Description] [ParameterDescription] ,[Archive].[Time] ,[Archive].[Value] ,[Archive].[MeasurementDeviceId] ,[Dimension].[Prefix] [DimensionPrefix] ,[MeasurementUnit].[Description] [MeasurementUnitDescription] ,[PhysicalParameter].[Code] [PhysicalParameterCode] ,[ParameterMapDeviceParameter].[Id] [ParameterMapDeviceParameterId]
,[C].[AverageResourceConsumption]
,[E].[DiffValue] ,[E].[DiffPeriodBegin] ,[E].[DiffPeriodEnd]
,@channelId [ChannelId] ,@periodBegin [PeriodBegin] ,@periodEnd [PeriodEnd]
FROM [Archive]
INNER JOIN [ParameterMapDeviceParameter] ON [ParameterMapDeviceParameter].[DeviceParameterId] = [Archive].[DeviceParameterId]
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [MeasurementUnit].[Id] = [ParameterMapDeviceParameter].[MeasurementUnitId]
INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [Dimension].[Id] = [ParameterMapDeviceParameter].[DimensionId]
INNER JOIN [Dictionaries].[PhysicalParameter] [PhysicalParameter] ON [PhysicalParameter].[Id] = [Parameter].PhysicalParameterId
LEFT JOIN (SELECT [D].[Num] ,[D].[ParameterId] ,[D].[DiffValue] ,[D].[DiffPeriodBegin] ,[D].[DiffPeriodEnd] FROM (SELECT ROW_NUMBER() OVER (PARTITION BY [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] DESC) [Num] ,[ParameterMapDeviceParameter].[ParameterId] [ParameterId]
,LAST_VALUE([Archive1].[Value]) OVER (PARTITION BY [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) - FIRST_VALUE([Archive1].[Value]) OVER (PARTITION BY [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) [DiffValue]
,LAST_VALUE([Archive1].[Time]) OVER (PARTITION BY [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) [DiffPeriodEnd] ,FIRST_VALUE([Archive1].[Time]) OVER (PARTITION BY [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) [DiffPeriodBegin]
FROM [Business].[Channel] [Channel] WITH (NOLOCK)
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK) ON [ParameterMapDeviceParameter].[ChannelTemplateId] = [Channel].[ChannelTemplateId] AND [ParameterMapDeviceParameter].[PeriodTypeId] = 5
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH (NOLOCK) ON [DeviceParameter].[Id] = [ParameterMapDeviceParameter].[DeviceParameterId]
LEFT JOIN [Archive1] WITH (NOLOCK) ON [Archive1].[MeasurementDeviceId] = [Channel].[MeasurementDeviceId] AND [Archive1].[DeviceParameterId] = [DeviceParameter].[Id]
WHERE @channelId = [Channel].[Id]) [D] WHERE [Num] = 1) [E] ON [E].[ParameterId] = [ParameterMapDeviceParameter].[ParameterId]
LEFT JOIN (SELECT [B].[ParameterId] ,[B].[AverageResourceConsumption] FROM (SELECT [A].[ParameterId] ,AVG([A].[Value]) OVER (PARTITION BY [A].[ParameterId]) [AverageResourceConsumption] ,ROW_NUMBER() OVER (PARTITION BY [A].[ParameterId] ORDER BY [A].[ParameterId]) [Num]
FROM (SELECT ROW_NUMBER() OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] DESC) [Num] ,[ParameterMapDeviceParameter].[ParameterId] [ParameterId]
,LAST_VALUE([Archive1].[Value]) OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) - FIRST_VALUE([Archive1].[Value]) OVER (PARTITION BY [Channel].[Id], [ParameterMapDeviceParameter].[Id] ORDER BY [Archive1].[Time] ASC) [Value]
FROM [Business].[Channel] [Channel] WITH (NOLOCK)
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[BuildingId] = @buildingId AND [Placement].[HasIndividualAccounting] = 1 AND [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK) ON [ParameterMapDeviceParameter].[ChannelTemplateId] = [Channel].[ChannelTemplateId] AND [ParameterMapDeviceParameter].[PeriodTypeId] = 5
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH (NOLOCK) ON [DeviceParameter].[Id] = [ParameterMapDeviceParameter].[DeviceParameterId]
LEFT JOIN [Archive1] WITH (NOLOCK) ON [Archive1].[MeasurementDeviceId] = [Channel].[MeasurementDeviceId] AND [Archive1].[DeviceParameterId] = [DeviceParameter].[Id]) [A]
WHERE [A].[Num] = 1) [B] WHERE [B].[Num] = 1) [C] ON [C].[ParameterId] = [ParameterMapDeviceParameter].[ParameterId] ;
COMMIT TRANSACTION;
DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'ParameterDescription', '', 1, 1) , ('MainData', 'Time', '', 0, 2) , ('MainData', 'Value', '', 1, 3) , ('MainData', 'DiffValue', ' ', 1, 4) , ('MainData', 'AverageResourceConsumption', ' ', 1, 5) , ('MainData', 'DimensionPrefix', '', 0, 6) , ('MainData', 'MeasurementUnitDescription', ' ', 0, 7)
END TRY BEGIN CATCH IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END; IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
RETURN (3); END CATCH;
RETURN (0);
END;

