IF @lastTimeSignatureId IS NULL BEGIN SET @lastTimeSignatureId = ( SELECT [TimeSignature].[Id] FROM ( SELECT [TimeSignature].[Id], ROW_NUMBER() OVER(ORDER BY [TimeSignature].[Time] DESC) [Num]
FROM [Business].[TimeSignature] [TimeSignature]
WHERE [TimeSignature].[MeasurementDeviceId] = @measurementDeviceId
