INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [Dimension].[Id] = [ParameterMapDeviceParameter].[DimensionId]
WHERE [ParameterMapDeviceParameter].[PeriodTypeId] = @periodTypeId AND [ParameterMapDeviceParameter].[ChannelTemplateId] = @channelTemplateId )
