WHERE [Archive].[PeriodTypeId] = @periodTypeId AND [Archive].[TimeSignatureId] = @lastTimeSignatureId AND [Archive].[MeasurementDeviceId] = @measurementDeviceId
ORDER BY [ParameterMapDeviceParameter].[Order];
/* 3. */
DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
