('MainData', 'PhysicalParameterCode', ' . ', 0, 20) ,('MainData', 'PhysicalParameterId', '. . ', 0, 20) ,('MainData', 'PhysicalParameterOrder', ' . ', 0, 20) ,('MainData', 'ParameterDescription', '', 1, 40) ,('MainData', 'DimensionPrefix', '', 0, 50) ,('MainData', 'MeasurementUnitDescription', '. ', 0, 60) ,('MainData', 'PhysicalParameterCode', ' . ', 0, 70) ,('MainData', 'PhysicalParameterDescription', '. ', 0, 80) ,('MainData', 'IsValid', '. ', 0, 85) ,('MainData', 'Value', '', 1, 90);
SELECT [Id] , [Code] , [Description] FROM [Dictionaries].[PeriodType] [PeriodType] WHERE [PeriodType].[Id] IN (1, 5);
SELECT [TimeSignature].[Id] ,[TimeSignature].[MeasurementDeviceId] ,[TimeSignature].[Time] ,[TimeSignature].[DeviceTime] ,[TimeSignature].[PollingDuration] ,[TimeSignature].[StatusConnectionId] FROM [Business].[TimeSignature] [TimeSignature] WHERE [TimeSignature].[Id] = @lastTimeSignatureId
COMMIT TRANSACTION; END TRY BEGIN CATCH IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END; IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
