BEGIN SET NOCOUNT ON; SET XACT_ABORT ON;
BEGIN TRY SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; BEGIN TRANSACTION;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'MainData');
/*2. */
WITH [MeasurementDevices] AS ( SELECT [MeasurementDevice].[Id] [MeasurementDeviceId], [MeasurementDevice].[LastTimeSignatureId], [Channel].[Id] [ChannelId], [Channel].[ChannelTemplateId], [MeasurementDevice].[DeviceId] [DeviceId]
