WHERE [Placement].[Id] = @placementId OR [Channel].[Id] = @channelId OR [MeasurementDevice].[Id] = @measurementDeviceId ),
/* */ [ParameterMapDeviceParameter] AS ( SELECT [ParameterMapDeviceParameter].[Id], [ParameterMapDeviceParameter].[DeviceParameterId], [ParameterMapDeviceParameter].[ParameterId], [ParameterMapDeviceParameter].[ParameterDictionaryId],
[MeasurementDevices].[MeasurementDeviceId], [MeasurementDevices].[LastTimeSignatureId], [MeasurementDevices].[ChannelId], [MeasurementDevices].[ChannelTemplateId], [MeasurementDevices].[DeviceId],
[Parameter].[Description] [ParameterDescription], [Parameter].[Code] [ParameterCode],
