WHERE [ParameterMapDeviceParameter].[PeriodTypeId] IN (1, 5) )
/* */ SELECT [ParameterMapDeviceParameter].[Id],
[ParameterMapDeviceParameter].[ParameterCode], [ParameterMapDeviceParameter].[ParameterDescription], [ParameterMapDeviceParameter].[DimensionPrefix], [ParameterMapDeviceParameter].[MeasurementUnitDescription], [Archive].[MeasurementDeviceId], [Archive].[Time], [dbo].[ConvertMeasurementUnit] ( [DimensionValue], [DimensionValue2], [Archive].[Value], [Expression] ) [Value]
,[ParameterMapDeviceParameter].[ChannelId] [ChannelId] ,[ParameterMapDeviceParameter].[ChannelTemplateId] [ChannelTemplateId] ,[ParameterMapDeviceParameter].[DeviceId] [DeviceId]
