CREATE PROCEDURE Programmability.GetMnemoschemeMeasurementDeviceSummaryInfo
  @placementId INT,  
  @measurementDeviceId INT,
  @channelId INT  
AS

BEGIN 

  DECLARE @linkScheme NVARCHAR(128) = [Programmability].[GetSchemaTableByName](@linkName); 

  SET @linkName = REPLACE(@linkName, 'Link', N'|');

  DECLARE @leftName NVARCHAR(128) = LEFT(@linkName, CHARINDEX(N'|', @linkName) - 1);
  DECLARE @rightName NVARCHAR(128) = RIGHT(@linkName, LEN(@linkName) - CHARINDEX(N'|', @linkName));

  DECLARE @leftScheme NVARCHAR(128) = [Programmability].[GetSchemaTableByName](@leftName);
  DECLARE @rightScheme NVARCHAR(128) = [Programmability].[GetSchemaTableByName](@rightName);

  DECLARE @leftNameFull NVARCHAR(128) = CONCAT('[', @leftScheme, '].', '[', @leftName, ']');
  DECLARE @rightNameFull NVARCHAR(128) = CONCAT('[', @rightScheme, '].', '[', @rightName, ']');

  DECLARE @query NVARCHAR(MAX) = '';
  DECLARE @temp NVARCHAR(MAX);

  DECLARE @link NVARCHAR(MAX) = CONCAT(@leftName, 'Link', @rightName);
  SET @linkName = CONCAT('[', @linkScheme, '].', '[', @link, ']');

  DECLARE @queryBase NVARCHAR(MAX) = 

  'SELECT  
    ''{LEFT} -> {RIGHT}'' [Direction],
    [R].[C] [CountId],      
    CONCAT(''{LEFT}Id: '', [R].[LeftId]) [Ld],
    CONCAT(''{RIGHT}Id: '', [R].[RightId]) [Rd],
    [R].[LeftDescription] [LeftDescription],       
    [R].[RightDescription] [RightDescription]
  FROM
    (  
      SELECT         
          COUNT(*) OVER(PARTITION BY [Left].[Id] ORDER BY [Left].[Id]) [C],
          [Left].[Id] [LeftId],
          [Right].[Id] [RightId],
          [Left].[Description] [LeftDescription],
          [Right].[Description] [RightDescription]
      FROM {LINK} [Link] WITH (NOLOCK)   
        INNER JOIN {LEFT_FULL} [Left] WITH (NOLOCK) ON [Link].[{LEFT}Id] = [Left].[Id]
        INNER JOIN {RIGHT_FULL} [Right] WITH (NOLOCK) ON [Link].[{RIGHT}Id] = [Right].[Id]
    ) [R]  WHERE [R].[C] > 1';

  SET @queryBase = REPLACE(@queryBase, '{LINK}', @linkName);

  IF(@direction = 'Both')
  BEGIN
    SET @temp = REPLACE(@queryBase, '{LEFT}', @leftName);
    SET @temp = REPLACE(@temp, '{RIGHT}', @rightName);
    SET @temp = REPLACE(@temp, '{LEFT_FULL}', @leftNameFull);
    SET @temp = REPLACE(@temp, '{RIGHT_FULL}', @rightNameFull);

    SET @query = CONCAT(@query, @temp);
    SET @query = CONCAT(@query, ' UNION ALL ');

    SET @temp = REPLACE(@queryBase, '{RIGHT}', @leftName);
    SET @temp = REPLACE(@temp, '{LEFT}', @rightName);
    SET @temp = REPLACE(@temp, '{LEFT_FULL}', @rightNameFull);
    SET @temp = REPLACE(@temp, '{RIGHT_FULL}', @leftNameFull);      
  END
  ELSE 
  BEGIN
    IF(@direction = 'Right')
    BEGIN
    	  DECLARE @swap NVARCHAR(128);

        SET @swap = @leftName;
        SET @leftName = @rightName;
        SET @rightName = @swap;

        SET @swap = @leftNameFull;
        SET @leftNameFull = @rightNameFull;
        SET @rightNameFull = @swap;
    END;

	  SET @temp = REPLACE(@queryBase, '{LEFT}', @leftName);
    SET @temp = REPLACE(@temp, '{RIGHT}', @rightName);
    SET @temp = REPLACE(@temp, '{LEFT_FULL}', @leftNameFull);
    SET @temp = REPLACE(@temp, '{RIGHT_FULL}', @rightNameFull);     
  END;

  /* 1.          */
   DECLARE @resultSetName [Programmability].[ResultSetNameTableType];

   INSERT INTO @resultSetName VALUES
	   (1, 'MainData'),		    --       
	   (2, 'ColumnCaption');	--        

   SELECT [ResultSetName].[Order], [ResultSetName].[Name]
	FROM @resultSetName [ResultSetName]

  /*  */
  SET @query = CONCAT(@query, @temp);
  EXEC sp_executesql @query;

  /*      */
	DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];

	INSERT INTO @columnCaption 
  OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order]
  VALUES
     ('MainData', 'Direction', '', 1, 1)
    ,('MainData', 'CountId', '', 1, 2)
    ,('MainData', 'Ld', '', 1, 3)     
	  ,('MainData', 'Rd', '', 1, 4)    
    ,('MainData', 'LeftDescription', ' ', 1, 5)
	  ,('MainData', 'RightDescription', ' ', 1, 6)	  		
	/**/
END;

BEGIN SET NOCOUNT ON; SET @isValid = 1;
DECLARE @heatCurveJson NVARCHAR(MAX) = '';
SELECT @heatCurveJson = @heatCurveJson + CONCAT(' "object', [DynamicParameter].[Id], '": ', [DynamicParameterValue].[Value], ', ')
FROM [Dictionaries].[DynamicParameter] [DynamicParameter] WITH(NOLOCK)
INNER JOIN [Core].[DynamicParameterValue] [DynamicParameterValue] WITH(NOLOCK) ON [DynamicParameterValue].[DynamicParameterId] = [DynamicParameter].[Id] WHERE [DynamicParameter].[Id] IN (61, 62) -- Organization.HeatCurveSupplyPipe, Organization.HeatCurveReturnPipe AND [DynamicParameter].[EntityTypeName] = 'Organization' AND [DynamicParameterValue].[EntityId] IN ( SELECT [Organization].[Id] [OrganizationId]
FROM [Business].[Building] [Building] WITH(NOLOCK) INNER JOIN [Business].[Placement] [Placement] WITH(NOLOCK) ON [Building].[Id] = [Placement].[BuildingId] INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Placement].[Id] = [Channel].[PlacementId] INNER JOIN [Business].[Organization] [Organization] WITH(NOLOCK) ON [Channel].[OrganizationId] = [Organization].[Id] WHERE [Building].[Id] = @buildingId AND [Channel].[ResourceSystemTypeId] = 4 -- HeatSys AND [Organization].[OrganizationTypeId] = 3 -- Rsc )
IF (@heatCurveJson IS NOT NULL AND @heatCurveJson != '') BEGIN SET @heatCurveJson = '{' + LEFT(@heatCurveJson, LEN(@heatCurveJson) - 1) + '}'; IF (ISJSON(@heatCurveJson) != 1) BEGIN -- Json ia not valid! SET @isValid = 0; END END ELSE BEGIN -- There are no associated Organization.HeatCurveSupplyPipe or Organization.HeatCurveReturnPipe dynamic parameters SET @isValid = 0; END;
IF(@isValid = 1 AND @onlyDiagnostic = 0) BEGIN SELECT [HeatCurveSupplyPipe].[Temper.OutdoorAir] ,[HeatCurveSupplyPipe].[Organization.HeatCurveSupplyPipe] ,[HeatCurveReturnPipe].[Organization.HeatCurveReturnPipe] FROM ( SELECT [Organization.HeatCurveSupplyPipe], CAST(REPLACE([Temperature], '', '') AS DECIMAL(9,4)) [Temper.OutdoorAir] FROM OPENJSON(@heatCurveJson, '$.object61.userInputValues') WITH ( [Organization.HeatCurveSupplyPipe] DECIMAL(9,4) '$.value', [Temperature] NVARCHAR(32) '$.caption' ) ) [HeatCurveSupplyPipe] INNER JOIN ( SELECT [Organization.HeatCurveReturnPipe], CAST(REPLACE([Temperature], '', '') AS DECIMAL(9,4)) [Temper.OutdoorAir] FROM OPENJSON(@heatCurveJson, '$.object62.userInputValues') WITH ( [Organization.HeatCurveReturnPipe] DECIMAL(9,4) '$.value', [Temperature] NVARCHAR(32) '$.caption' ) ) [HeatCurveReturnPipe] ON [HeatCurveSupplyPipe].[Temper.OutdoorAir] = [HeatCurveReturnPipe].[Temper.OutdoorAir] END; END;

CREATE PROCEDURE [Programmability].[GetOrganizationHeatCurves]
	@buildingId INT,
	@onlyDiagnostic BIT,
	@isValid BIT OUTPUT
AS

BEGIN SET DATEFORMAT dmy; SET LANGUAGE N''; SET NOCOUNT ON;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'MainData'), -- (2, 'ColumnCaption'); --
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */ DECLARE @unitCodesTable TABLE ( [PhysicalParameterId] INT NOT NULL, [Description] NVARCHAR(128) NULL, [UnitCodes] NVARCHAR(128) NULL );
INSERT INTO @unitCodesTable SELECT [PhysicalParameter].[Id] [PhysicalParameterId], [PhysicalParameter].[Description] [Description], LEFT([MeasurementUnit].[Codes], LEN([MeasurementUnit].[Codes]) - 1) [UnitCodes]
FROM [Dictionaries].[PhysicalParameter] [PhysicalParameter] WITH(NOLOCK) INNER JOIN (SELECT DISTINCT([Mu1].[PhysicalParameterId])[PhysicalParameterId], (SELECT CONCAT('[', [Mu2].[Description], '], ') AS [text()] FROM [Dictionaries].[MeasurementUnit] [Mu2] WITH(NOLOCK) WHERE [Mu1].[PhysicalParameterId] = [Mu2].[PhysicalParameterId] ORDER BY [Mu2].[PhysicalParameterId] FOR XML PATH ('')) [Codes]
FROM [Dictionaries].[MeasurementUnit] [Mu1] WITH(NOLOCK)) [MeasurementUnit] ON [PhysicalParameter].[Id] = [MeasurementUnit].[PhysicalParameterId]; /**/
/* 3. */ SET @deviceId = IIF(@deviceId = 0 /*ALL*/, NULL, @deviceId);
SELECT ROW_NUMBER() OVER(ORDER BY [Device].[Id]) [RowNumber], [Device].[Code] [DeviceCode], [Parameter].[Code] [ParameterCode], [PeriodType].[Code] [PeriodTypeCode], [PhysicalParameter].[Description] [PhysicalParameterDescription], [Parameter].[Description] [ParameterDescription], [Unit].[UnitCodes] [UnitCodes] FROM [Business].[ParameterMapDeviceParameter] [Map] WITH(NOLOCK)
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH (NOLOCK) ON [DeviceParameter].[Id] = [Map].[DeviceParameterId]
INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK) ON [Device].[Id] = [DeviceParameter].[DeviceId]
INNER JOIN [Dictionaries].[Parameter] [Parameter] WITH(NOLOCK) ON [Map].[ParameterId] =[Parameter].[Id]
INNER JOIN [Dictionaries].[PeriodType] [PeriodType] WITH(NOLOCK) ON [Map].[PeriodTypeId] = [PeriodType].[Id]
INNER JOIN [Dictionaries].[PhysicalParameter] [PhysicalParameter] WITH(NOLOCK) ON [PhysicalParameter].[Id] = [Parameter].[PhysicalParameterId]
INNER JOIN @unitCodesTable [Unit] ON [Unit].[PhysicalParameterId] = [PhysicalParameter].[Id]
WHERE ([Device].[Id] = @deviceId OR @deviceId IS NULL) /**/
/* 4. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'RowNumber', '', 1, 1) ,('MainData', 'DeviceCode', ' ', 1, 2) ,('MainData', 'ParameterCode', ' ', 1, 3) ,('MainData', 'PeriodTypeCode', ' ', 1, 4) ,('MainData', 'ParameterDescription', ' ', 1, 5) ,('MainData', 'PhysicalParameterDescription', ' ', 1, 6) ,('MainData', 'UnitCodes', ' ', 1, 7)
/**/
END;

BEGIN
	SET @physicalParameterId = IIF(@physicalParameterId = 0, NULL, @physicalParameterId);

   /*1.     */
   DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
   INSERT INTO @resultSetName VALUES 
      (1, 'MainData');

   SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName];
   /**/

   /* 2.   */
   SELECT 
      [Unit].[Id]
      ,[Unit].[Code]
		,[Unit].[Description]
		,[Parameter].[Description] [PhysicalParameter]
      ,[Parameter].[Id] [PhysicalParameterId]

   FROM [Dictionaries].[MeasurementUnit] [Unit]

   INNER JOIN Dictionaries.PhysicalParameter Parameter	
      ON [Unit].[PhysicalParameterId] = [Parameter].[Id] 

   WHERE [Parameter].[Id] = @physicalParameterId OR @physicalParameterId IS NULL;
   /**/
END;

CREATE PROCEDURE [Programmability].[GetPhysicalParameter]
	@physicalParameterId INT NULL
AS

BEGIN SET NOCOUNT ON;
SET @districtId = IIF(@districtId = 0, NULL, @districtId); SET @deviceId = IIF(@deviceId = 0, NULL, @deviceId);
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName];
/* 2. */ SELECT [Range].[Range] [Arg], COUNT(*) [Value], ROUND(COUNT(*) * 100.0 / CAST(SUM(COUNT(*)) OVER() AS DECIMAL(8, 1)), 2) [Percentage] FROM ( SELECT CASE WHEN [SuccessConnectionPercent] BETWEEN 1 AND 10 THEN '1-10%' WHEN [SuccessConnectionPercent] BETWEEN 11 AND 20 THEN '11-20%' WHEN [SuccessConnectionPercent] BETWEEN 21 AND 30 THEN '21-30%' WHEN [SuccessConnectionPercent] BETWEEN 31 AND 40 THEN '31-40%' WHEN [SuccessConnectionPercent] BETWEEN 41 AND 50 THEN '41-50%' WHEN [SuccessConnectionPercent] BETWEEN 51 AND 60 THEN '51-60%' WHEN [SuccessConnectionPercent] BETWEEN 61 AND 70 THEN '61-70%' WHEN [SuccessConnectionPercent] BETWEEN 71 AND 80 THEN '71-80%' WHEN [SuccessConnectionPercent] BETWEEN 81 AND 90 THEN '81-90%' WHEN [SuccessConnectionPercent] BETWEEN 91 AND 100 THEN '91-100%' ELSE ' < 1 %' END AS [Range] FROM ( SELECT [Md].[SuccessConnectionPercent] FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY [Md].[Id] ORDER BY [Md].[Id]) [Num], [Md].[SuccessConnectionPercent]
FROM [Business].[MeasurementDevice] [Md] WITH(NOLOCK)
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id]
INNER JOIN [Programmability].[GetUserLinkChannel] (@userName) [UserLink] ON [UserLink].[ChannelId] = [Channel].[Id] AND ((ISNULL([UserLink].[UserId], 0) = @userId) OR (@roleCode = N'ADMIN'))
LEFT OUTER JOIN [Business].[AccessPoint] [AccessPoint] WITH(NOLOCK) ON [AccessPoint].[Id] = [Md].[AccessPointId]
LEFT OUTER JOIN [Business].[AccessPointLinkBuilding] [LinkBuilding] WITH(NOLOCK) ON [AccessPoint].[Id] = [LinkBuilding].[AccessPointId]
LEFT OUTER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [LinkBuilding].[BuildingId]
WHERE ([Md].[DeviceId] = @deviceId OR @deviceId IS NULL) AND ([Building].[DistrictId] = @districtId OR @districtId IS NULL)
) [Md] WHERE [Md].[Num] = 1
) [Md] ) [Range] GROUP BY [Range].[Range]
END;

CREATE PROCEDURE [Programmability].[GetPivotDiagrammByConnectionQuality]
  @userName NVARCHAR(32),
  @deviceId INT,
  @districtId INT  
AS

BEGIN SET NOCOUNT ON;
SET @districtId = IIF(@districtId = 0, NULL, @districtId);
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName];
/* 2. */ SELECT [Device].[Code] [Arg], [MeasurementDeviceGroupByDevice].[Value] [Value], [MeasurementDeviceGroupByDevice].[Percentage] [Percentage] FROM ( SELECT [Md].[DeviceId], COUNT(*) [Value], ROUND(COUNT(*) * 100.0 / CAST(SUM(COUNT(*)) OVER() AS DECIMAL(8, 1)), 2) [Percentage] FROM ( SELECT [Md].[DeviceId] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [Md].[Id] ORDER BY [Md].[Id]) [Num], [Md].[DeviceId]
FROM [Business].[MeasurementDevice] [Md] WITH(NOLOCK)
LEFT OUTER JOIN [Business].[AccessPoint] WITH(NOLOCK) ON [AccessPoint].[Id] = [Md].[AccessPointId]
LEFT OUTER JOIN [Business].[AccessPointLinkBuilding] [LinkBuilding] WITH(NOLOCK) ON [AccessPoint].[Id] = [LinkBuilding].[AccessPointId]
LEFT OUTER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [LinkBuilding].[BuildingId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id]
INNER JOIN [Programmability].[GetUserLinkChannel] (@userName) [UserLink] ON [UserLink].[ChannelId] = [Channel].[Id] AND ((ISNULL([UserLink].[UserId], 0) = @userId) OR (@roleCode = N'ADMIN'))
WHERE ([Building].[DistrictId] = @districtId OR @districtId IS NULL) ) [Md] WHERE [Md].[Num] = 1 ) [Md]
GROUP BY [Md].[DeviceId]
) [MeasurementDeviceGroupByDevice]
INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK) ON [MeasurementDeviceGroupByDevice].[DeviceId] = [Device].[Id]
ORDER BY [Device].[Code] END;

CREATE PROCEDURE [Programmability].[GetPivotDiagrammByDevice]
  @userName NVARCHAR(32),
  @districtId INT  
AS

BEGIN SET NOCOUNT ON;
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
SET @deviceId = IIF(@deviceId = 0, NULL, @deviceId);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName];
/* 2. */ SELECT [District].[Description] [Arg], [MeasurementDeviceGroupByDistrict].[Value], [MeasurementDeviceGroupByDistrict].[Percentage] FROM ( SELECT [Building].[DistrictId], COUNT(*) [Value], ROUND(COUNT(*) * 100.0 / CAST(SUM(COUNT(*)) OVER() AS DECIMAL(8, 1)), 2) [Percentage] FROM ( SELECT [Building].[Num], [Building].[DistrictId] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [Md].[Id] ORDER BY [Md].[Id]) [Num], [Building].[DistrictId]
FROM [Business].[MeasurementDevice] [Md] WITH(NOLOCK)
LEFT OUTER JOIN [Business].[AccessPoint] [Ap] WITH(NOLOCK) ON [Md].[AccessPointId] = [Ap].[Id]
LEFT OUTER JOIN [Business].[AccessPointLinkBuilding] [BuildingLink] WITH(NOLOCK) ON [Ap].[Id] = [BuildingLink].[AccessPointId]
LEFT OUTER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [BuildingLink].[BuildingId] = [Building].[Id]
LEFT OUTER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [Building].[DistrictId] = [District].[Id]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id]
INNER JOIN [Programmability].[GetUserLinkChannel] (@userName) [UserLink] ON [UserLink].[ChannelId] = [Channel].[Id] AND ((ISNULL([UserLink].[UserId], 0) = @userId) OR (@roleCode = N'ADMIN'))
WHERE ([Md].[DeviceId] = @deviceId OR @deviceId IS NULL) ) [Building] WHERE [Building].[Num] = 1 ) [Building]
GROUP BY [Building].[DistrictId]
) [MeasurementDeviceGroupByDistrict]
INNER JOIN [Dictionaries].[District] [District] WITH(NOLOCK) ON [MeasurementDeviceGroupByDistrict].[DistrictId] = [District].[Id]
ORDER BY [District].[Description]
END;

