CREATE PROCEDURE [Programmability].[GetPreliminaryElectroWorkingDayGraphDataSource]
@userName NVARCHAR(32),
@channelId INT,
@withDesignFactor BIT

AS

BEGIN SET NOCOUNT ON; SET XACT_ABORT ON;
/* */ /* "" */
DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'MainData'), (2, 'ColumnCaption');
BEGIN TRY SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; BEGIN TRANSACTION;
WITH /* */ [Channel] AS ( SELECT
/* */ [Placement].[Id] [PlacementId], [Placement].[Number] [PlacementNumber], [PlacementPurpose].[Description] [PlacementPurposeDescription],
COALESCE([UserAdditionalInfo].[Description], [Organization].[FullAddress]) [PlacementHolderDescription], /**/
[Channel].[Id] [ChannelId], [Channel].[MeasurementDeviceId] [MeasurementDeviceId], [Channel].[ChannelTemplateId] [ChannelTemplateId] FROM [Business].[Channel] [Channel]
