INNER JOIN [Dictionaries].[PlacementPurpose] [PlacementPurpose] ON [PlacementPurpose].[Id] = [Placement].[PlacementPurposeId]
/**/ LEFT JOIN [Business].[UserAdditionalInfo] [UserAdditionalInfo] ON [Placement].[UserAdditionalInfoId] = [UserAdditionalInfo].[Id]
LEFT JOIN [Business].[Organization] [Organization] ON [Placement].[OrganizationId] = [Organization].[Id]
WHERE ([Channel].[Id] = @entityId OR @entityTypeName = 'Building') AND (([Building].[Id] = @entityId AND [Placement].[HasIndividualAccounting] = 1) OR @entityTypeName = 'Channel') AND
[Channel].[ResourceSystemTypeId] = 6 ), /* */ [ParameterMapDeviceParameter] AS ( SELECT
[Channel].[ChannelId] [ChannelId], [Channel].[MeasurementDeviceId] [MeasurementDeviceId],
/* */ [PlacementId], [PlacementHolderDescription], [PlacementNumber], [PlacementPurposeDescription], /**/
/* */ [ParameterMapDeviceParameter].[Id] [Id], [ParameterMapDeviceParameter].[ParameterId] [ParameterId],
[RegulatorParameterValue].[Id] [RegulatorParameterValueId], [Parameter].[Description] [ParameterDescription], [ParameterMapDeviceParameter].[Order],
/* */ [Dimension].[Prefix] [DimensionPrefix], [MeasurementUnit].[Description] [MeasurementUnitDescription], [PhysicalParameter].[Code] [PhysicalParameterCode], [PhysicalParameter].[Id] [PhysicalParameterId],
/* */ [RegulatorParameterValue].[DeviceValue] [DeviceValue], [RegulatorParameterValue].[UserValue] [UserValue],
[DeviceParameterSetting].[ValueTypeCode], [DeviceParameterSetting].[Min], [DeviceParameterSetting].[Max],
[ParameterDictionary].[Id] [ParameterDictionaryId], [ParameterDictionary].[Code] [ParameterDictionaryCode],
[RegulatorParameterValue].[SyncDeviceTime] [SyncDeviceTime], [RegulatorParameterValue].[UpdateUserValueTime] [UpdateUserValueTime],
/* */ [SubsystemType].[Id] [SubsystemTypeId], [SubsystemType].[Description] [SubsystemTypeDescription],
[RegulatorParameterValue].[UpdatedBy] [UpdatedBy]
FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter]
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] ON [DeviceParameter].[Id] = [ParameterMapDeviceParameter].[DeviceParameterId]
LEFT JOIN [Business].[DeviceParameterSetting] [DeviceParameterSetting] ON [DeviceParameterSetting].[Id] = [DeviceParameter].[Id]
LEFT JOIN [Business].[RegulatorParameterValue] [RegulatorParameterValue] ON [ParameterMapDeviceParameter].[DeviceParameterId] = [DeviceParameter].[Id] AND [ParameterMapDeviceParameter].[DeviceParameterId] = [RegulatorParameterValue].[DeviceParameterId]
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId]
INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [Dimension].[Id] = [ParameterMapDeviceParameter].[DimensionId]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [MeasurementUnit].[Id] = [ParameterMapDeviceParameter].[MeasurementUnitId]
INNER JOIN [Dictionaries].[SubsystemType] [SubsystemType] ON [SubsystemType].[Id] = [ParameterMapDeviceParameter].[SubsystemTypeId]
LEFT JOIN [Dictionaries].[PhysicalParameter] [PhysicalParameter] ON [PhysicalParameter].[Id] = [Parameter].[PhysicalParameterId] AND [PhysicalParameter].[Id] = [MeasurementUnit].[PhysicalParameterId]
INNER JOIN [Dictionaries].[ParameterDictionary] [ParameterDictionary] ON [ParameterDictionary].[Id] = [ParameterMapDeviceParameter].[ParameterDictionaryId]
INNER JOIN [Channel] ON [Channel].[ChannelTemplateId] = [ParameterMapDeviceParameter].[ChannelTemplateId] AND [Channel].[MeasurementDeviceId] = [RegulatorParameterValue].[MeasurementDeviceId]
WHERE [ParameterMapDeviceParameter].[PeriodTypeId] = 7 /* */ )
SELECT [ChannelId], [MeasurementDeviceId],
/* */ [PlacementId], [PlacementHolderDescription], [PlacementNumber], [PlacementPurposeDescription], /**/
[Id], [ParameterId],
[RegulatorParameterValueId], [ParameterDescription], [Order],
[PhysicalParameterCode], [DimensionPrefix], [MeasurementUnitDescription], [ParameterDictionaryCode], [ParameterDictionaryId], [PhysicalParameterId],
[Min], [Max], [ValueTypeCode],
[DeviceValue], [UserValue],
[SyncDeviceTime], [UpdateUserValueTime],
[SubsystemTypeId], [SubsystemTypeDescription],
[UpdatedBy] FROM [ParameterMapDeviceParameter] ORDER BY [Order];
COMMIT TRANSACTION;
DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];
/* */ INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order]
VALUES
('MainData', 'Id', '', 0, 0) ,('MainData', 'PlacementNumber', '', 0, 0) ,('MainData', 'PlacementPurposeDescription', ' ', 0, 0) ,('MainData', 'PlacementHolderDescription', '', 0, 1)
,('MainData', 'ParameterDescription', '', 1, 1)
,('MainData', 'Order', '', 0, 1)
,('MainData', 'DimensionPrefix', '', 0, 1) ,('MainData', 'MeasurementUnitDescription', '. ', 0, 1) ,('MainData', 'PhysicalParameterCode', ' ', 0, 1) ,('MainData', 'ParameterDictionaryCode', ' ', 0, 1)
,('MainData', 'DeviceValue', ' ', 1, 1) ,('MainData', 'UserValue', ' ', 1, 1)
,('MainData', 'SyncDeviceTime', ' ', 1, 1) ,('MainData', 'UpdateUserValueTime', ' ', 1, 1)
,('MainData', 'SubsystemTypeId', '. ', 0, 1) ,('MainData', 'SubsystemTypeDescription', '', 0, 1)
,('MainData', 'UpdatedBy', ' ', 1, 20)
COMMIT TRANSACTION; END TRY BEGIN CATCH IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END; IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
RETURN (3); END CATCH;
END;

BEGIN SET NOCOUNT ON;
SET XACT_ABORT ON;
BEGIN TRY
/* "" */ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; BEGIN TRANSACTION;
/* */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'ReportParameter'), (2, 'MainData');
DECLARE @reportParameter [Programmability].[CustomParameterTableType];
SELECT 'UserName' [Name], CONVERT(NVARCHAR(128), @userName) [Value]
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
DECLARE @periodEnd DATETIME = DATEADD(HOUR, -@periodBeginOffset, @periodBegin);
WITH [NearestTimeSignature] AS ( SELECT [TimeSignature].[MeasurementDeviceId] ,[TimeSignature].[TimeSignatureId] ,[TimeSignature].[TimeSignatureTime] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [MeasurementDevice].[Id] ORDER BY [TimeSignature].[Time] DESC) [Num] ,[MeasurementDevice].[Id] [MeasurementDeviceId] ,[TimeSignature].[Id] [TimeSignatureId] ,[TimeSignature].[Time] [TimeSignatureTime]
FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK)
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [Channel].[MeasurementDeviceId]
INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH(NOLOCK) ON [Channel].[Id] = [UserLinkChannel].[ChannelId] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[TimeSignature] [TimeSignature] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [TimeSignature].[MeasurementDeviceId]
-- It's "non searchable args" already! AND ( (@includeAnyMeasurement = 0 AND [TimeSignature].[Time] <= @periodBegin AND [TimeSignature].[Time] >= @periodEnd) OR (@includeAnyMeasurement = 1 AND [TimeSignature].[Time] <= @periodBegin) )
WHERE [MeasurementDevice].[DeviceId] IN (8, 34) AND [Channel].[ResourceSystemTypeId] = 8 ) [TimeSignature]
WHERE [TimeSignature].[Num] = 1 )
SELECT [Building].[Id] [BuildingId], [Building].[Description] [BuildingDescription], [Device].[ShortName] [DeviceShortName],
[FinalMainData].[Time] [Time], [FinalMainData].[Monitoring.Temper.ReturnPipe], [FinalMainData].[Monitoring.Temper.HwsReturnPipe], [FinalMainData].[Monitoring.Temper.CalcSupplyPipe], [FinalMainData].[Monitoring.Temper.SupplyPipe], [FinalMainData].[Monitoring.Temper.CalcHwsReturnPipe], [FinalMainData].[Monitoring.Temper.CalcReturnPipe], [FinalMainData].[Monitoring.Temper.HwsSupplyPipe], [FinalMainData].[Monitoring.Temper.OutdoorAir], [FinalMainData].[Monitoring.Temper.CalcHwsSupplyPipe]
,ABS(([FinalMainData].[Monitoring.Temper.CalcReturnPipe] - [FinalMainData].[Monitoring.Temper.ReturnPipe])) [Monitoring.Temper.ReturnPipe.Err] ,ABS(([FinalMainData].[Monitoring.Temper.CalcSupplyPipe] - [FinalMainData].[Monitoring.Temper.SupplyPipe])) [Monitoring.Temper.SupplyPipe.Err] ,ABS(([FinalMainData].[Monitoring.Temper.CalcHwsSupplyPipe] - [FinalMainData].[Monitoring.Temper.HwsSupplyPipe])) [Monitoring.Temper.HwsSupplyPipe.Err] ,ABS(([FinalMainData].[Monitoring.Temper.CalcHwsReturnPipe] - [FinalMainData].[Monitoring.Temper.HwsReturnPipe])) [Monitoring.Temper.HwsReturnPipe.Err] FROM ( SELECT [MeasurementDeviceId], [PlacementId], [Time], [Monitoring.Temper.ReturnPipe], [Monitoring.Temper.HwsReturnPipe], [Monitoring.Temper.CalcSupplyPipe], [Monitoring.Temper.SupplyPipe], [Monitoring.Temper.CalcHwsReturnPipe], [Monitoring.Temper.CalcReturnPipe], [Monitoring.Temper.HwsSupplyPipe], [Monitoring.Temper.OutdoorAir], [Monitoring.Temper.CalcHwsSupplyPipe] FROM ( SELECT [Archive].[MeasurementDeviceId] [MeasurementDeviceId] ,[Channel].[PlacementId] [PlacementId] ,[NearestTimeSignature].[TimeSignatureTime] [Time] ,[Parameter].[Code] [ParameterCode] ,[Archive].[Value] [Value]
FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK)
INNER JOIN [NearestTimeSignature] ON [NearestTimeSignature].[MeasurementDeviceId] = [MeasurementDevice].[Id]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [Channel].[MeasurementDeviceId]
INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] WITH(NOLOCK) ON [Channel].[Id] = [UserLinkChannel].[ChannelId] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH(NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH(NOLOCK) ON [ChannelTemplate].[Id] = [ParameterMapDeviceParameter].[ChannelTemplateId]
INNER JOIN [Dictionaries].[Parameter] [Parameter] WITH(NOLOCK) ON [ParameterMapDeviceParameter].[ParameterId] = [Parameter].[Id]
INNER JOIN [Business].[Archive] [Archive] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [Archive].[MeasurementDeviceId]
AND [Archive].[TimeSignatureId] = [NearestTimeSignature].[TimeSignatureId]
AND [Archive].[PeriodTypeId] = 1
AND [ParameterMapDeviceParameter].[DeviceParameterId] = [Archive].[DeviceParameterId]
AND [Archive].[IsValid] = 1
WHERE [MeasurementDevice].[DeviceId] IN (8, 34) AND [Channel].[ResourceSystemTypeId] = 8
) [MainData]
PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( [Monitoring.Temper.ReturnPipe], [Monitoring.Temper.HwsReturnPipe], [Monitoring.Temper.CalcSupplyPipe], [Monitoring.Temper.SupplyPipe], [Monitoring.Temper.CalcHwsReturnPipe], [Monitoring.Temper.CalcReturnPipe], [Monitoring.Temper.HwsSupplyPipe], [Monitoring.Temper.OutdoorAir], [Monitoring.Temper.CalcHwsSupplyPipe] ) ) [PivotedMainData] ) [FinalMainData]
INNER JOIN [Business].[Placement] [Placement] WITH(NOLOCK) ON [Placement].[Id] = [FinalMainData].[PlacementId]
INNER JOIN [Business].[Building] [Building] WITH(NOLOCK) ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH(NOLOCK) ON [MeasurementDevice].[Id] = [FinalMainData].[MeasurementDeviceId]
INNER JOIN [Dictionaries].[Device] [Device] WITH(NOLOCK) ON [Device].[Id] = [MeasurementDevice].[DeviceId]
COMMIT TRANSACTION;
END TRY BEGIN CATCH
/* */ IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END;
-- !IMPORTANT /* , , */ IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ' , @userName, @lineFeed, '@periodBegin: ', @periodBegin, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ' , ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ( [Time], [Exception], [Message], [UserName], [StackTrace] ) VALUES ( GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace );
THROW; END CATCH END;

