CREATE PROCEDURE Programmability.GetSummaryAccountingSheet2
	@userName NVARCHAR(64),
  @resourceSystemTypeId INT,

	@periodBegin DATETIME,
  @periodEnd DATETIME	
AS

BEGIN SET NOCOUNT ON;
SET XACT_ABORT ON;
BEGIN TRY
/* "" */ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
BEGIN TRANSACTION;
DECLARE @isDebug BIT = 0;
/*----------------------------------------------------------------------------------------------------*/ /*
*/
/* */ DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName = @userName, @userId = @userId OUT, @roleCode = @roleCode OUT;
/* */ SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME ); SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME );
/* */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName OUTPUT inserted.[Order], inserted.[Name] VALUES (1, 'ReportParameter'), (2, 'ReportFieldInfo'), (3, 'MainData'); /*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/ /*
*/
/* */
DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter SELECT [Name], [Value] FROM ( SELECT CAST([ResourceSystemType].[Code] AS NVARCHAR(128)) [ResourceSystemTypeCode], CAST([ResourceSystemType].[ShortName] AS NVARCHAR(128)) [ResourceSystemTypeShortName], CAST([ResourceSystemType].[Description] AS NVARCHAR(128)) [ResourceSystemTypeDescription] FROM [Dictionaries].[ResourceSystemType] [ResourceSystemType] WHERE [ResourceSystemType].[Id] = @resourceSystemTypeId ) [ReportParameterInfo] UNPIVOT ( [Value] FOR [Name] IN ( [ResourceSystemTypeShortName], [ResourceSystemTypeCode], [ResourceSystemTypeDescription] ) ) [ReportParameter];
SELECT [ReportParameter].[Name], [ReportParameter].[Value] FROM @reportParameter [ReportParameter] UNION ALL SELECT 'PeriodBegin' [Name], CONVERT(NVARCHAR(128), @periodBegin, 104) [Value] UNION ALL SELECT 'PeriodEnd' [Name], CONVERT(NVARCHAR(128), @periodEnd, 104) [Value] UNION ALL SELECT 'User' [Name], CONVERT(NVARCHAR(128), @userName) [Value] UNION ALL SELECT 'District' [Name], [District].[Description] [Value] FROM [Dictionaries].[District] [District] WHERE [District].[Id] = @districtId UNION ALL SELECT 'City' [Name], [City].[Description] [Value] FROM [Dictionaries].[City] [City] INNER JOIN [Dictionaries].[District] [District] ON [City].[Id] = [District].[CityId] AND [District].[Id] = @districtId
/* */ ( SELECT CONCAT([Parameter].[Code], '.Instant') [Name], [Parameter].[IntegrableValue] [IntegrableValue] FROM [Programmability].[GetSummaryAccountingAbstractParameters] (1, @resourceSystemTypeId, @userId, @districtId) [ReportFieldInfo] INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ReportFieldInfo].[Id] ) UNION ALL ( SELECT CONCAT([Parameter].[Code], '.Daily') [Name], [Parameter].[IntegrableValue] [IntegrableValue] FROM [Programmability].[GetSummaryAccountingAbstractParameters] (3, @resourceSystemTypeId, @userId, @districtId) [ReportFieldInfo] INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ReportFieldInfo].[Id] ) UNION ALL ( SELECT CONCAT([Parameter].[Code], '.FinalInstant') [Name], [Parameter].[IntegrableValue] [IntegrableValue] FROM [Programmability].[GetSummaryAccountingAbstractParameters] (5, @resourceSystemTypeId, @userId, @districtId) [ReportFieldInfo] INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ReportFieldInfo].[Id] ) /*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/ /*
*/ DECLARE @dateRangePredicate NVARCHAR(MAX) = ''; SELECT @dateRangePredicate = @dateRangePredicate + CONCAT ( '(', '(', '[Time]>=', '''', CONVERT(NVARCHAR,DATEADD(SECOND, [DeviceArchiveTimeConverter].[Interval], @periodBegin), 126 ), '''', ' AND ', '[Time]<=', '''', CONVERT(NVARCHAR, DATEADD(SECOND, [DeviceArchiveTimeConverter].[Interval], @periodEnd), 126), '''', ') AND [Device].[Id]=', [DeviceArchiveTimeConverter].[DeviceId], ')' ) + ' OR ' FROM [Rules].[DeviceArchiveTimeConverter] [DeviceArchiveTimeConverter] WHERE EXISTS ( SELECT DISTINCT [MeasurementDevice].[DeviceId] [DeviceId] FROM [Business].[MeasurementDevice] [MeasurementDevice]
INNER JOIN [Business].[Channel] ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[Placement] [Placement] ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] ON [Building].[Id] = [Placement].[BuildingId] AND [Building].[DistrictId] = @districtId
WHERE [MeasurementDevice].[DeviceId] = [DeviceArchiveTimeConverter].[DeviceId]
) AND [DeviceArchiveTimeConverter].[PeriodTypeId] = 3 ;
SET @dateRangePredicate = LEFT(@dateRangePredicate, LEN(@dateRangePredicate) - 3); /*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/ /*
PIVOT */
/* */
IF( EXISTS( SELECT 1 FROM [Programmability].[GetSummaryAccountingAbstractParameters] (1, @resourceSystemTypeId, @userId, @districtId) )) BEGIN /* */ DECLARE @abstractParameterFieldsDaily NVARCHAR(MAX) = ''; DECLARE @abstractParameterFieldsDailyAlias NVARCHAR(MAX) = ''; DECLARE @abstractParameterFieldsWithNextDaily NVARCHAR(MAX) = '';
SELECT @abstractParameterFieldsDaily = @abstractParameterFieldsDaily + CONCAT('[', [CodePull].[Code], ']') + ', ',
@abstractParameterFieldsDailyAlias = @abstractParameterFieldsDailyAlias + CONCAT('[ArchiveDailyPivot].[',[CodePull].[Code], '] ', '[', [CodePull].[Code], '.Daily]') + ', ' + CONCAT('[ArchiveDailyPivot].[',[CodePull].[Code], '.Last] ', '[', [CodePull].[Code], '.Last.Daily]') + ', ',
@abstractParameterFieldsWithNextDaily = @abstractParameterFieldsWithNextDaily + CONCAT('[', [CodePull].[Code], ']') + ', ' + CONCAT('LAST_VALUE ([', [CodePull].[Code], ']) OVER(PARTITION BY [ChannelId] ORDER BY [Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [', [CodePull].[Code], '.Last', ']') + ',' FROM [Programmability].[GetSummaryAccountingAbstractParameters] (3, @resourceSystemTypeId, @userId, @districtId) [CodePull];
SET @abstractParameterFieldsDaily = LEFT(@abstractParameterFieldsDaily, LEN(@abstractParameterFieldsDaily) - 1); SET @abstractParameterFieldsDailyAlias = LEFT(@abstractParameterFieldsDailyAlias, LEN(@abstractParameterFieldsDailyAlias) - 1); SET @abstractParameterFieldsWithNextDaily = LEFT(@abstractParameterFieldsWithNextDaily, LEN(@abstractParameterFieldsWithNextDaily) - 1);
/* */ DECLARE @abstractParameterFieldsFinalInstant NVARCHAR(MAX) = ''; DECLARE @abstractParameterFieldsFinalInstantAlias NVARCHAR(MAX) = '';
SELECT @abstractParameterFieldsFinalInstant = @abstractParameterFieldsFinalInstant + CONCAT('[', [CodePull].[Code], ']') + ', ',
@abstractParameterFieldsFinalInstantAlias = @abstractParameterFieldsFinalInstantAlias + CONCAT('[ArchiveFinalInstantPivot].[',[CodePull].[Code], '] ', '[', [CodePull].[Code], '.FinalInstant]') + ', '
FROM [Programmability].[GetSummaryAccountingAbstractParameters] (5, @resourceSystemTypeId, @userId, @districtId) [CodePull];
SET @abstractParameterFieldsFinalInstant = LEFT(@abstractParameterFieldsFinalInstant, LEN(@abstractParameterFieldsFinalInstant) - 1); SET @abstractParameterFieldsFinalInstantAlias = LEFT(@abstractParameterFieldsFinalInstantAlias, LEN(@abstractParameterFieldsFinalInstantAlias) - 1);
/* */ DECLARE @abstractParameterFieldsInstant NVARCHAR(MAX) = ''; DECLARE @abstractParameterFieldsInstantAlias NVARCHAR(MAX) = '';
SELECT @abstractParameterFieldsInstant = @abstractParameterFieldsInstant + CONCAT('[', [CodePull].[Code], ']') + ', ',
@abstractParameterFieldsInstantAlias = @abstractParameterFieldsInstantAlias + CONCAT('[ArchiveInstantPivot].[',[CodePull].[Code], '] ', '[', [CodePull].[Code], '.Instant]') + ', '
FROM [Programmability].[GetSummaryAccountingAbstractParameters] (1, @resourceSystemTypeId, @userId, @districtId) [CodePull];
SET @abstractParameterFieldsInstant = LEFT(@abstractParameterFieldsInstant, LEN(@abstractParameterFieldsInstant) - 1); SET @abstractParameterFieldsInstantAlias = LEFT(@abstractParameterFieldsInstantAlias, LEN(@abstractParameterFieldsInstantAlias) - 1);
END ELSE BEGIN SELECT [Unknown] FROM ( SELECT NULL [Unknown] ) [MainData] WHERE 1 != 1 END;
/*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/ /* CTE ,
*/ DECLARE @mainDataQuery NVARCHAR(MAX) = ' /* */ WITH [ArchiveDaily] AS ( SELECT [Channel].[Id] [ChannelId], [Archive].[Time] [Time], [Parameter].[Code] [ParameterCode] ,[dbo].[ConvertMeasurementUnit] ( [Dimension].[Value], [Muc].[DimensionValue2], [Archive].[Value], [Muc].[Expression] ) [Value] FROM [Business].[Archive] [Archive]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] ON [MeasurementDevice].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
/* */ INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId] AND [ChannelTemplate].[ResourceSystemTypeId] = @resourceSystemTypeId
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Dictionaries].[Device] [Device] ON [Device].[Id] = [DeviceParameter].[DeviceId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] ON [ParameterMapDeviceParameter].[DeviceParameterId] = [DeviceParameter].[Id] AND [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [ParameterMapDeviceParameter].[PeriodTypeId] = 3
INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [ParameterMapDeviceParameter].[DimensionId] = [Dimension].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [ParameterMapDeviceParameter].[MeasurementUnitId] = [MeasurementUnit].[Id]
/* ()*/ LEFT OUTER JOIN [Programmability].[MeasurementUnitConverterWithDefaultMeasurementUnitView] [Muc] ON [MeasurementUnit].[Id] = [Muc].[MeasurementUnit1Id]
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[Placement] [Placement] ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] ON [Building].[Id] = [Placement].[BuildingId] AND [Building].[DistrictId] = @districtId /**/
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId]
WHERE [Archive].[PeriodTypeId] = 3 AND ({dateRangePredicate}) ),
/* */ [ArchiveFinalInstant] AS ( SELECT [Channel].[Id] [ChannelId], [Parameter].[Id] [ParameterId], [Parameter].[Code] [ParameterCode] ,[dbo].[ConvertMeasurementUnit] ( [Dimension].[Value], [Muc].[DimensionValue2], [Archive].[Value], [Muc].[Expression] ) [Value] FROM [Business].[Archive] [Archive]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] ON [MeasurementDevice].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
/* */ INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId] AND [ChannelTemplate].[ResourceSystemTypeId] = @resourceSystemTypeId
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Dictionaries].[Device] [Device] ON [Device].[Id] = [DeviceParameter].[DeviceId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] ON [ParameterMapDeviceParameter].[DeviceParameterId] = [DeviceParameter].[Id] AND [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [ParameterMapDeviceParameter].[PeriodTypeId] = 5
INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [ParameterMapDeviceParameter].[DimensionId] = [Dimension].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [ParameterMapDeviceParameter].[MeasurementUnitId] = [MeasurementUnit].[Id]
/* ()*/ LEFT OUTER JOIN [Programmability].[MeasurementUnitConverterWithDefaultMeasurementUnitView] [Muc] ON [MeasurementUnit].[Id] = [Muc].[MeasurementUnit1Id]
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[Placement] [Placement] ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] ON [Building].[Id] = [Placement].[BuildingId] AND [Building].[DistrictId] = @districtId /**/
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId]
WHERE [Archive].[PeriodTypeId] = 5 AND [Archive].[TimeSignatureId] = [MeasurementDevice].[LastTimeSignatureId] ),
/* */ [ArchiveInstant] AS ( SELECT [Channel].[Id] [ChannelId], [Parameter].[Id] [ParameterId], [Parameter].[Code] [ParameterCode] ,[dbo].[ConvertMeasurementUnit] ( [Dimension].[Value], [Muc].[DimensionValue2], [Archive].[Value], [Muc].[Expression] ) [Value] FROM [Business].[Archive] [Archive]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] ON [MeasurementDevice].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
/* */ INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId] AND [ChannelTemplate].[ResourceSystemTypeId] = @resourceSystemTypeId
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] ON [DeviceParameter].[Id] = [Archive].[DeviceParameterId]
INNER JOIN [Dictionaries].[Device] [Device] ON [Device].[Id] = [DeviceParameter].[DeviceId]
INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] ON [ParameterMapDeviceParameter].[DeviceParameterId] = [DeviceParameter].[Id] AND [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] AND [ParameterMapDeviceParameter].[PeriodTypeId] = 1
INNER JOIN [Dictionaries].[Dimension] [Dimension] ON [ParameterMapDeviceParameter].[DimensionId] = [Dimension].[Id]
INNER JOIN [Dictionaries].[MeasurementUnit] [MeasurementUnit] ON [ParameterMapDeviceParameter].[MeasurementUnitId] = [MeasurementUnit].[Id]
/* ()*/ LEFT OUTER JOIN [Programmability].[MeasurementUnitConverterWithDefaultMeasurementUnitView] [Muc] ON [MeasurementUnit].[Id] = [Muc].[MeasurementUnit1Id]
/* */ INNER JOIN [Business].[UserLinkChannel] [UserLinkChannel] ON [UserLinkChannel].[ChannelId] = [Channel].[Id] AND [UserLinkChannel].[UserId] = @userId
INNER JOIN [Business].[Placement] [Placement] ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] ON [Building].[Id] = [Placement].[BuildingId] AND [Building].[DistrictId] = @districtId /**/
INNER JOIN [Dictionaries].[Parameter] [Parameter] ON [Parameter].[Id] = [ParameterMapDeviceParameter].[ParameterId]
WHERE [Archive].[PeriodTypeId] = 1 AND [Archive].[TimeSignatureId] = [MeasurementDevice].[LastTimeSignatureId] ),
/* */ [ArchiveDailyPivot] AS ( SELECT * FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY [ChannelId] ORDER BY [ChannelId], [Time]) [Num], [ChannelId], [Time] [Time.Daily], LAST_VALUE([Time]) OVER(PARTITION BY [ChannelId] ORDER BY [Time] ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) [Time.Last.Daily], {abstractParameterFieldsWithNextDaily} FROM ( SELECT [ChannelId], [Time], {abstractParameterFieldsDaily} FROM [ArchiveDaily] PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( {abstractParameterFieldsDaily} ) ) [MainData]
) [PrePivotArchive] ) [PivotData] WHERE [PivotData].[Num] = 1 ),
/* */ [ArchiveFinalInstantPivot] AS ( SELECT [ChannelId], {abstractParameterFieldsFinalInstant} FROM ( SELECT [ChannelId], [ParameterCode], [Value] FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY [ArchiveFinalInstant].[ChannelId], [ArchiveFinalInstant].[ParameterId] ORDER BY [ArchiveFinalInstant].[ChannelId] ) [Num], * FROM [ArchiveFinalInstant] ) [ArchiveFinalInstant] WHERE [Num] = 1 ) [MainData] PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( {abstractParameterFieldsFinalInstant} ) ) [PivotData] ),
/* */ [ArchiveInstantPivot] AS ( SELECT [ChannelId], {abstractParameterFieldsInstant} FROM ( SELECT [ChannelId], [ParameterCode], [Value] FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY [ArchiveInstant].[ChannelId], [ArchiveInstant].[ParameterId] ORDER BY [ArchiveInstant].[ChannelId] ) [Num], * FROM [ArchiveInstant] ) [ArchiveInstant] WHERE [Num] = 1
) [MainData] PIVOT ( AVG([Value]) FOR [ParameterCode] IN ( {abstractParameterFieldsInstant} ) ) [PivotData] ) /**/
/* */ ,[ArchiveDailyPivotSummary] AS ( SELECT [ArchiveDailyPivot].[ChannelId], [ArchiveDailyPivot].[Time.Daily], [ArchiveDailyPivot].[Time.Last.Daily], {abstractParameterFieldsDailyAlias} FROM [ArchiveDailyPivot] ) ,[ArchiveFinalInstantPivotSummary] AS ( SELECT [ChannelId], {abstractParameterFieldsFinalInstantAlias} FROM [ArchiveFinalInstantPivot] ) ,[ArchiveInstantPivotSummary] AS ( SELECT [ChannelId], {abstractParameterFieldsInstantAlias} FROM [ArchiveInstantPivot] ) /**/
SELECT
[Building].[Description] [BuildingDescription], [MeasurementDevice].[Description] [MeasurementDeviceDescription], [MeasurementDevice].[NetworkAddress] [MeasurementDeviceNetworkAddress], --[MeasurementDevice].[LastConnectionTime], [TimeSignature].[Time] [LastConnectionTime],
[StatusConnection].[Description] [StatusConnectionDescription], [Organization].[Description] [OrganizationDescription], [TopChannelLinkAgreementParameter].[Value] [AgreementParameterValue], [TechnologicAdjunctionType].[Description] [TechnologicAdjunctionTypeDescription],
[ArchiveDailyPivotSummary].*, [ArchiveFinalInstantPivotSummary].*, [ArchiveInstantPivotSummary].*
FROM [ArchiveDailyPivotSummary]
INNER JOIN [ArchiveFinalInstantPivotSummary] ON [ArchiveFinalInstantPivotSummary].[ChannelId] = [ArchiveDailyPivotSummary].[ChannelId]
INNER JOIN [ArchiveInstantPivotSummary] ON [ArchiveInstantPivotSummary].[ChannelId] = [ArchiveDailyPivotSummary].[ChannelId]
INNER JOIN [Business].[Channel] [Channel] ON [Channel].[Id] = [ArchiveFinalInstantPivotSummary].[ChannelId]
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] ON [MeasurementDevice].[Id] = [Channel].[MeasurementDeviceId]
INNER JOIN [Business].[TimeSignature] [TimeSignature] ON [TimeSignature].[Id] = [MeasurementDevice].[LastTimeSignatureId]
INNER JOIN [Business].[Placement] [Placement] ON [Placement].[Id] = [Channel].[PlacementId]
INNER JOIN [Business].[Building] [Building] ON [Building].[Id] = [Placement].[BuildingId]
INNER JOIN [Dictionaries].[StatusConnection] [StatusConnection] ON [StatusConnection].[Id] = [MeasurementDevice].[LastStatusConnectionId]
INNER JOIN [Dictionaries].[TechnologicAdjunctionType] [TechnologicAdjunctionType] ON [TechnologicAdjunctionType].[Id] = [Channel].[TechnologicAdjunctionTypeId]
LEFT JOIN [Business].[Organization] [Organization] ON [Organization].[Id] = [Channel].[OrganizationId]
OUTER APPLY ( SELECT TOP 1 [AgreementParameter].[Value] [Value] FROM [Business].[ChannelLinkAgreementParameter] [ChannelLinkAgreementParameter]
INNER JOIN [Business].[AgreementParameter] [AgreementParameter] ON [AgreementParameter].[Id] = [ChannelLinkAgreementParameter].[AgreementParameterId]
INNER JOIN [Dictionaries].[AgreementParameterType] [AgreementParameterType] ON [AgreementParameterType].[Id] = [AgreementParameter].[AgreementParameterTypeId]
WHERE [ChannelLinkAgreementParameter].[ChannelId] = [ArchiveDailyPivotSummary].[ChannelId] AND ( ([AgreementParameter].[AgreementParameterTypeId] = 1 AND @resourceSystemTypeId = 4) OR ([AgreementParameter].[AgreementParameterTypeId] = 2 AND @resourceSystemTypeId = 3) )
) [TopChannelLinkAgreementParameter]
'; /*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/ /* */ SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsDaily}', @abstractParameterFieldsDaily); SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsWithNextDaily}', @abstractParameterFieldsWithNextDaily); SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsFinalInstant}', @abstractParameterFieldsFinalInstant); SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsInstant}', @abstractParameterFieldsInstant);
SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsDailyAlias}', @abstractParameterFieldsDailyAlias); SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsFinalInstantAlias}', @abstractParameterFieldsFinalInstantAlias); SET @mainDataQuery = REPLACE(@mainDataQuery, '{abstractParameterFieldsInstantAlias}', @abstractParameterFieldsInstantAlias);
SET @mainDataQuery = REPLACE(@mainDataQuery, '{dateRangePredicate}', @dateRangePredicate);
/* */ EXEC [sys].[sp_executesql] @mainDataQuery, N'@userId INT, @districtId INT, @resourceSystemTypeId INT, @periodBegin DATETIME, @periodEnd DATETIME',
@userId = @userId, @districtId = @districtId, @resourceSystemTypeId = @resourceSystemTypeId, @periodBegin = @periodBegin, @periodEnd = @periodEnd; /*----------------------------------------------------------------------------------------------------*/
COMMIT TRANSACTION; END TRY
BEGIN CATCH PRINT ERROR_MESSAGE();
/* */ IF (XACT_STATE()) = -1 BEGIN ROLLBACK TRANSACTION; END;
-- !IMPORTANT /* , , */ IF (XACT_STATE()) = 1 BEGIN COMMIT TRANSACTION; END;
IF (@isDebug = 0) BEGIN
DECLARE @lineFeed CHAR(1) = CHAR(13);
DECLARE @parameterListTrace NVARCHAR(256) = CONCAT ( '@userName: ' , @userName, @lineFeed, '@resourceSystemTypeId: ', @resourceSystemTypeId, @lineFeed, '@districtId: ', @districtId, @lineFeed, '@periodBegin: ', @periodBegin, @lineFeed, '@periodEnd: ', @periodEnd, @lineFeed );
DECLARE @errorMessage NVARCHAR(MAX) = CONCAT ( 'ErrorNumber: ', ERROR_NUMBER(), @lineFeed, 'ErrorSeverity: ', ERROR_SEVERITY(), @lineFeed, 'ErrorState: ' , ERROR_STATE(), @lineFeed, 'ErrorProcedure: ', ERROR_PROCEDURE(), @lineFeed, 'ErrorLine: ', ERROR_LINE(), @lineFeed, 'ErrorMessage: ', ERROR_MESSAGE() );
INSERT INTO [Admin].[ErrorLogEntry] ( [Time], [Exception], [Message], [UserName], [StackTrace] ) VALUES ( GETDATE(), 'InternalSqlServerException', @errorMessage, SUSER_NAME(), @parameterListTrace );
THROW; END;
END CATCH;
END;

/*   INNER JOIN   */
CREATE PROCEDURE Programmability.GetSummaryAccountingSheet3
	@userName NVARCHAR(64),
  @resourceSystemTypeId INT,
  @districtId INT,	
	@periodBegin DATETIME,
  @periodEnd DATETIME	
AS

BEGIN

   DECLARE @msg NVARCHAR(2048) = 
   (
      SELECT TOP(1) [text]
            FROM [sys].[messages] [Msg] 
            WHERE [Msg].[message_id] = @msgId AND [Msg].[language_id] = @sysLangId
   );

   RETURN @msg;   
END;

BEGIN
SET NOCOUNT ON;
IF (@intervalByDay != 0) BEGIN
SET @periodBegin = CAST(DATEDIFF(DAY, @intervalByDay, GETDATE()) AS DATETIME);
/* */ IF(@intervalByDay = -1) BEGIN SET @periodBegin = CAST(CAST (GETDATE() AS DATE) AS DATETIME); END;
SET @periodEnd = GETDATE(); END;
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64); EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') ,(2, 'ColumnCaption');
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName]; /**/
/* 2. */
DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
IF(@protocolType = 'EntityHistory') BEGIN SELECT [EntityHistory].[Id] ,[EntityHistory].[EntityId] ,[EntityHistory].[EntityTypeName] ,[EntityHistory].[EntityTypeDescription] ,[EntityHistory].[PropertyName] ,[EntityHistory].[PropertyDescription] ,[EntityHistory].[OriginalValue] ,[EntityHistory].[CurrentValue] ,[EntityHistory].[User] ,[EntityHistory].[DateTime] FROM [Admin].[EntityHistory] [EntityHistory] WITH (NOLOCK)
WHERE ([EntityHistory].[User] = @userName OR @roleCode = N'ADMIN') AND ([EntityHistory].[DateTime] BETWEEN @periodBegin AND @periodEnd) AND
-- "" NOT ([EntityHistory].[CurrentValue] = [EntityHistory].[OriginalValue] AND [EntityHistory].[PropertyName] = 'Description') AND ( [EntityHistory].[EntityTypeName] IN ( SELECT CONVERT(NVARCHAR(64), [Item]) [EnyityCode] FROM [Business].[StringSplitToArray] (@entityCodeArray, ',') ) OR @selectAllEntity = 1 ) ORDER BY [EntityHistory].[DateTime] DESC;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 0, 1), ('MainData', 'EntityTypeName', ' ', 1, 2), ('MainData', 'EntityTypeDescription', ' ', 1, 3), ('MainData', 'EntityId', ' ', 1, 4), ('MainData', 'PropertyName', ' ', 1, 5), ('MainData', 'PropertyDescription', ' ', 1, 6), ('MainData', 'OriginalValue', ' ', 1, 7), ('MainData', 'CurrentValue', ' ', 1, 8), ('MainData', 'User', '', 1, 9), ('MainData', 'DateTime', ' ', 1, 10); END
ELSE IF (@protocolType = 'DeletedEntity') BEGIN SELECT [DeletedEntityLogEntry].[Id] ,[DeletedEntityLogEntry].[EntityId] ,[DeletedEntityLogEntry].[EntityDescription] ,[DeletedEntityLogEntry].[EntityTypeName] ,[DeletedEntityLogEntry].[EntityTypeDescription]
,[DeletedEntityLogEntry].[User] ,[DeletedEntityLogEntry].[DateTime] FROM [Admin].[DeletedEntityLogEntry] [DeletedEntityLogEntry] WITH (NOLOCK)
WHERE ([DeletedEntityLogEntry].[User] = @userName OR @roleCode = N'ADMIN') AND ([DeletedEntityLogEntry].[DateTime] BETWEEN @periodBegin AND @periodEnd) AND ( [DeletedEntityLogEntry].[EntityTypeName] IN ( SELECT CONVERT(NVARCHAR(64), [Item]) [EnyityCode] FROM [Business].[StringSplitToArray] (@entityCodeArray, ',') ) OR @selectAllEntity = 1 )
ORDER BY [DeletedEntityLogEntry].[DateTime] DESC;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 0, 1), ('MainData', 'EntityTypeName', ' ', 1, 2), ('MainData', 'EntityTypeDescription', ' ', 1, 3), ('MainData', 'EntityId', ' ', 1, 4), ('MainData', 'EntityDescription', ' ', 1, 5), ('MainData', 'User', '', 1, 6), ('MainData', 'DateTime', ' ', 1, 7); END
ELSE IF (@protocolType = 'CreatedBy') BEGIN
DECLARE @queries NVARCHAR(MAX);
SELECT @queries = STUFF(CAST(( SELECT [text()] = 'UNION ALL ' + '(' +'SELECT ' + CAST([EntityOrder] AS NVARCHAR(4)) +' [EntityOrder], ' + '''' + [EntityTypeName] + ''' [EntityTypeName], ' + '''' + [EntityTypeDescription] + ''' [EntityTypeDescription]' + ' ,[Entity].[Id] [EntityId]' + IIF ( EXISTS -- Does it have Description property? ( SELECT 1 FROM Core.[Entity] [EntityInfo] WHERE EXISTS ( SELECT 1 FROM Core.[EntityProperty] [EntityPropertyInfo] WHERE [EntityPropertyInfo].[PropertyName] = 'Description' AND [EntityPropertyInfo].[EntityId] = [EntityInfo].[Id] ) AND [EntityInfo].[Code] = [EntityTypeName] ), ' ,[Entity].[Description] [EntityDescription]', ' ,NULL [EntityDescription]' ) + ' ,[Entity].[CreatedBy] [User]' + ' ,[Entity].[CreatedDate] [DateTime] ' + 'FROM [' + [EntityScheme] + '].[' + [EntityTypeName] + '] [Entity] WITH (NOLOCK)' + ' WHERE ([Entity].[CreatedBy] = @userName OR @roleCode = N''ADMIN'') AND ([Entity].[CreatedDate] BETWEEN @periodBegin AND @periodEnd)' + ')' FROM ( SELECT [Entity].[Id] [EntityOrder] ,[Entity].[Schema] [EntityScheme] ,[Entity].[Code] [EntityTypeName] ,[Entity].[Description] [EntityTypeDescription]
FROM [Core].[Entity] [Entity]
WHERE [Entity].[Code] IN ( SELECT CONVERT(NVARCHAR(64), [Item]) [EnyityCode] FROM [Business].[StringSplitToArray] (@entityCodeArray, ',') ) ) [Entity]
FOR XML PATH(''), TYPE) AS NVARCHAR(MAX)), 1, 9, '')
SET @queries = 'SELECT ROW_NUMBER() OVER(ORDER BY [EntityOrder]) [Id] ,[Entity].[EntityTypeName] ,[Entity].[EntityTypeDescription] ,[Entity].[EntityId] ,[Entity].[EntityDescription] ,[Entity].[User] ,[Entity].[DateTime] FROM ( ' + @queries + ') [Entity] ORDER BY [Entity].[DateTime] DESC';
IF(@queries IS NULL) BEGIN
DECLARE @emptyStub NVARCHAR(MAX) = 'SELECT * FROM ( SELECT NULL [Id] ,NULL [EntityTypeName] ,NULL [EntityTypeDescription] ,NULL [EntityId] ,NULL [EntityDescription] ,NULL [User] ,NULL [DateTime] ) [Entity] WHERE NULL IS NOT NULL';
SET @queries = @emptyStub; END;
EXEC [sys].[sp_executesql] @queries, N'@userName NVARCHAR(32), @roleCode NVARCHAR(32), @periodBegin DATETIME, @periodEnd DATETIME', @userName = @userName, @roleCode = @roleCode, @periodBegin = @periodBegin, @periodEnd = @periodEnd ;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 0, 1), ('MainData', 'EntityTypeName', ' ', 1, 2), ('MainData', 'EntityTypeDescription', ' ', 1, 3), ('MainData', 'EntityId', ' ', 1, 4), ('MainData', 'EntityDescription', ' ', 1, 5), ('MainData', 'User', '', 1, 6), ('MainData', 'DateTime', ' ', 1, 7); END
ELSE IF(@protocolType = 'ErrorLog') BEGIN SELECT [Err].[UserName] [User] ,[Err].[Time] [DateTime] ,[Err].[Exception]
,IIF( LEN([Err].[Message]) > 128, [dbo].[GetGridHyperlink] ( 'ErrorLogEntry', 'Message', [Err].[Id], 'show-largetext', '...' ), [Err].[Message]) [Message]
,[Err].[UserAgent] ,[dbo].[GetGridHyperlink] ( 'ErrorLogEntry', 'StackTrace', [Err].[Id], 'show-largetext', ' ...' ) [StackTrace] ,[dbo].[GetGridHyperlink] ( 'ErrorLogEntry', 'RequestParams', [Err].[Id], IIF( [Err].[RequestParams] IS NOT NULL, 'show-largetext', ''), ' ...' ) [RequestParams]
FROM [Admin].[ErrorLogEntry] [Err] WITH (NOLOCK)
WHERE ([Err].[UserName] = @userName OR @roleCode = N'ADMIN') AND ([Err].[Time] BETWEEN @periodBegin AND @periodEnd)
ORDER BY [Err].[Time] DESC
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'User', '', 1, 1) ,('MainData', 'Exception', '', 1, 3) ,('MainData', 'Message', '', 1, 4) ,('MainData', 'UserAgent', '', 1, 5) ,('MainData', 'StackTrace', '', 1, 6) ,('MainData', 'RequestParams', ' ', 1, 7) ,('MainData', 'DateTime', '', 1, 7); END
ELSE IF(@protocolType = 'Audit') BEGIN SELECT [Audit].[Id] [Id], [Role].[Code] [Role], [User].[UserName] [UserName], [Audit].[Time] [Time], [Audit].[Elapsed] [Elapsed],
[Audit].[Area], [Audit].[Controller], [Audit].[Action] [Action],
IIF( LEN([Audit].[ActionParams]) > 128, [dbo].[GetGridHyperlink] ( 'Audit', 'ActionParams', [Audit].[Id], 'show-largetext', ' ...' ), [Audit].[ActionParams]) [ActionParams]
FROM [Admin].[Audit] [Audit] WITH (NOLOCK) INNER JOIN [Admin].[User] [User] WITH (NOLOCK) ON [Audit].[UserId] = [User].[Id] INNER JOIN [Admin].[Role] [Role] WITH (NOLOCK) ON [Role].[Id] = [User].[RoleId]
WHERE ([User].[UserName] = @userName OR @roleCode = N'ADMIN') AND ([Audit].[Time] BETWEEN @periodBegin AND @periodEnd)
ORDER BY [Audit].[Time] DESC
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'Id', '', 1, 1) ,('MainData', 'Role', '', 1, 2) ,('MainData', 'UserName', '', 1, 3) ,('MainData', 'Elapsed', ' ', 1, 5) ,('MainData', 'Area', '', 1, 6) ,('MainData', 'Controller', '', 1, 7) ,('MainData', 'Action', '', 1, 8) ,('MainData', 'ActionParams', ' ', 1, 8) ,('MainData', 'Time', '', 1, 9) END;
END;

CREATE PROCEDURE Programmability.GetSystemProtocols
	@userName NVARCHAR(64),
	@periodBegin DATETIME = NULL,
	@periodEnd DATETIME = NULL,
	@intervalByDay INT = 31,
	@protocolType NVARCHAR(16) = 'EntityHistory',
	@entityCodeArray NVARCHAR(MAX),
	@selectAllEntity BIT
AS

BEGIN
    SET NOCOUNT ON;
	SET XACT_ABORT ON;

	BEGIN TRY

	/*         "" */
      SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

      BEGIN TRANSACTION;

	  DECLARE @measurementDeviceId INT = 
      (
        SELECT TOP(1) [Channel].[MeasurementDeviceId] 
        FROM [Business].[Channel] [Channel] WITH (NOLOCK)
        WHERE [Channel].[Id] = @channelId 
      );

	  /* 1.          */
      DECLARE @resultSetName [Programmability].[ResultSetNameTableType];

      INSERT INTO @resultSetName VALUES
         (1, 'ReportParameter'),
         (2, 'MainData'),
		 (3, 'ColumnCaption') --       

	  SELECT [ResultSetName].[Order], [ResultSetName].[Name]  FROM @resultSetName [ResultSetName]

	  /* 2.      */
      DECLARE @userId  INT;    
      DECLARE @roleCode NVARCHAR(64);

      EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;

     --      
     IF(NOT EXISTS(SELECT 1 FROM [Business].[UserLinkChannel] [Link] WITH(NOLOCK) WHERE [Link].[UserId] = @userId AND [Link].[ChannelId] = @channelId))
     BEGIN    
        DECLARE @errorPermissionMsg  NVARCHAR(2048) 
         = [Programmability].[GetSysMessage](297, 1033);
        --      297                 
        THROW 500001, @errorPermissionMsg, 1;
     END;

	 /* 3.    */
      DECLARE @reportParameter [Programmability].[CustomParameterTableType]; 

      INSERT INTO @reportParameter
         SELECT [Name], [Value]
         FROM
		      (SELECT  
			   CAST([D].[Code] AS NVARCHAR(128)) [DeviceCode],           
               CAST([Z].[Description] AS NVARCHAR(128)) [District],
               CAST([B].[FullAddress] AS NVARCHAR(128)) [FullAddress],
               CONVERT(NVARCHAR(128), [Md].[FactoryNumber], 0) [FactoryNumber]

		      FROM [Business].[MeasurementDevice] [Md] WITH (NOLOCK)
			      INNER JOIN [Dictionaries].[Device] [D] 
              ON [D].[Id] = [Md].[DeviceId]

            INNER JOIN [Business].[AccessPoint] [Ap] WITH (NOLOCK)
              ON [Ap].[Id] = [Md].[AccessPointId]

            INNER JOIN [Business].[AccessPointLinkBuilding] [ApBL] WITH (NOLOCK)  
              ON [ApBL].[AccessPointId] = [Ap].[Id]

            INNER JOIN [Business].[Building] [B] WITH (NOLOCK)
              ON [B].[Id] = [ApBL].[BuildingId]

            INNER JOIN [Dictionaries].[District] [Z] WITH (NOLOCK)
              ON [Z].[Id] = [B].[DistrictId] 

            INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK)
              ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT

		      WHERE [Md].[Id] = @measurementDeviceId) [MeasurementDeviceInfo]
         UNPIVOT 
         (  
            [Value] FOR [Name] IN 
            (
			  [DeviceCode],
              [District], 
              [FullAddress], 
              [FactoryNumber]
            ) 
         ) [ReportParameter];

	  DECLARE @reportTitle NVARCHAR(48) = N'   ';
	  DECLARE @internalDeviceEventStartIndex INT = -1;
	  DECLARE @internalDeviceEventEndIndex INT = -1;

	  IF @asyncArchiveTypeId = 0
	  BEGIN
	      SET @reportTitle = N'   ';
		  SET @internalDeviceEventStartIndex = 1;
		  SET @internalDeviceEventEndIndex = 49;
	  END
	  IF @asyncArchiveTypeId = 1
	  BEGIN
	      SET @reportTitle = N' ';
		  SET @internalDeviceEventStartIndex = 50;
		  SET @internalDeviceEventEndIndex = 80;
	  END
	  IF @asyncArchiveTypeId = 2
	  BEGIN
	      SET @reportTitle = N' ';
		  SET @internalDeviceEventStartIndex = 81;
		  SET @internalDeviceEventEndIndex = 86;
      END

      INSERT INTO @reportParameter ([Name],[Value])
	  VALUES ('ReportTitle', @reportTitle);

      SELECT 
         [ReportParameter].[Name], 
         [ReportParameter].[Value] 
      FROM @reportParameter [ReportParameter];

	  /* 4.   */

	  SELECT [MainData].[Time] [Time],
	         [MainData].[Description] [Event],
			 [MainData].[OriginalValue] [OriginalValue],
			 [MainData].[CurrentValue] [CurrentValue]	  
	  FROM [Business].[MeasurementDeviceJournal] [MainData] WITH (NOLOCK)
	         WHERE [MeasurementDeviceId] = @measurementDeviceId AND 
			       [InternalDeviceEventId] >= @internalDeviceEventStartIndex AND
				   [InternalDeviceEventId] <= @internalDeviceEventEndIndex
      ORDER BY [Time] ASC

	  /* 5.      */
	  DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];

	  INSERT INTO @columnCaption 
      OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order]
      VALUES
        ('MainData', 'Time', '', 1, 1),
		('MainData', 'Event', '/', 1, 2),
		('MainData', 'OriginalValue', ' ', 1, 3),
		('MainData', 'CurrentValue', ' ', 1, 4)
      /**/

	  COMMIT TRANSACTION;

	  END TRY

  /*     */
  BEGIN CATCH
    DECLARE @lineFeed CHAR(1) = CHAR(13);

       DECLARE @parameterListTrace NVARCHAR(256) = 
             CONCAT
             (  
                '@userName: ' ,            @userName, @lineFeed, 
                '@measurementDeviceId: ',  @measurementDeviceId, @lineFeed, 
                '@channelId: ',            @channelId, @lineFeed, 
                '@asyncArchiveTypeId: ',   @asyncArchiveTypeId, @lineFeed                        
             );

      DECLARE @errorMessage NVARCHAR(MAX) = 
         CONCAT
         (
            'ErrorNumber: ',     ERROR_NUMBER(), @lineFeed,
            'ErrorSeverity: ',   ERROR_SEVERITY(), @lineFeed,
            'ErrorState: ' ,     ERROR_STATE(), @lineFeed,
            'ErrorProcedure: ',  ERROR_PROCEDURE(), @lineFeed,
            'ErrorLine: ',       ERROR_LINE(), @lineFeed,
            'ErrorMessage: ',    ERROR_MESSAGE()
         );

      INSERT INTO [Admin].[ErrorLogEntry] 
         (
            [Time], 
            [Exception], 
            [Message], 
            [UserName],
            [StackTrace]
         )
      VALUES
         (
            GETDATE(), 
            'InternalSqlServerException', 
            @errorMessage, 
            SUSER_NAME(),
           @parameterListTrace
         );
      --    
      THROW;    
  END CATCH;
END;

--V1Diff DECLARE @diffParameterId INT = 4083; --V1Sum DECLARE @sumParameterId INT = 4049;
SELECT MIN([ArchiveBase].[Time]) [Min], MAX([ArchiveBase].[Time]) [Max] FROM [Business].[Archive] [ArchiveBase] WITH(NOLOCK) LEFT JOIN [Business].[Archive] [ArchiveSum] WITH(NOLOCK) ON [ArchiveBase].[MeasurementDeviceId] = [ArchiveSum].[MeasurementDeviceId] AND [ArchiveBase].[PeriodTypeId] = [ArchiveSum].[PeriodTypeId] AND [ArchiveBase].[Time] = [ArchiveSum].[Time] AND [ArchiveBase].[DeviceParameterId] = @diffParameterId AND [ArchiveSum].[DeviceParameterId] = @sumParameterId AND [ArchiveBase].[DeviceParameterId] IS NOT NULL WHERE [ArchiveSum].[Time] IS NULL AND [ArchiveBase].[DeviceParameterId] = @diffParameterId AND [ArchiveBase].[MeasurementDeviceId] = @measurementDeviceId AND [ArchiveBase].[PeriodTypeId] = 2
RETURN 0

BEGIN SET NOCOUNT ON;
IF OBJECT_ID('tempdb..#ChannelInfoStub') IS NOT NULL DROP Table #ChannelInfoStub
SELECT [ChannelInfoStub].[Id] ,[ChannelInfoStub].[Code] ,[ChannelInfoStub].[Description] ,[ChannelInfoStub].[ChannelId] ,[ChannelInfoStub].[ResourceSystemTypeId] ,[ChannelInfoStub].[ResourceSubsystemTypeId] ,[ChannelInfoStub].[ResourceSystemTypeCode] ,[ChannelInfoStub].[ResourceSubsystemTypeCode] ,[ChannelInfoStub].[IsStubChannel] ,[ChannelInfoStub].[ChannelTemplateId] INTO [#ChannelInfoStub] FROM ( SELECT [ChannelInfoStub].[Id] ,[ChannelInfoStub].[Code] ,[ChannelInfoStub].[Description] ,[ChannelInfoStub].[ChannelId] ,[ChannelInfoStub].[ResourceSystemTypeId] ,[ChannelInfoStub].[ResourceSubsystemTypeId] ,[ChannelInfoStub].[ResourceSystemTypeCode] ,[ChannelInfoStub].[ResourceSubsystemTypeCode] ,[ChannelInfoStub].[IsStubChannel] ,[ChannelInfoStub].[ChannelTemplateId]
FROM ( VALUES (0, CAST(NULL AS NVARCHAR(64)), CAST(NULL AS NVARCHAR(64)), 0, 4, NULL, N'HeatSys', N'HeatSys', CAST( CASE WHEN @channelIdHeatSys = 0 AND @isPreload = 0 THEN 1 ELSE 0 END AS BIT), 2), (0, CAST(NULL AS NVARCHAR(64)), CAST(NULL AS NVARCHAR(64)), 0, 4, 8, N'HeatSys', N'HeatSysBlock', CAST( CASE WHEN @channelIdHeatSysBlock = 0 AND @isPreload = 0 THEN 1 ELSE 0 END AS BIT), 3), (0, CAST(NULL AS NVARCHAR(64)), CAST(NULL AS NVARCHAR(64)), 0, 4, 7, N'HeatSys', N'HeatSysMakeup', CAST( CASE WHEN @channelIdHeatSysMakeup = 0 AND @isPreload = 0 THEN 1 ELSE 0 END AS BIT), 500), (0, CAST(NULL AS NVARCHAR(64)), CAST(NULL AS NVARCHAR(64)), 0, 3, NULL, N'Hws', N'Hws', CAST( CASE WHEN @channelIdHws = 0 AND @isPreload = 0 THEN 1 ELSE 0 END AS BIT), 101), (0, CAST(NULL AS NVARCHAR(64)), CAST(NULL AS NVARCHAR(64)), 0, 5, NULL, N'ElectroSys', N'ElectroSys', CAST( CASE WHEN @channelIdElectroSys = 0 AND @isPreload = 0 THEN 1 ELSE 0 END AS BIT), 20) ) [ChannelInfoStub]([Id], [Code], [Description], [ChannelId], [ResourceSystemTypeId], [ResourceSubsystemTypeId], [ResourceSystemTypeCode], [ResourceSubsystemTypeCode], [IsStubChannel], [ChannelTemplateId])
) [ChannelInfoStub];
IF(@isStubMode = 1 AND @resourceSubsystemTypeCode IS NOT NULL) BEGIN DECLARE @channelTemplateId INT = (SELECT [#ChannelInfoStub].[ChannelTemplateId] FROM [#ChannelInfoStub] WHERE [#ChannelInfoStub].[ResourceSubsystemTypeCode] = @resourceSubsystemTypeCode)
EXEC [Programmability].[GetAccountingParamSheetStub] @periodTypeId = @periodTypeId, @channelTemplateId = @channelTemplateId ;
RETURN; END;
DECLARE @userId INT = ( SELECT TOP (1) [User].[Id] FROM [Admin].[User] WITH (NOLOCK) WHERE [UserName] = @userName );
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM ( VALUES (1, N'ReportParameter'), (2, N'MainData')) ResultSetName([Order], [Name] );
SELECT [Name], [Value] FROM (SELECT CAST([District].[Description] AS NVARCHAR(128)) [District], CAST([Building].[FullAddress] AS NVARCHAR(128)) [FullAddress]
FROM [Business].[Building] [Building] WITH (NOLOCK)
INNER JOIN [Dictionaries].[District] [District] WITH (NOLOCK) ON [District].[Id] = [Building].[DistrictId]
WHERE [Building].[Id] = @buildingId ) [BuildingInfo] UNPIVOT ( [Value] FOR [Name] IN ( [District], [FullAddress] ) ) [ReportParameter];
( SELECT [ChannelInfoStub].[Id] ,[ChannelInfoStub].[Code] ,[ChannelInfoStub].[Description] ,[ChannelInfoStub].[ChannelId] ,[ChannelInfoStub].[ResourceSystemTypeId] ,[ChannelInfoStub].[ResourceSubsystemTypeId] ,[ChannelInfoStub].[ResourceSystemTypeCode] ,[ChannelInfoStub].[ResourceSubsystemTypeCode] ,[ChannelInfoStub].[IsStubChannel]
FROM [#ChannelInfoStub] [ChannelInfoStub]
WHERE [ChannelInfoStub].[IsStubChannel] = 1 ) UNION ALL ( SELECT [ChannelInfo].[Id] ,[ChannelInfo].[Code] ,[ChannelInfo].[Description]
,[ChannelInfo].[ChannelId] ,[ChannelInfo].[ResourceSystemTypeId] ,[ChannelInfo].[ResourceSubsystemTypeId] ,[ChannelInfo].[ResourceSystemTypeCode] ,[ChannelInfo].[ResourceSubsystemTypeCode] ,CAST (0 AS BIT) [IsStubChannel] FROM (SELECT [Channel].[Id] [Id], [Channel].[Description] [Code], [ResourceSystemType].[Description] [Description],
[Channel].[Id] [ChannelId], [Channel].[ResourceSystemTypeId] [ResourceSystemTypeId], [Channel].[ResourceSubsystemTypeId] [ResourceSubsystemTypeId], [ResourceSystemType].[Code] [ResourceSystemTypeCode],
CASE [ResourceSystemType].[Id] WHEN 3 THEN [ResourceSystemType].[Code] ELSE COALESCE([ResourceSubsystemType].[Code], [ResourceSystemType].[Code]) END [ResourceSubsystemTypeCode]
FROM [Business].[Channel] [Channel] WITH(NOLOCK)
INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) ON [Channel].[MeasurementDeviceId] = [MeasurementDevice].[Id]
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Channel].[PlacementId] = [Placement].[Id]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Placement].[BuildingId] = [Building].[Id]
INNER JOIN [Dictionaries].[ResourceSystemType] [ResourceSystemType] WITH (NOLOCK) ON [Channel].[ResourceSystemTypeId] = [ResourceSystemType].[Id]
LEFT JOIN [Dictionaries].[ResourceSubsystemType] [ResourceSubsystemType] WITH (NOLOCK) ON [Channel].[ResourceSubsystemTypeId] = [ResourceSubsystemType].[Id]
WHERE (@isPreload = 0 OR [Building].[Id] = @buildingId)
AND ([ResourceSystemType].[Code] IN (SELECT [#ChannelInfoStub].[ResourceSystemTypeCode] FROM [#ChannelInfoStub]))
AND (@isPreload = 1 OR ( [Channel].[Id] IN (@channelIdHeatSys, @channelIdHeatSysBlock, @channelIdHws, @channelIdHeatSysMakeup, @channelIdElectroSys)))
) [ChannelInfo]
WHERE [ChannelInfo].[Id] IN ( SELECT [UserLinkChannel].[ChannelId] FROM [Business].[UserLinkChannel] [UserLinkChannel] WITH (NOLOCK) WHERE [UserLinkChannel].[UserId] = @userId ) )
END;

