CREATE PROC Programmability.GetUnitedAccountingParamSheet
    @userName NVARCHAR (32) = NULL,
    @buildingId INT = NULL,
    @periodTypeId INT = 3,
    @isPreload BIT = 1,
    
    @channelIdHeatSys INT = 0,
    @channelIdHeatSysBlock INT = 0,
    @channelIdHws INT = 0,
    @channelIdHeatSysMakeup INT = 0,
    @channelIdElectroSys INT = 0,

    @isStubMode BIT = 0,
    @resourceSubsystemTypeCode NVARCHAR(32) = NULL
AS

CREATE PROC Programmability.GetUserExtendedInfoSummary
  @userName NVARCHAR(32)
AS

BEGIN

  SELECT TOP(1) 
      @userId = [User].[Id], 
      @roleCode = [Role].[Code]     
    FROM [Admin].[User] [User] 
      INNER JOIN [Admin].[Role] [Role] ON [User].[RoleId] = [Role].[Id]    
    WHERE [User].[UserName] = @userName;

  RETURN 0

END;

CREATE PROCEDURE [Programmability].[GetUserInfo]
  @userName NVARCHAR(64),
  @userId INT OUTPUT,
  @roleCode NVARCHAR(64) OUTPUT
AS

BEGIN
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
SELECT TOP(1) @userId = [User].[Id], @roleCode = [Role].[Code] FROM [Admin].[User] [User] WITH (NOLOCK) INNER JOIN [Admin].[Role] [Role] WITH (NOLOCK) ON [User].[RoleId] = [Role].[Id] WHERE [User].[UserName] = @userName;
IF (@roleCode = N'OPERADMIN' OR @roleCode = N'LEADOPERADMIN') BEGIN -- OPERADMIN - INSERT INTO @userLinkTable SELECT [UserLink].[ChannelId], [UserLink].[UserId] FROM [Business].[UserLinkChannel] [UserLink] WITH (NOLOCK) WHERE [UserLink].[UserId] = @userId END ELSE IF (@roleCode = N'ADMIN') BEGIN -- ADMIN INSERT INTO @userLinkTable SELECT [UserLink].[Id] [ChannelId], NULL [UserId] FROM [Business].[Channel] [UserLink] WITH (NOLOCK) END;
RETURN; END;

CREATE FUNCTION [Programmability].[GetUserLinkChannel]
(
  @userName NVARCHAR(32)  
)
RETURNS @userLinkTable TABLE
(
      [ChannelId] INT,
      [UserId] INT
)
AS

BEGIN
   SET NOCOUNT ON;

   /* 1.          */
   DECLARE @resultSetName [Programmability].[ResultSetNameTableType];

   INSERT INTO @resultSetName VALUES
	   (1, 'MainData'),		   --       
	   (2, 'ColumnCaption');	--        

   SELECT [ResultSetName].[Order], [ResultSetName].[Name]
	FROM @resultSetName [ResultSetName]

  /**/
  DECLARE @monthCount INT = 1;

  /*2.   */

   DECLARE @eoMonth DATETIME = EOMONTH(GETDATE());

   SET @eoMonth = DATEADD(second, 86399,  @eoMonth);

   DECLARE @soMonth DATETIME =
   (
     SELECT DATEADD(month, DATEDIFF(month, 0, DATEADD(month, - (@monthCount -1 ), GETDATE())), 0)
   );

  DECLARE @userId INT;
  DECLARE @roleCode NVARCHAR(64);

  EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;

  DECLARE @requestRoleCode NVARCHAR(64) = IIF (@roleCode = 'OPERADMIN', 'HOLDER', NULL);
  -- ([Ci].[Id] = @cityId OR @cityId IS NULL) 

  SELECT        
      [Role].[Code] [RoleCode]
      ,[User].[UserName]
      ,[UserMessage].[Date]                        
      ,[dbo].[GetGridHyperlink] 
      (
            'UserMessage', 
            'Text', 
            [UserMessage].[Id], 
            'show-largetext', 
            '...'
      ) [Text]      

   FROM [Admin].[UserMessage] [UserMessage] WITH (NOLOCK)

   INNER JOIN [Admin].[User] [User]  WITH (NOLOCK)
    ON [User].[Id] = [UserMessage].[UserId]

   INNER JOIN [Admin].[Role] [Role] WITH (NOLOCK)
    ON [Role].[Id] = [User].[RoleId]

   --    
   WHERE 
    --([UserMessage].[Date] >= @soMonth AND [UserMessage].[Date] <= @eoMonth) AND 
    ([Role].[Code] = @requestRoleCode OR @requestRoleCode IS NULL) 

   ORDER BY [UserMessage].[Date] DESC

   /**/

   /* 3.      */
  DECLARE @columnCaption [Programmability].[ColumnCaptionTableType]; 

  INSERT INTO @columnCaption 
  OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order]
  VALUES  
  	('MainData', 'RoleCode', '', 1, 1)
  	,('MainData', 'UserName', '', 1, 2)
  	,('MainData', 'Date', '', 1, 3)
  	,('MainData', 'Text', '', 1, 4)

  /**/

   RETURN 0;
END;

BEGIN
    SET NOCOUNT ON;
	SET XACT_ABORT ON;

	BEGIN TRY

	/*         "" */
      SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

      BEGIN TRANSACTION;

	  DECLARE @measurementDeviceId INT = 
      (
        SELECT TOP(1) [Channel].[MeasurementDeviceId] 
        FROM [Business].[Channel] [Channel] WITH (NOLOCK)
        WHERE [Channel].[Id] = @channelId 
      );

	  DECLARE @deviceId INT =
	  (
	    SELECT TOP(1) [MeasurementDevice].[DeviceId]
		FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK)
		WHERE [MeasurementDevice].[Id] = @measurementDeviceId
	  );

	  /* 1.          */
      DECLARE @resultSetName [Programmability].[ResultSetNameTableType];

      INSERT INTO @resultSetName VALUES
         (1, 'ReportParameter'),
         (2, 'MainData'),
		 (3, 'ColumnCaption') --       

	  SELECT [ResultSetName].[Order], [ResultSetName].[Name]  FROM @resultSetName [ResultSetName]

	  /* 2.      */
      DECLARE @userId  INT;    
      DECLARE @roleCode NVARCHAR(64);

      EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;

     --      
     IF(NOT EXISTS(SELECT 1 FROM [Business].[UserLinkChannel] [Link] WITH(NOLOCK) WHERE [Link].[UserId] = @userId AND [Link].[ChannelId] = @channelId))
     BEGIN    
        DECLARE @errorPermissionMsg  NVARCHAR(2048) 
         = [Programmability].[GetSysMessage](297, 1033);
        --      297                 
        THROW 500001, @errorPermissionMsg, 1;
     END;

	 /* 3.    */
      DECLARE @reportParameter [Programmability].[CustomParameterTableType]; 

      INSERT INTO @reportParameter
         SELECT [Name], [Value]
         FROM
		      (SELECT  
			   CAST([D].[Code] AS NVARCHAR(128)) [DeviceCode],           
               CAST([Z].[Description] AS NVARCHAR(128)) [District],
               CAST([B].[FullAddress] AS NVARCHAR(128)) [FullAddress],
               CONVERT(NVARCHAR(128), [Md].[FactoryNumber], 0) [FactoryNumber]

		      FROM [Business].[MeasurementDevice] [Md] WITH (NOLOCK)
			      INNER JOIN [Dictionaries].[Device] [D] 
              ON [D].[Id] = [Md].[DeviceId]

            INNER JOIN [Business].[AccessPoint] [Ap] WITH (NOLOCK)
              ON [Ap].[Id] = [Md].[AccessPointId]

            INNER JOIN [Business].[AccessPointLinkBuilding] [ApBL] WITH (NOLOCK)  
              ON [ApBL].[AccessPointId] = [Ap].[Id]

            INNER JOIN [Business].[Building] [B] WITH (NOLOCK)
              ON [B].[Id] = [ApBL].[BuildingId]

            INNER JOIN [Dictionaries].[District] [Z] WITH (NOLOCK)
              ON [Z].[Id] = [B].[DistrictId] 

            INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK)
              ON [Channel].[MeasurementDeviceId] = [Md].[Id] AND [Channel].[Id] = @channelId --!IMPORTANT

		      WHERE [Md].[Id] = @measurementDeviceId) [MeasurementDeviceInfo]
         UNPIVOT 
         (  
            [Value] FOR [Name] IN 
            (
			  [DeviceCode],
              [District], 
              [FullAddress], 
              [FactoryNumber]
            ) 
         ) [ReportParameter];

	  DECLARE @reportTitle NVARCHAR(48) = N'  ';
	  DECLARE @internalDeviceEventStartIndex INT = -1;
	  DECLARE @internalDeviceEventEndIndex INT = -1;

	  IF @asyncArchiveTypeId = 0
	  BEGIN
	      SET @reportTitle = N'  ';
		  SET @internalDeviceEventStartIndex = 3000;
		  SET @internalDeviceEventEndIndex = 3030;
	  END
	  IF @asyncArchiveTypeId = 1
	  BEGIN
	      SET @reportTitle = N' ';
		  SET @internalDeviceEventStartIndex = 3031;
		  SET @internalDeviceEventEndIndex = 3035;
	  END
	  IF @asyncArchiveTypeId = 2
	  BEGIN
	      SET @reportTitle = N'  ( )';
		  SET @internalDeviceEventStartIndex = 3036;
		  SET @internalDeviceEventEndIndex = 3040;
      END

	  --  -26   +1000
	  IF @deviceId = 21
	  BEGIN
	      SET @internalDeviceEventStartIndex = @internalDeviceEventStartIndex + 1000;
		  SET @internalDeviceEventEndIndex = @internalDeviceEventEndIndex + 1000;
	  END

      INSERT INTO @reportParameter ([Name],[Value])
	  VALUES ('ReportTitle', @reportTitle);

      SELECT 
         [ReportParameter].[Name], 
         [ReportParameter].[Value] 
      FROM @reportParameter [ReportParameter];

	  /* 4.   */

	  SELECT [MainData].[Time] [Time],
	         [MainData].[Description] [Event],
			 [MainData].[OriginalValue] [OriginalValue],
			 [MainData].[CurrentValue] [CurrentValue]	  
	  FROM [Business].[MeasurementDeviceJournal] [MainData] WITH (NOLOCK)
	         WHERE [MeasurementDeviceId] = @measurementDeviceId AND 
			       [InternalDeviceEventId] >= @internalDeviceEventStartIndex AND
				   [InternalDeviceEventId] <= @internalDeviceEventEndIndex
      ORDER BY [Time] ASC

	  /* 5.      */
	  DECLARE @columnCaption [Programmability].[ColumnCaptionTableType];

	  INSERT INTO @columnCaption 
      OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order]
      VALUES
        ('MainData', 'Time', '', 1, 1),
		('MainData', 'Event', '/', 1, 2),
		('MainData', 'OriginalValue', ' ', 1, 3),
		('MainData', 'CurrentValue', ' ', 1, 4)
      /**/

	  COMMIT TRANSACTION;

	  END TRY

  /*     */
  BEGIN CATCH
    DECLARE @lineFeed CHAR(1) = CHAR(13);

       DECLARE @parameterListTrace NVARCHAR(256) = 
             CONCAT
             (  
                '@userName: ' ,            @userName, @lineFeed, 
                '@measurementDeviceId: ',  @measurementDeviceId, @lineFeed, 
                '@channelId: ',            @channelId, @lineFeed, 
                '@asyncArchiveTypeId: ',   @asyncArchiveTypeId, @lineFeed                        
             );

      DECLARE @errorMessage NVARCHAR(MAX) = 
         CONCAT
         (
            'ErrorNumber: ',     ERROR_NUMBER(), @lineFeed,
            'ErrorSeverity: ',   ERROR_SEVERITY(), @lineFeed,
            'ErrorState: ' ,     ERROR_STATE(), @lineFeed,
            'ErrorProcedure: ',  ERROR_PROCEDURE(), @lineFeed,
            'ErrorLine: ',       ERROR_LINE(), @lineFeed,
            'ErrorMessage: ',    ERROR_MESSAGE()
         );

      INSERT INTO [Admin].[ErrorLogEntry] 
         (
            [Time], 
            [Exception], 
            [Message], 
            [UserName],
            [StackTrace]
         )
      VALUES
         (
            GETDATE(), 
            'InternalSqlServerException', 
            @errorMessage, 
            SUSER_NAME(),
           @parameterListTrace
         );
      --    
      THROW;    
  END CATCH;
END;

BEGIN SET DATEFORMAT dmy; SET LANGUAGE N''; SET NOCOUNT ON;
SET @periodTypeId = IIF(@periodTypeId = 0, NULL, @periodTypeId);
-- DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
-- IF(NOT EXISTS(SELECT 1 FROM [Business].[UserLinkChannel] [Link] WITH(NOLOCK) WHERE [Link].[UserId] = @userId AND [Link].[ChannelId] = @channelId)) BEGIN DECLARE @errorPermissionMsg NVARCHAR(2048) = [Programmability].[GetSysMessage](297, 1033); -- 297 THROW 500001, @errorPermissionMsg, 1; END;
-- IF(NOT EXISTS (SELECT 1 FROM [Business].[Channel] [Channel] INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) ON [Channel].[ChannelTemplateId] = [ChannelTemplate].[Id] WHERE [Channel].[Id] = @channelId) ) BEGIN ;THROW 500001, N' ', 1; END;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType]; INSERT INTO @resultSetName VALUES (1, 'MainData') ,(2, 'ColumnCaption');
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName]
/**/
-- DECLARE @measurementDeviceId INT = (SELECT [MeasurementDeviceId] FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].[Id] = @channelId)
-- DECLARE @channelTemplateId INT = (SELECT [ChannelTemplateId] FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].[Id] = @channelId)
/* 2. */ SELECT [Archive].[Id] [ArchiveId], [PeriodType].[Description] [PeriodTypeDescription], [DeviceParameter].[Description] [DeviceParameterDescription], [Archive].[Value] [ArchiveValue], [Archive].[Time] [ArchiveTime]
FROM [Business].[Archive] [Archive] WITH(NOLOCK)
INNER JOIN [Business].[MeasurementDevice] [Device] WITH(NOLOCK) ON [Device].[Id] = [Archive].[MeasurementDeviceId]
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[Id] = @channelId AND [Device].[Id] = [Channel].[MeasurementDeviceId]
INNER JOIN [Business].[UserLinkChannel] [UserLink] WITH(NOLOCK) ON [Channel].[Id] = [UserLink].[ChannelId] AND [UserLink].[UserId] = @userId
INNER JOIN [Dictionaries].[PeriodType] [PeriodType] WITH(NOLOCK) ON [Archive].[PeriodTypeId] = [PeriodType].[Id]
INNER JOIN [Dictionaries].[DeviceParameter] [DeviceParameter] WITH(NOLOCK) ON [Archive].[DeviceParameterId] = [DeviceParameter].[Id]
WHERE [Device].[Id] = @measurementDeviceId AND ([Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEnd) AND ([Archive].PeriodTypeId = @periodTypeId OR @periodTypeId IS NULL) -- , AND [Archive].[DeviceParameterId] IN ( SELECT [DeviceParameterId] FROM [Business].[ParameterMapDeviceParameter] [PMDP] WITH (NOLOCK) WHERE ([PMDP].[ChannelTemplateId] = @channelTemplateId AND ([PMDP].[PeriodTypeId] = @periodTypeId OR @periodTypeId IS NULL)) )
ORDER BY [Archive].[Time] DESC OFFSET 0 ROWS FETCH NEXT 5000 ROWS ONLY;
/* 3. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'ArchiveId', '', 1, 1) ,('MainData', 'PeriodTypeDescription', '', 1, 2) ,('MainData', 'DeviceParameterDescription', ' ', 1, 3) ,('MainData', 'ArchiveValue', '', 1, 4) ,('MainData', 'ArchiveTime', '', 1, 5)
END;

-- ====================================================================================================
-- Author:  	Leo
-- Create date: 2014-10-28
-- Description:    
-- ===================================================================================================


CREATE PROCEDURE [Programmability].[MeasurementDeviceArchiveData]
	@userName NVARCHAR(32),
	@periodBegin DATETIME,
    @periodEnd DATETIME,
	@channelId INT,
	@periodTypeId INT
AS

BEGIN SET DATEFORMAT dmy; SET LANGUAGE N''; SET NOCOUNT ON;
DECLARE @empty NCHAR(1) = '';
DECLARE @userId INT; DECLARE @roleCode NVARCHAR(64);
EXEC [Programmability].[GetUserInfo] @userName, @userId OUT, @roleCode OUT;
/* 1. */ DECLARE @resultSetName [Programmability].[ResultSetNameTableType];
INSERT INTO @resultSetName VALUES (1, 'ReportParameter') -- ,(2, 'MainData') ,(3, 'ColumnCaption'); --
SELECT [ResultSetName].[Order], [ResultSetName].[Name] FROM @resultSetName [ResultSetName] /**/
/* 2. */
DECLARE @reportParameter [Programmability].[CustomParameterTableType];
INSERT INTO @reportParameter OUTPUT inserted.[Name], inserted.[Value] SELECT [Name], [Value] FROM ( SELECT [Name], [Value] FROM ( SELECT CAST([User].[UserName] AS NVARCHAR(128)) [UserName] ,CAST(ISNULL([UserInfo].[FirstName], @empty) AS NVARCHAR(128)) [FirstName] ,CAST(ISNULL([UserInfo].[LastName], @empty) AS NVARCHAR(128)) [LastName] ,CAST(ISNULL([UserInfo].[Description], @empty) AS NVARCHAR(128)) [UserInfoDescription]
FROM [Admin].[User] [User] WITH (NOLOCK) LEFT OUTER JOIN [Business].[UserAdditionalInfo] [UserInfo] WITH (NOLOCK) ON [User].[Id] = [UserInfo].[Id] WHERE [User].[UserName] = @userName) [A] UNPIVOT ( [Value] FOR [Name] IN ( [UserName], [FirstName], [LastName], [UserInfoDescription] ) ) [B] ) [C];
/**/
/* 2. */
SELECT [MeasurementDeviceDeviceId], [MeasurementDeviceDescription], [BuildingDescription], [DistrictId], [DistrictDescription], [CityDescription], [AccessPointDescription] FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY [Md].[Id] ORDER BY [Md].[Id]) [Num], [Md].[Id] [MeasurementDeviceDeviceId] ,[Md].[Description] [MeasurementDeviceDescription] ,[Building].[Description] [BuildingDescription] ,[District].[Id] [DistrictId] ,[City].[Description] [CityDescription] ,[District].[Description] [DistrictDescription] ,[AccessPoint].[Description] [AccessPointDescription] FROM [Business].[MeasurementDevice] [Md] WITH (NOLOCK)
INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK) ON [Channel].[MeasurementDeviceId] = [Md].[Id]
INNER JOIN [Programmability].[GetUserLinkChannel] (@userName) [UserLink] ON [UserLink].[ChannelId] = [Channel].[Id] AND ((ISNULL([UserLink].[UserId], 0) = @userId) OR (@roleCode = N'ADMIN'))
INNER JOIN [Business].[AccessPoint] [AccessPoint] WITH (NOLOCK) ON [AccessPoint].[Id] = [Md].[AccessPointId]
INNER JOIN [Business].[AccessPointLinkBuilding] [AccessPointLink] WITH (NOLOCK) ON [AccessPointLink].[AccessPointId] = [AccessPoint].[Id]
INNER JOIN [Business].[Building] [Building] WITH (NOLOCK) ON [Building].[Id] = [AccessPointLink].[BuildingId]
INNER JOIN [Business].[Placement] [Placement] WITH (NOLOCK) ON [Placement].[BuildingId] = [Building].[Id]
INNER JOIN [Dictionaries].[District] [District] WITH (NOLOCK) ON [District].[Id] = [Building].[DistrictId]
INNER JOIN [Dictionaries].[City] [City] WITH (NOLOCK) ON [City].[Id] = [District].[CityId]
) [Md]
WHERE [Md].[Num] = 1
ORDER BY [Md].[DistrictDescription] ASC
/**/
/* 3. */ DECLARE @columnCaption [Programmability].[ColumnCaptionTableType] ;
INSERT INTO @columnCaption OUTPUT inserted.[Table], inserted.[Name], inserted.[Caption], inserted.[Visible], inserted.[Order] VALUES ('MainData', 'CityDescription', '', 0, 1) ,('MainData', 'DistrictDescription', '', 1, 1) ,('MainData', 'MeasurementDeviceDeviceId', '', 1, 2) ,('MainData', 'MeasurementDeviceDescription', '', 1, 3) ,('MainData', 'AccessPointDescription', ' ', 1, 4) ,('MainData', 'BuildingDescription', ' ', 1, 5) ;
SELECT [ColumnCaption].[Name], [ColumnCaption].[Caption] FROM @columnCaption [ColumnCaption];
END;

BEGIN
   SET NOCOUNT ON;

   DECLARE @dateTime DATETIME = GETDATE();
   DECLARE @uid UNIQUEIDENTIFIER = NEWID();
   DECLARE @methodName VARCHAR(64) = 'Dictionary';
   DECLARE @error NVARCHAR(128); 

   SET @actionUid = @uid;

   DECLARE @query NVARCHAR(MAX) = 

    'SELECT  @dateTime [DateTime], @uid [Uid], @methodName [MethodName],
    (
	    SELECT (
	    SELECT @name AS ''@name'',
	    (   
		    SELECT [Id], [Code], [Description] FROM [Dictionaries].[' + @name +'] [' + @name + '] WITH(NOLOCK)
		    FOR XML AUTO, ELEMENTS, TYPE		    	 	 	
	    ) FOR XML PATH(''Dictionary''), ELEMENTS, TYPE
	    ) FOR XML PATH(''Dictionaries''), ELEMENTS, TYPE
    ) FOR XML PATH(''Package'')';

   BEGIN TRY	

     EXEC [sys].[sp_executesql] @query, 
      N'@dateTime DATETIME, @uid UNIQUEIDENTIFIER, @name VARCHAR(128), @methodName  VARCHAR(64)',  
      @dateTime = @dateTime, 
      @uid = @uid,
      @name = @name, 
      @methodName = @methodName;

   END TRY
   BEGIN CATCH
	   SET @error = '   . ' + ERROR_MESSAGE();
      SELECT 
         @dateTime [DateTime], 
         @uid [Uid],
         @methodName [MethodName],      
         @error [Message]             
      FOR XML PATH('Error'), ELEMENTS, TYPE;  
   END CATCH;
END;

BEGIN
   SET NOCOUNT ON;
   SET XACT_ABORT ON;

   DECLARE @dateTime DATETIME = GETDATE();
   DECLARE @uid UNIQUEIDENTIFIER = NEWID();  
   DECLARE @methodName VARCHAR(64) = 'MeasurementDeviceArchive';
   DECLARE @error NVARCHAR(256); 

   SET @actionUid = @uid;

   BEGIN TRY

     SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
     BEGIN TRANSACTION;             

    /*    /   */
    IF NOT EXISTS
    (
      --       
      SELECT 1 FROM [Business].[UserLinkChannel] [UserLinkChannel] WITH(NOLOCK)
        INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK)
          ON [Channel].[Id] = [UserLinkChannel].[ChannelId] 
            AND [Channel].[MeasurementDeviceId] = @measurementDeviceId
        INNER JOIN [Admin].[User] [User] WITH(NOLOCK)
          ON [UserLinkChannel].[UserId] = [User].[Id]
      WHERE [User].[UserName] = @userName

      UNION ALL

      --       
      --       
      SELECT 1 FROM [WebApi].[ArchiveDownloader] [ArchiveDownloader] WITH(NOLOCK)  
        INNER JOIN [Admin].[User] [User] WITH(NOLOCK)
          ON [User].[Id] = [ArchiveDownloader].[Id]
        INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK)
          ON [Channel].[OrganizationId] = [ArchiveDownloader].[OrganizationId] 
            AND [Channel].[MeasurementDeviceId] = @measurementDeviceId
      WHERE [User].[UserName] = @userName
    )
    BEGIN
  	    SET @error = N'       .         .';
              SELECT
                @dateTime [DateTime], 
                @uid [Uid],
                @methodName [MethodName],      
                @error [Message],
                (
                  SELECT @measurementDeviceId [MeasurementDeviceId]
                  FOR XML PATH('AdditionalInfo'), ELEMENTS, TYPE
                )
                FOR XML PATH('Error'), ELEMENTS;
          IF(@@trancount > 0)
          BEGIN
            COMMIT TRANSACTION;
          END;
          RETURN;     
      END;

     /*    */
     SET @periodBegin = CAST( CAST( @periodBegin AS DATE ) AS DATETIME);
     SET @periodEnd = CAST( CAST( @periodEnd AS DATE ) AS DATETIME);

     --      
     IF(@includePeriodEnd = 1)
     BEGIN
        SET @periodEnd = DATEADD(day, 1, @periodEnd);
     END;

     DECLARE @expectedDateInterval INT = DATEDIFF(DAY, @periodBegin, @periodEnd);   
     DECLARE @selectedRowLimitCount INT = 250000;
     DECLARE @selectedDateInterval INT = 0; 

     IF(@channelId != 0)
     BEGIN
      SELECT TOP(1) @measurementDeviceId = [Channel].[MeasurementDeviceId] 
      FROM [Business].[Channel] [Channel] WHERE [Channel].[Id] = @channelId
     END;

     IF (@periodTypeId = 1)
     BEGIN
      SET @error = '    .';

      SELECT 
           @dateTime [DateTime], 
           @uid [Uid],
           @methodName [MethodName],      
           @error [Message],
           (
            SELECT           
              @periodTypeId [PeriodTypeId]          
            FOR XML PATH('AdditionalInfo'), ELEMENTS, TYPE
           )
        FOR XML PATH('Error'), ELEMENTS;

      IF(@@trancount > 0)
      BEGIN
            COMMIT TRANSACTION;
      END;
      RETURN;
     END;

     /*      */

     /*      */
     IF(@periodTypeId = 2 OR @periodTypeId = 5) 
     BEGIN
        SET @selectedDateInterval = 31; --1;
     END

     /*   1  */
     ELSE IF (@periodTypeId = 3)
     BEGIN
        SET @selectedDateInterval = 31;
     END

     /*       */
     ELSE IF (@periodTypeId = 4 OR @periodTypeId = 6)
     BEGIN
        SET @selectedDateInterval = 365;
     END;

     IF(@expectedDateInterval > @selectedDateInterval)
     BEGIN 
      SET @error = '    .';
      SELECT 
           @dateTime [DateTime], 
           @uid [Uid],
           @methodName [MethodName],      
           @error [Message],
           (
            SELECT
              @periodBegin [PeriodBegin],
              @periodEnd [PeriodEnd],
              @periodTypeId [PeriodTypeId],
              @expectedDateInterval [ArchiveEntriesDateInterval],
              @selectedDateInterval [ArchiveEntriesLimitDateInterval] 
            FOR XML PATH('AdditionalInfo'), ELEMENTS, TYPE
           )
        FOR XML PATH('Error'), ELEMENTS;

      IF(@@trancount > 0)
      BEGIN
        COMMIT TRANSACTION;
      END;
      RETURN;
     END;

     DECLARE @expectedRowCount BIGINT = 
     (
        SELECT COUNT([Archive].[Id]) [ExpectedRowCount]
           FROM [Business].[Archive] WITH (NOLOCK)         		   
		     INNER JOIN [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) 
              ON [MeasurementDevice].[Id] = [Archive].[MeasurementDeviceId]	  
		     WHERE 
		        ([Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEnd)		      
		        AND [Archive].PeriodTypeId = @periodTypeId 
              AND [MeasurementDevice].[Id] = @measurementDeviceId 
     );

     IF(@expectedRowCount > @selectedRowLimitCount)
     BEGIN
        SET @error = '      .';
        SELECT 
           @dateTime [DateTime], 
           @uid [Uid],
           @methodName [MethodName],      
           @error [Message],
           (
              SELECT
                @expectedRowCount [ArchiveEntriesTotalCount],
                @selectedRowLimitCount [ArchiveEntriesLimitCount]
              FOR XML PATH('AdditionalInfo'), ELEMENTS, TYPE
           )         
        FOR XML PATH('Error'), ELEMENTS;

        IF(@@trancount > 0)
        BEGIN
          COMMIT TRANSACTION;
        END;
        RETURN;
     END;

    SELECT                     
        @dateTime [DateTime],  
        @uid [Uid],
        @methodName [MethodName],      
        -- Business
        (      
          SELECT 
          (
              SELECT 
                [MeasurementDevice].[Id], 
                [MeasurementDevice].[DeviceId],   
                [MeasurementDevice].[FactoryNumber]

		         FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK)				

		            WHERE [MeasurementDevice].[Id] = @measurementDeviceId
		         FOR XML PATH('MeasurementDevice'), ELEMENTS, TYPE,  ROOT('MeasurementDevices')

           ), -- MeasurementDevices               
           (     
             SELECT    
                [Archive].[Id]
                ,[Archive].[Time]
                ,[Archive].[DeviceParameterId]
                ,[Archive].[Value]
                ,[Archive].[IsValid] 
              FROM
              (
                SELECT
                  [Archive].[Id]
                 ,[Archive].[Time]
                 ,[Archive].[DeviceParameterId]
                 ,[Archive].[Value]
                 ,[Archive].[IsValid]
                 ,[ParameterMapDeviceParameter].[ChannelTemplateId]
                 ,ROW_NUMBER() OVER
                  (
                    PARTITION BY [Archive].[Id] 
                    ORDER BY [Archive].[Id] 
                  ) [Num] 

                FROM [Business].[Archive] [Archive] WITH (NOLOCK)

                INNER JOIN [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH(NOLOCK) ON 
                  [Archive].[DeviceParameterId] = [ParameterMapDeviceParameter].[DeviceParameterId] 
                  AND [Archive].[PeriodTypeId] = [ParameterMapDeviceParameter].[PeriodTypeId] 
                  AND [ParameterMapDeviceParameter].[PeriodTypeId] = @periodTypeId

                INNER JOIN 
                ( 
                  SELECT [ChannelOuter].[Id], [ChannelOuter].[ChannelTemplateId], [ChannelOuter].[MeasurementDeviceId]
                  FROM
                  (
                      SELECT [Channel].[Id] [Id]

                      FROM [Business].[UserLinkChannel] [UserLinkChannel] WITH(NOLOCK)

                        INNER JOIN [Business].[Channel] [Channel] WITH(NOLOCK)
                          ON [Channel].[Id] = [UserLinkChannel].[ChannelId] 
                            AND [Channel].[MeasurementDeviceId] = @measurementDeviceId

                        INNER JOIN [Admin].[User] [User] WITH(NOLOCK)
                          ON [UserLinkChannel].[UserId] = [User].[Id]

                      WHERE [User].[UserName] = @userName

                      UNION

                      SELECT [Channel].[Id] [Id]

                      FROM [WebApi].[ArchiveDownloader] [ArchiveDownloader] WITH (NOLOCK)

                        INNER JOIN [Admin].[User] [User] WITH (NOLOCK)
                          ON [User].[Id] = [ArchiveDownloader].[Id]

                        INNER JOIN [Business].[Channel] [Channel] WITH (NOLOCK)
                          ON [Channel].[OrganizationId] = [ArchiveDownloader].[OrganizationId] 
                            AND [Channel].[MeasurementDeviceId] = @measurementDeviceId

                      WHERE [User].[UserName] = @userName
                    ) [ChannelInner] INNER JOIN [Business].[Channel] [ChannelOuter] WITH (NOLOCK) ON [ChannelOuter].[Id] = [ChannelInner].[Id]

                ) [Channel] ON [Archive].[MeasurementDeviceId] = [Channel].[MeasurementDeviceId]

                INNER JOIN [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK)
                  ON [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId] AND [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] 

              WHERE 
                [Archive].[MeasurementDeviceId] = @measurementDeviceId AND 
                [Archive].PeriodTypeId = @periodTypeId AND 
                ([Archive].[Time] >= @periodBegin AND [Archive].[Time] <= @periodEnd)

              ) [Archive] WHERE [Archive].[Num] = 1

		         FOR XML PATH('Archive'), ELEMENTS, TYPE, ROOT('Archives')
           ) -- Archives

           FOR XML PATH('Business'), ELEMENTS, TYPE) -- Business       

      FOR XML PATH('Package'), ELEMENTS;

      COMMIT TRANSACTION;
  END TRY
  BEGIN CATCH
      IF (XACT_STATE()) = -1
      BEGIN       
        ROLLBACK TRANSACTION;
      END;
      IF (XACT_STATE()) = 1
      BEGIN      
        COMMIT TRANSACTION;   
      END;

      SET @error = '   . ' + ERROR_MESSAGE();
      SELECT 
         @dateTime [DateTime],  
         @uid [Uid],
         @methodName [MethodName],      
         @error [Message]             
      FOR XML PATH('Error'), ELEMENTS, TYPE;        
  END CATCH;

  RETURN (0); 
 END;

-- ====================================================================================================
-- Author:  	Leo
-- Create date: 2014-11-21
-- Updated date 2015-09-09
-- Description:    XML
-- ===================================================================================================

CREATE PROCEDURE WebApi.GetMeasurementDeviceArchive 
(
    @userName NVARCHAR(64),
    @measurementDeviceId INT,
	  @periodBegin DATETIME,		--    
	  @periodEnd DATETIME,			--    
    @includePeriodEnd BIT = 1,  
    @periodTypeId INT,	
	  @withDictionaries BIT = NULL,	--   	
    @channelId INT = 0,
    @actionUid UNIQUEIDENTIFIER OUTPUT
)	
AS

BEGIN SET NOCOUNT ON; DECLARE @dateTime DATETIME = GETDATE(); DECLARE @uid UNIQUEIDENTIFIER = NEWID(); DECLARE @methodName VARCHAR(64) = 'NativeDatabaseStructureExample'; DECLARE @error NVARCHAR(128); SET @actionUid = @uid;
BEGIN TRY -- for Building entity as origin IF( @entityTypeName = 'Building' ) BEGIN SELECT @dateTime [DateTime], @uid [Uid], @methodName [MethodName], ( SELECT ( SELECT [Building].[Id] ,[Building].[Description] ,[Building].[FullAddress] ,[Building].[Square] ,[Building].[BuildingPurposeId] ,[Building].[CountFlats] ,[Building].[Location].ToString() [Location] ,[Building].[OrganizationId] ,[Building].[CreatedBy] ,[Building].[UpdatedBy] ,[Building].[CreatedDate] ,[Building].[UpdatedDate] ,[Building].[DistrictId] ,( SELECT ( SELECT *, ( SELECT *, ( SELECT * FROM [Business].[Archive] [Archive] WITH (NOLOCK) WHERE [Archive].[MeasurementDeviceId] = [MeasurementDevice].[Id] AND [Archive].[TimeSignatureId] = [MeasurementDevice].[LastTimeSignatureId] FOR XML AUTO, ELEMENTS, TYPE, ROOT('Archives') ) FROM [Business].[MeasurementDevice] [MeasurementDevice] WITH (NOLOCK) WHERE [MeasurementDevice].[AccessPointId] = [AccessPoint].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('MeasurementDevices') ) FROM [Business].[AccessPoint] [AccessPoint] WITH (NOLOCK) WHERE [AccessPoint].[Id] = [AccessPointLinkBuilding].[AccessPointId] FOR XML AUTO, ELEMENTS, TYPE ) FROM [Business].[AccessPointLinkBuilding] [AccessPointLinkBuilding] WITH (NOLOCK) WHERE [AccessPointLinkBuilding].[BuildingId] = [Building].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('AccessPointLinkBuildings') ) ,( SELECT *, ( SELECT *, ( SELECT *, ( SELECT [User].[Id] ,[User].[UserName] ,[User].[Description] FROM [Admin].[User] [User] WHERE [User].[Id] = [UserLinkChannel].[UserId] FOR XML AUTO, ELEMENTS, TYPE ) FROM [Business].[UserLinkChannel] [UserLinkChannel] WITH (NOLOCK) WHERE [UserLinkChannel].[ChannelId] = [Channel].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('UserLinkChannels') ) ,( SELECT *, ( SELECT * FROM [Business].[ParameterMapDeviceParameter] [ParameterMapDeviceParameter] WITH (NOLOCK) WHERE [ParameterMapDeviceParameter].[ChannelTemplateId] = [ChannelTemplate].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('ParameterMapDeviceParameters') ) FROM [Business].[ChannelTemplate] [ChannelTemplate] WITH (NOLOCK) WHERE [ChannelTemplate].[Id] = [Channel].[ChannelTemplateId] FOR XML AUTO, ELEMENTS, TYPE ) FROM [Business].[Channel] [Channel] WITH (NOLOCK) WHERE [Channel].[PlacementId] = [Placement].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('Channels') ) FROM [Business].[Placement] [Placement] WITH (NOLOCK) WHERE [Placement].[BuildingId] = [Building].[Id] FOR XML AUTO, ELEMENTS, TYPE, ROOT('Placements') ) FROM [Business].[Building] [Building] WITH (NOLOCK) WHERE [Building].[Id] = @entityId FOR XML AUTO, ELEMENTS, TYPE, ROOT('Buildings') ) FOR XML PATH('Business'), ELEMENTS, TYPE ), ( SELECT (SELECT * FROM [Dictionaries].[ResourceSystemType] [ResourceSystemType] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('ResourceSystemTypes')), (SELECT * FROM [Dictionaries].[Device] [Device] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Devices')), (SELECT * FROM [Dictionaries].[DeviceParameter] [DeviceParameter] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('DeviceParameters')), (SELECT * FROM [Dictionaries].[PeriodType] [PeriodType] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('PeriodTypes')), (SELECT * FROM [Dictionaries].[Parameter] [Parameter] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Parameters')), (SELECT * FROM [Dictionaries].[MeasurementUnit] [MeasurementUnit] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('MeasurementUnits')), (SELECT * FROM [Dictionaries].[Dimension] [Dimension] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('Dimensions')), (SELECT * FROM [Dictionaries].[PhysicalParameter] [PhysicalParameter] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('PhysicalParameters')), (SELECT * FROM [Dictionaries].[PlacementPurpose] [PlacementPurpose] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('PlacementPurposes')), (SELECT * FROM [Dictionaries].[BuildingPurpose] [BuildingPurpose] WITH (NOLOCK) FOR XML AUTO, ELEMENTS, TYPE, ROOT('BuildingPurposes')) FOR XML PATH('Dictionaries'), ELEMENTS, TYPE ) FOR XML PATH('Package'), ELEMENTS, TYPE; END ELSE -- If necessary this method may be developed for another way of presentation database relationships BEGIN SELECT @dateTime [DateTime], @uid [Uid], @methodName [MethodName], 'The database structure example didn''t implement for this entity.' [Message] FOR XML PATH('Package'), ELEMENTS, TYPE; END; END TRY BEGIN CATCH SET @error = 'There was internal query execution error. ' + ERROR_MESSAGE(); SELECT @dateTime [DateTime], @uid [Uid], @methodName [MethodName], @error [Message] FOR XML PATH('Error'), ELEMENTS, TYPE; END CATCH;
END;

-- ====================================================================================================
-- Author:  	Leo
-- Create date: 20180402
-- Description: This stored procedure returns xml-package containing data example of structure of relations 
-- for some entity (it's developed only for Building entity). Besides the stored procedure forms section 
-- with content of the most valuable dictionaries. 
-- ===================================================================================================

CREATE PROCEDURE WebApi.GetNativeDatabaseStructureExample
	@userName NVARCHAR(32),
	@entityId INT, 
	@entityTypeName NVARCHAR(32),
	@actionUid UNIQUEIDENTIFIER OUTPUT
AS

BEGIN
   SET NOCOUNT ON;

   DECLARE @dateTime DATETIME = GETDATE();
   DECLARE @uid UNIQUEIDENTIFIER = NEWID();
   DECLARE @methodName VARCHAR(64) = 'ScheduleInfo';
   DECLARE @error NVARCHAR(128); 

   SET @actionUid = @uid;

   BEGIN TRY	    	
    SELECT       
      @dateTime [DateTime], 
      @uid [Uid],
      @methodName [MethodName],
      (
        SELECT      
        (
          SELECT [ScheduleItem].[Id] ,
                 [ScheduleItem].[Description] ,
                 [ScheduleItem].[Enabled] ,               
                 (
                    SELECT [ScheduleType].[Id] ,
                           [ScheduleType].[Code] ,
                           [ScheduleType].[Description]
                    FROM [Dictionaries].[ScheduleType] [ScheduleType] WITH (NOLOCK)
                    WHERE [ScheduleType].[Id] = [ScheduleItem].[ScheduleTypeId]
                    FOR XML AUTO, ELEMENTS, TYPE
                 ),
                 [ScheduleItem].[PeriodBegin],
                 [ScheduleItem].[PeriodEnd],
                 [ScheduleItem].[OrdinalNumberOfDay]
                 FROM [Core].[ScheduleItem] [ScheduleItem] WITH (NOLOCK)

          INNER JOIN [WebApi].[ArchiveDownloaderLinkScheduleItem] [ArchiveDownloaderLinkScheduleItem] WITH (NOLOCK)
            ON [ArchiveDownloaderLinkScheduleItem].[ScheduleItemId] = [ScheduleItem].[Id]

          INNER JOIN [Admin].[User] [User] WITH(NOLOCK) 
            ON [User].[Id] = [ArchiveDownloaderLinkScheduleItem].[ArchiveDownloaderId] 

          WHERE [User].[UserName] = @userName
          FOR XML AUTO, ELEMENTS, TYPE, ROOT('ScheduleItems')
        )
        FOR XML PATH('Core'), ELEMENTS, TYPE
      )
	  FOR XML PATH('Package')
  END TRY
  BEGIN CATCH
	  SET @error = '   . ' + ERROR_MESSAGE();
      SELECT 
         @dateTime [DateTime],
         @uid [Uid], 
         @methodName [MethodName],      
         @error [Message]             
      FOR XML PATH('Error'), ELEMENTS, TYPE;  
  END CATCH
END;

BEGIN
   SET NOCOUNT ON;
   DECLARE @uid UNIQUEIDENTIFIER = NEWID();
   SET @actionUid = @uid;

   DECLARE @schema XML = 
   (
    SELECT TOP(1) CONVERT(XML, [Binary].[Data])
    FROM [Core].[Binary] [Binary] 
    WHERE [Binary].[Id] = 1000
   );

   SELECT @schema FOR XML PATH('');
END;

CREATE PROCEDURE [WebApi].[GetSchema]
(
  @userName NVARCHAR(64),
  @actionUid UNIQUEIDENTIFIER OUTPUT
)
AS
